css:
/* i really want this to be global */
.o_EditorCarousel_EditorCarousel_editorCarousel {
    height: 50px;
    padding: 0 16px;
}

.o_ContentEditor_ContentEditor_contentEditor {
    position: absolute;
    top: 105px;
}

section.o_EditorMain_EditorMain_mediaEditor {
    width: 50%;
}

aside.o_EditorMain_EditorMain_editorSidebar {
    width: 50%;
}

div.o_Card_Card_selectableCard.o_EditorCard_EditorCard_editorCard {
    flex-basis: 15%;
}

ul.o_EditorDetails_EditorDetails_itemHeader {
    margin: 0;
}

div.o_EditorCategory_EditorCategory_category {
    margin-top: 0px;
    margin-left: 0;
    margin-bottom: -9px;
}

span.o_list_theme_itemText.o_list_theme_primary {
    color: rgb(33, 33, 33);
    font-size: 14px;
}

div.o_Pill_Pill_override.o_Pill_Pill_pill.o_Pill_Pill_button {
    border-radius: 4px;
    margin: 5px 5px 0 0;
    min-width: 10px;
    padding: 0 0px;
}

hr.o_EditorKeywords_EditorKeywords_bottomDivider {
    margin-top: -15px;
}

hr.o_EditorReleases_EditorReleases_bottomDivider {
    margin-top: -5px;
}

div.o_EditorNote_EditorNote_reviewerNotesDropdown {
    margin-top: -7px;
    margin-bottom: -25px;
    margin-left: 0;
}

li.o_List_List_singleTextListItem.o_List_List_pad-small {
    display: inline;
}

ul.o_list_theme_list {
    display: inline-flex;
    list-style: none;
}

div.o_EditorAdditionalInfo_EditorAdditionalInfo_additionalInfoControl {
    margin-bottom: 0px;
}

.o_List_List_singleTextListItem.o_List_List_pad-large .o_List_List_item {
    min-height: 36px;
}

div.o_EditorKeywords_EditorKeywords_keywordSuggestionArea {
    margin-top: 0;
}

.o_List_List_pad-small:not(.o_List_List_singleTextListItem) .o_List_List_item {
    padding: 1px 0;
}

.o_List_List_singleTextListItem.o_List_List_pad-large .o_List_List_itemContentRoot {
    padding: 10px 0;
}

.o_List_List_itemAction {
    margin: 0px 10px 0px 10px;
    width: 40px;
}

.o_Dropdown_Dropdown_values li {
line-height: 10px;
padding: 5px 11px;
}
