css:
@-moz-document domain("roll.io") {
  html, input, textarea, 
  #logo_wrapper, #playlist_wrapper, #playlist {
      background: rgb(40,40,40);
      color: silver;
  }

  #playlist_wrapper, #playlist { background: none }

  input, textarea {
      border: 1px solid rgba(255,255,255,0.35);
      background: rgba(0,0,0,0.3);
      padding: 3px;
  }

  input:hover { background: rgba(0,0,0,0.5); }

  a, h1, h2, h3, h4, h5, h6,
  #feed_me_links a, #feed_me_enter a {
      color: dodgerblue;
  }

  #upload_file, #type_songs, #enter_url {
      background: rgba(50,200,130,0.2);
  }

  #type_songs h3, #close_type_song  { color: lightgreen; }
  #fb_share, #twitter_post { opacity: 0.5; }

  #right_col {
      width: 305px;
      position: relative;
  }

  #close_type_song { right: -7px; }
  #type_songs { width: 100%; }

  #playlist {
      outline: 1px solid dimgray;
      overflow: hidden;
      background: rgb(30,30,30);
  }

  #playlist .active_song { background: rgba(0,0,0,1); color: lightgreen; }
  #playlist ul li:hover { background: rgba(255,255,255,0.1); }
  #playlist ul li.active_song:hover { background: rgba(0,0,0,0.4); }

  #controls {
      box-sizing: border-box;
      padding: 8px;
      height: 30px;
      background: rgb(30,30,30);
  }

  /* Scrollbar */
  ::-webkit-scrollbar {
      background: rgb(30,30,30) !important;   
      padding: 2px;
      border: 1px solid rgb(60,60,60);
  }

  ::-webkit-scrollbar-thumb {
      background: rgb(60,60,60) !important;
      border-radius: 10px;
      border: 3px solid rgb(80,80,80);
  }
}
