css:
@-moz-document domain("9anime.to"),
domain("9anime.is")
{

/* 9anime Custom Background by Jacob McKendrick */
/* !!! When changing values to suit your needs, be sure not to change anything but the previous value or you might break something */

/* Edit these to suit your needs */

* {
    --main-color: #365C78;
    --main-color-light: #4c82a9;
    --neutral-box-color: #1C1F26;
    --neutral-box-color-light: #303541;
    --main-bg-color: rgba(0,0,0,0.4);
}

/* Change the URL below to set your own background image for the site */
body.dark, bodylight {
    background-image: url("https://i.imgur.com/frqHu6F.jpg");  
}

/* MAIN CODE */

#header{
	margin-left: 15%;
    margin-right: 15%;
}

.container {
    min-width: 70%;
}

body.dark #header .head #logo {
	margin-right: 47%;
}



.film-list .item .inner .poster {
	height: 100% !important;
}

.nav {
	padding-top: .3%;
}

body.dark, bodylight {
	background-attachment: fixed;
}

div.widget.crop.text-center {
	display: none;
}

body.dark #header .head #logo, #footer .main .mainc .logo {
	background-image: url("https://i.imgur.com/D22xI5k.png");
}

div#body-container.container {
	margin-top: -.7%;
    padding-top: .7%;
}

#footer .content:after {
	display: none;
}

#footer {
	margin-left: 15%;
    margin-right: 15%;
    margin-top: 0;
    padding-top: 5%;
}

/* Color changes */

.widget.ranking .item .rank, body.dark .tooltip .tooltip-inner, #header .head #sign .signin {
	color: #fff;
}

body.dark .simple-film-list .item .info p, body.dark .widget.ranking .item {
	filter: brightness(1.3);
}

body.dark .simple-film-list .item {
	border-color: #000;
}

	/* background transparency */

body.dark .widget, div#body-container.container, body.dark #header .head, body.dark #header .nav, body.dark #footer {
	background-color: var(--main-bg-color);
}

	/* main-color */

body.dark .widget .widget-title, body.dark .widget .widget-title .tabs .tab:hover, .widget .widget-title .tabs .tab.active, .film-list .item .inner .poster .status .ep {
	border-bottom-color: var(--main-color);
}

#header .head #logo a, body.dark .widget .widget-title .title, body.dark #header .nav #menu>li:hover>a, .widget.info .info .head .title, .widget.info .info dl.meta dd a, .widget.slider .item .info .watch:hover, body.dark .widget.genres ul li a:hover, .icon-right-open-big:before, .icon-left-open-big:before, body.dark #header .head #search .inner input:focus+button {
	color: var(--main-color);
    border-color: var(--main-color);
}

body.dark .btn-primary, body.dark #header .nav #menu>li:hover>a:before, .widget .widget-title a.more, body.dark .widget.servers ul.episodes li a:hover, a.active, .widget.info .info dl.meta dd .quality, .widget.slider .paging .swiper-pagination-bullet.swiper-pagination-bullet-active, body.dark .widget.schedule .widget-body .slider .items .item.active, body.dark .widget.schedule .widget-body .slider .items .item.active:hover, body.dark .tooltip .tooltip-inner, body.dark #footer .azlist ul li a, .quicktoggler .slider.round:before, body.dark .widget.servers .range>span.active {
	background-color: var(--main-color);
    border-color: var(--main-color);
}

body.dark .widget.servers ul.episodes li a.active {
	background-color: var(--main-color) !important;
}

.widget .widget-title a.more:hover, body.dark #footer .azlist ul li a:hover {
	filter: brightness(1.3);
    background-color: var(--main-color);
}

body.dark .tooltip.top .tooltip-arrow {
	border-top-color: var(--main-color);
}

body.dark #header .head #search .inner input:focus {
	border-color: var(--main-color);
}

body.dark .btn-primary:hover {
	background-color: var(--main-color-light);
	border-color: var(--main-color-light);
}

#header .head #sign .signin:hover, #footer .azlist .heading b, #footer .azlist .heading span, body.dark #footer .main a, body.dark #footer .main .mainc p.copyright {
    color: var(--main-color-light);
}

	/* neutral box color */

body.dark #header .nav #menu, body.dark .btn-default, .alert-primary, body.dark #header .nav #menu>li>.sub, .widget.ranking .item .rank, body.dark .widget.servers ul.episodes li a, body.dark .widget.info .info .desc .morelink, body.dark #header .head #search .inner input, body.dark .widget.schedule .widget-body .timeline .item:nth-child(2n), body.dark .widget.schedule .widget-body .slider .items .item, body.dark .link-list .item:nth-child(4n-3), body.dark .link-list .item:nth-child(4n-2), body.dark .link-list .item:nth-child(4n-3), body.dark #footer .bar, body.dark .widget.servers .range>span {
	background-color: var(--neutral-box-color);
    border-color: var(--neutral-box-color);
}

body.dark #header .nav #menu>li>.sub>li:hover>a, body.dark #header .nav #menu>li:hover>a, body.dark .widget.servers ul.episodes li a:visited, body.dark .widget.info .info .desc .morelink:hover, body.dark .widget.schedule .widget-body .slider .items .item:hover, body.dark .widget.servers .range>span:hover {
    background-color: var(--neutral-box-color-light);
}

/* Scrollbar */

body::-webkit-scrollbar {
    width: 12px;
}
body::-webkit-scrollbar-track {
    background-color: #000;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
}
body::-webkit-scrollbar-thumb {
    background-color: var(--neutral-box-color);
}

/* Hide ads */

#exoNativeWidget3021722.exo-native-widget, #MarketGidComposite94435 .mgbox {
	display: none;
}

}
