css:
@-moz-document domain(twitch.tv) {

div.twilight-root, div.chat__pane, div.channel-info-bar, div.channel-header, .tse-scroll-content {
    background-color: #000000 !important;
    border-color: #333333 !important;
} 

div.chatcaption {
    background: #000000 !important;
    background-color: #000000 !important;
}

/* hides icons and subscribe button*/
span.chat-line__message--badges, button.tw-button.tw-button--dropmenu, span.ffz-cheer, span.badges  {
    visibility: hidden !important;
    display: none !important;
}

/* Make the timestamps take up less space*/
span.chat-line__timestamp, span.timestamp  {
    padding-top: 0px !important;  
    padding-bottom: 0px !important; 
    font-family: Roboto !important;
    font-size: 10px !important;
    color: #777777 !important;
    vertical-align: top !important;
} 



/* Name and Chat message stuff */
div.chat-line__message, li.message-line, span.message, a.chat-author__display-name, .full-width  {
    padding-top: 0px !important;  
    padding-bottom: 3px !important; 
    font-family: Roboto !important;
    font-size: 17px !important;
}
 
/* fill out light areas when using multistre.am */
ul.chat-lines, .js-chat-interface, div.chat-interface, textarea {
    background-color: #000000 !important;  
    color: #CCCCCC !important;
    font-family: Roboto !important;
    font-size: 17px !important;
}

div.btn-wrapper, div.label {
    background-color: #333333 !important;  
    color: #CCCCCC !important;
    font-family: Roboto !important;
    font-size: 17px !important;    
}


/* add black stroke to names, thanks speedrun.com */
a.chat-author__display-name {
  /* text-shadow: 0 -2px #111, 2px -2px #111, 2px 0 #111, 2px 2px #111, 0 2px #111, -2px 2px #111, -2px 0 #111, -2px -2px #111 !important; */
  text-shadow: 0 -2px #222, 2px -2px #222, 2px 0 #222, 2px 2px #222, 0 2px #222, -2px 2px #222, -2px 0 #222, -2px -2px #222 !important;
  filter: contrast(150%) saturate(300%) !important;
}

span.has-color {
 filter: saturate(300%) !important;   
}

/* darken up rightside panes and chat area */
div.simplebar-content, div.chat-input, div.chat-room__header, .tw-theme--ffz.tw-theme--dark .tw-c-background-alt-2 {
    background-color: #000 !important;  
    color: #CCCCCC !important;
    font-family: Roboto !important;
    font-size: 17px !important;
    
}
}
