css:
@-moz-document domain("start.me") {
/*//////////// header & pop-ups ////////////*/
.header__main-bar {
	height: 70px;
}
.search__wrapper,
.breadcrumb__button:hover {
    background: rgba(0,0,0,/*[[head]]*/);
    color: #fff;
	
}
.search__settings,
.search__button,
.search__input,
.breadcrumb__button,
.drop-item__title {
    color: #f0f0f0;
}
.search__input,
.search__chevron,
.search__button,
.header-right__control,
.search__button:hover,
.breadcrumb__button {
    background: none;
	box-shadow: none;
}
.breadcrumb__edit, .breadcrumb__logo,
.search__favicon,
.header-right__button {
    display: none;
}
.search__wrapper,
.header-right__avatar,
.drop-list {
    border-radius: 100px;
    box-shadow: none;
}
.search__input {
	margin: 0 0 3px -20px;
}
.header-right__avatar:hover {
    border-radius: 0px;
    transition: 500ms ease;
		-webkit-transition: 500ms ease;
		-moz-transition: 500ms ease;
}
.header-right__button + .header-right__control:hover {
    transform: rotate(360deg);
    transition: 1s ease;
		-webkit-transition: 1s ease;
		-moz-transition: 1s ease;
	border-radius: 100px;
}
.breadcrumb__button {
	transition: 250ms ease;
}
.search__input {
	font-family: Segoe UI;
}
.drop-list,
.confirm__container,
.autocomplete-list__hidden {
	border-radius: 15px;
	background: rgba(0,0,0,0.75);
    color: #fff;
	border: none;
}

/*//////////// search results ////////////*/
.search__results {
	color: #f0f0f0;
    background: rgba(0,0,0,.7);
}
.search__results-list {
	border: none;
}
.search__search-result .search-result__icon {
	color: #f0f0f0;
}
.search__search-result_selected {
	background: rgba(255,255,255,.3);
}

/*[[dark]]*/

/*//////////// sidebars ////////////*/
.main-menu,
.page-actions-sidebar__header,
.page-action-form,
.sidebar input, 
.markdown-textarea__field,
.add-content-sidebar__body,
.subject-overview,
.widget-settings-sidebar__container,
.icon-select-sidebar__container,
.icon-select-sidebar__header,
.preview-sidebar,
.add-feed-sidebar__container,
.add-feed-sidebar__header,
.add-auth-provider-sidebar__container,
.ical-calendar-sidebar__container,
.default-provider-sidebar__container,
.providers-list-sidebar__container,
.user-preferences-sidebar__container,
.bookmark-sidebar__container,
.upload-sidebar__container,
.feed-settings-sidebar__container {
    background: rgba(0,0,0,/*[[side]]*/);
    color: #fff !important;
}
.menu-section__header,
.menu-section__container,
.main-menu__header,
.menu-section__title,
.page-link:hover .page-link__title,
.sidebar-form__subtitle,
.radio-input__button,
.select-item_enlarged,
.page-action-form__list,
.add-content-sidebar__list,
.add-content-sidebar__link:hover .add-content-sidebar__name,
.add-content-sidebar__link:hover .add-content-sidebar__add,
.add-content-sidebar__search,
.widget-settings-sidebar__header,
.tabs-layout__tab_active,
.select-item,
.widget-settings-sidebar__list,
.custom-selector__label,
.custom-selector__icon,
.custom-selector__subtitle,
.select-input__chevron,
.vertical-list-item__info,
.filters-set__title,
.vertical-list-item_type_provider .vertical-list-item__control,
.add-auth-provider-sidebar__header,
.ical-calendar-sidebar__header,
.default-provider-sidebar__header,
.providers-list-sidebar__header,
.user-preferences-sidebar__header,
.bookmark-sidebar__header,
.upload-sidebar__header,
.feed-settings-sidebar__header {
	background: none;
    color: #fff !important;
}
.content-sidebar__placeholder,
.rss-article__bullet,
.notes-widget .notes-widget__result ul li::before,
.menu-section__arrow,
.tabs-layout__tab:hover,
.custom-filter__label {
	color: rgba(255,255,255,.7);
}
.user-sidebar__link:hover,
.user-sidebar__link_marked:hover,
.sidebar-button:hover,
.side-menu__hamburger,
.pages-menu__action:hover,
.menu-section__header:hover,
.page-link:hover,
.calendar-body__day:hover,
.radio-input__button:hover,
.page-action-form__item:hover,
.add-content-sidebar__item:hover,
.widget-settings-sidebar__item:hover,
.custom-list__item:hover {
    background: rgba(255,255,255,.3);
}
.subject-overview__list,
.subject-overview__dropdown > ul:nth-child(1),
.add-content-sidebar__list:nth-child(2),
.sidebar select {
    background: #141414;
    color: #fff !important;
	border-radius: 15px !important;
}
.bookmark-card {
	background: rgba(255,255,255,.7);
}
.sidebar input:not([type="range"]) {
	border-bottom: 2px solid rgba(255,255,255,.5);
	border-width: 0 0 2px;
}
.markdown-textarea__field {
	border: solid 2px rgba(255,255,255,.5);
}
.main-menu__action,
.radio-input__button,
.page-action-form__item,
.sidebar-button_default,
.add-content-sidebar__item,
.widget-settings-sidebar__item,
.custom-list__item,
.preview-sidebar__add {
	border-radius: 100px;
}
.add-content-sidebar__input,
.subject-overview__block {
	border-radius: 15px !important;
}
.add-content-sidebar__item_enlarged {
	border-radius: 0;
}
.menu-section__item + .menu-section__item,
.radio-input__button,
.select-item_enlarged,
.page-action-form__item + .page-action-form__item,
.add-content-sidebar__item + .add-content-sidebar__item,
.add-content-sidebar .add-content-sidebar__search .add-content-sidebar__input,
.subject-overview__item + .subject-overview__item,
.select-item,
.sidebar select {
	border: none;
}
.main-menu__logo,
.page-actions-sidebar__arrow,
.subject-overview__arrow {
	display: none;
}
.sidebar-button:hover {
	background: #09b3ff;
}
.custom-list__item {
	padding-left: 10px;
}
.me-menu__header {
	margin-top: -5px;
}
.page-actions-sidebar__title:before,
.subject-overview__title:before {
	font: bold 22px FontAwesome;
	content: "\F104";
	color: #fff;
	margin-right: 5px;
}
.add-content-sidebar__link:hover .add-content-sidebar__add {
    transform: rotate(360deg) scale(1.5);
    transition: 500ms ease;
		-webkit-transition: 1s ease;
		-moz-transition: 1s ease;
	border-radius: 100px;
}

/*//////////// footer ////////////*/
.widget-page__ads {
    display: none;
}
.page-footer,
.page-footer__toggler:hover {
    background: rgba(0,0,0,.8) !important;
}
.page-footer__link, .page-footer__link:visited,
.page-footer__subtitle,
.page-footer__copyright,
.page-footer__icon {
	color: #f0f0f0;
}
.page-footer__logo {
	display: none;
}
.page-footer__toggler {
	background: transparent !important;
	transition: 500ms ease;
		-webkit-transition: 500ms ease;
		-moz-transition: 500ms ease;
}
.page-footer.page-footer .page-footer__toggler:after {
	font: normal 18px FontAwesome;
	content: "\F106";
	color: #fff;
}
.page-footer.page-footer_expanded .page-footer__toggler:after {
	content: "\F107";
}
.page-footer__toggler {
	border-radius: 100px;
	top: -5px;
}
#loader {
	display: none !important;
}
}

style_setting_options.value:
/*//////////// dark widgets ////////////*/
.widget,
.rss-tooltip{
    color: #f0f0f0;
    background: rgba(0,0,0,/*[[widg]]*/);
    border-radius: 15px;
	box-shadow: none;
}
.widget__container {
    background: none !important;
    box-shadow: none;
}
.page-section__button,
.currency-converter-widget .currency-converter-widget__input {
	background: rgba(0,0,0,.7);
}
.page-section__sign,
.SandboxRoot.env-bp-430 .timeline-Tweet-text,
.u-linkBlend:not(:focus):not(:hover):not(:active),
.notes-widget__textarea,
.embed-widget__area,
.SandboxRoot,
.add-todo,
.calculator__input,
.fa-times::before,
.rss-widget__adder {
	color: #f0f0f0 !important;
}
.widget-header__settings-list .settings-list__button {
	border-radius: 0 13px 0 13px 
}
.keypad__key {
	border-radius: 100px;
}
.calculator__display {
	border: none;
}
.analog-clock--modern,
.analog-clock--sec {
	filter: invert(1);
}
