css:
@-moz-document domain("feedly.com") {
.pro {
    display: none !important;
}
.fx .container {
    max-width: none;
}
div h4 {
    margin: 10px !important;
}
header,
.col-xs-6,
.col-md-6 {
    margin-top: 10px !important;
    padding-top: 0px !important;
}
@media screen and (min-width: 1200px) and (max-width: 1366px) and (orientation: landscape) {
    .fx .entry.u5 {
        margin-left: 0 !important;
        margin-right: 0 !important;
        margin-bottom: 20px !important;
        margin-top: -8px !important;
        padding: 4px !important;
        width: 25.2vw !important;
        height: 44vh !important;
        border: 0px solid transparent !important;
        border-radius: 0px !important;
    }
    .fx .entry.u5 .visual,
    .fx .entry.u5 .visual-overlay {
        height: 44vh !important;
        width: 100% !important;
    }
}
@media screen and (min-width: 1367px) and (max-width: 1680px) and (orientation: landscape) {
    .fx .entry.u5 {
        margin-left: 0 !important;
        margin-right: 0 !important;
        margin-bottom: 20px !important;
        margin-top: -8px !important;
        padding: 4px !important;
        width: 26.5vw !important;
        height: 45vh !important;
        border: 0px solid transparent !important;
        border-radius: 0px !important;
    }
    .fx .entry.u5 .visual,
    .fx .entry.u5 .visual-overlay {
        height: 45vh !important;
        width: 100% !important;
    }
}
@media screen and (min-width: 1681px) and (max-width: 2000px) and (orientation: landscape) {
    .fx .entry.u5 {
        margin-left: 0 !important;
        margin-right: 0 !important;
        margin-bottom: 20px !important;
        margin-top: -8px !important;
        padding: 4px !important;
        width: 27.5vw !important;
        height: 45vh !important;
        border: 0px solid transparent !important;
        border-radius: 0px !important;
    }
    .fx .entry.u5 .visual,
    .fx .entry.u5 .visual-overlay {
        height: 45vh !important;
        width: 100% !important;
    }
}
.fx .entry.u5 .content {
    position: relative;
    top: -75px !important;
    margin: 10px !important;
}
.title {
    position: relative;
    transition: transform 0.3s linear!important;
    -moz-transition: transform 0.3s linear!important;
    -webkit-transition: transform 0.3s linear!important;
    -o-transition: transform 0.3s linear!important;
    -ms-transition: transform 0.3s linear!important;
    height: 100px !important;
}
.metadata,
.summary {
    display: none !important;
}
.visual-overlay {
    transition: all 300ms ease-in-out!important;
}
.visual {
    background-size: cover;
    background-position: bottom;
    transition: transform 0.3s linear!important;
    -moz-transition: transform 0.3s linear!important;
    -webkit-transition: transform 0.3s linear!important;
    -o-transition: transform 0.3s linear!important;
    -ms-transition: transform 0.3s linear!important;
}
.fx .entry.u5:hover .visual-overlay,
.fx .entry.u5.tag-picker-open .visual-overlay,
.fx .entry.u5.quicklisted:hover .visual-overlay {
    opacity: 0.5!important;
    z-index: 10 !important;
    -webkit-transition: all 300ms ease-in !important;
    -webkit-transform: scale(1) rotate(2deg) !important;
    -ms-transition: all 300ms ease-in !important;
    -ms-transform: scale(1) rotate(2deg) !important;
    -moz-transition: all 300ms ease-in !important;
    -moz-transform: scale(1) rotate(2deg) !important;
    transition: all 300ms ease-in !important;
    transform: scale(1) rotate(2deg) !important;
}
.fx .entry.u5:hover .visual {
    z-index: 9 !important;
    -webkit-transition: all 300ms ease-in !important;
    -webkit-transform: scale(1) rotate(2deg) !important;
    -ms-transition: all 300ms ease-in !important;
    -ms-transform: scale(1) rotate(2deg) !important;
    -moz-transition: all 300ms ease-in !important;
    -moz-transform: scale(1) rotate(2deg) !important;
    transition: all 300ms ease-in !important;
    transform: scale(1) rotate(2deg) !important;
}
.fx .entry.u5:hover .title {
    z-index: 10 !important;
    -webkit-transition: all 300ms ease-in !important;
    -webkit-transform: scale(1) rotate(2deg) !important;
    -ms-transition: all 300ms ease-in !important;
    -ms-transform: scale(1) rotate(2deg) !important;
    -moz-transition: all 300ms ease-in !important;
    -moz-transform: scale(1) rotate(2deg) !important;
    transition: all 300ms ease-in !important;
    transform: scale(1) rotate(2deg) !important;
    color: #fff !important;
}
}
