css:
@-moz-document url("https://c.disquscdn.com/next/embed/styles/lounge.a9c345d20223cddc6d9c2b57728298f1.css"), domain("bezprawnik.pl") {
     body {
         background: #171717;
         color: #8b8d93;
    }
     .headline span {
         padding: 1px 5px 0;
         background-color: #2e2e2e;
    }
     .wall-bg .wall-bg-inner {
         background: transparent;
     }
     .wall-bg {
         background-image: none !important;
     }
     .article-content blockquote, .article-content code, .article-content div, .article-content input, .article-content label, .article-content li, .article-content p, .article-content pre, .article-content td {
         color: #787777;
    }
     .categories-nav ul li a {
         color: #8a8a8a;
    }
     .categories-nav ul li a:focus, .categories-nav ul li a:hover {
         color: #727272;
    }
     h1, h2, h3, h4, h5, h6 {
         color: #7a7a7a;
    }
     .tab-conversation.active > a {
         color: #2196f3 !important;
    }
     .textarea-wrapper {
         background: #383838 !important;
         border: 2px solid #2d2d2d !important;
         border-radius: 4px;
    }
     .placeholder {
         color: #aaa;
    }
     .post-actions {
         background: #2d2d2d;
         border-top: solid 0 #3f4f59;
    }
     .btn {
         box-shadow: none;
    }
     .post-action__button {
         margin: 0 -2px 0 0;
    }
     .nav-primary {
         border-bottom: 2px solid #1b5b8d;
    }
     .vote-down {
         border-left: 1px solid #2196f3;
    }
     .comment__footer .vote-down, .comment__footer a, .comment__header .vote-down, .comment__header a {
         color: #2196f3;
    }
     .comment__footer .vote-down:hover, .comment__footer a:hover, .comment__header .vote-down:hover, .comment__header a:hover {
         color: #1b7ece;
    }
     .disqus-footer__wrapper {
         border-top: 2px solid #2c2c2c;
    }
     #header-scroll {
         background: #0c436f;
    }
     #header a.search-link, #header ul li a, #header-scroll ul li a, a.white {
         color: #6bbcfc !important;
    }
     #header a.search-link:hover, #header ul li a:hover, #header-scroll ul li a:hover, a.white:hover {
         color: #5999cc !important;
    }
     .button {
         color: #2a9bf8 !important;
    }
     .button.wycen-scroll {
         box-shadow: inset 0 0 0 .125rem #1878c6;
         color: #2a9bf8;
    }
     .button.wycen-scroll:hover {
         color: #abd4f5 !important;
         background: #1878c6 !important;
    }
     #header {
         background: #0c436f;
    }
     .categories-nav ul li a::before {
         background: #1777c3;
    }
     #header .logo img {
         filter: brightness(70%);
    }
     .article .article-cover {
         filter: brightness(80%);
    }
     body.dark {
         color: #8f8f8f;
    }
     .article.entryheight .title {
         background-color: #333;
    }
     .article.entryheight .title h2 a {
         color: #929292;
    }
     .article.bluebg .text, .article.bluebg .title:hover {
         background-color: #219dff !important;
    }
     #footer {
         background-color: #111;
    }
     #footer li a:hover, #footer p a:hover {
         color: #219dff;
    }
     #footer li a::before {
         background-color: #219dff;
    }
     .article.bluebg:hover .text a:hover, .article.bluebg:hover .title a:hover {
         color: #000 !important;
    }
     .article-content a:not(.button) {
         color: #28a0ff;
    }
     .article .kategoria {
         color: #c2c2c2;
         background: #333;
    }
     .article.large .text {
         background-color: #333;
    }
     .article.large .text h1 a {
         color: #9e9d9d;
    }
     .article.large .text blockquote, .article.large .text code, .article.large .text input, .article.large .text label, .article.large .text li, .article.large .text p, .article.large .text pre, .article.large .text td {
         color: #a8a8a8;
    }
     .article.normal .text {
         background-color: #333;
    }
     .article.normal .text h2 a {
         color: #8f8f8f;
    }
     .wp-pagenavi .first, .wp-pagenavi .last, .wp-pagenavi .nextpostslink {
         color: #a8a8a8;
         border-color: #a8a8a8;
    }
     .wp-embed {
         color: #919192;
         background: #212121;
         border: 1px solid #212121;
    }
     .wp-embed-heading a {
         color: #a4a4a4;
    }
     blockquote, code, input, label, li, p, pre, td {
         color: #939393;
    }
     form:not(#adminbarsearch) label {
         color: #7b7b7b;
    }
     [type="color"], [type="date"], [type="datetime-local"], [type="datetime"], [type="email"], [type="month"], [type="number"], [type="password"], [type="search"], [type="tel"], [type="text"], [type="time"], [type="url"], [type="week"], textarea {
         border: 1px solid #202020;
         background-color: #202020;
         color: #aaa7a7;
    }
     form:not(#adminbarsearch) input[type="submit"] {
         border: .0625rem solid #171717;
    }
     .sidebar {
         background-color: #171717;
    }
     .visual-wide {
         background-color: #171717;
    }
     .wp-pagenavi .current {
         color: #a8a8a8;
         border-color: #a8a8a8;
    }
}
