css:
@-moz-document domain("time.mk") {
#header_content {
  display: flex;
  justify-content: space-between;
}
#step1 {
  max-width: 100%;
}
#step2 {
  display: flex;
  justify-content: space-between;
}
#step2 a {
  width: 100%
}
#wrapper {
  width: auto;
  margin: 0 1em;
}
#news_pane {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  width: auto !important;
  padding-right: 0 !important;
}
#news_pane.top .cluster:nth-child(3) {
  /* glavna strana, top vesti, vtor red*/

  margin: 0 auto
}
#news_pane .cluster {
  width: 49%;
  margin-right: 1%;
}
#news_pane .article_body {
  width: calc(100% - 152px);
}
.kategorija_clusters .article_body {
  width: calc(100% - 125px);
}
.article_body .snippet {
  padding-bottom: 5px;
}
.pagination {
  margin-left: auto; /*align right*/
}
#footer {
  width: auto
}
#ads_pane {
  display: none
}
}
