css:
@-moz-document url-prefix("https://dou.ua/") {
#commentsList {
    position: relative;
}

#commentsList:before {
    content: '';
    display: block;
    position: absolute;
    height: 100%;
    width: 1px;
    top: 0;
    left: -1em;
    border-right: 1px dashed #888;
}

.b-comments .b-comment.level-1, .b-comments .b-comment.level-2, .b-comments .b-comment.level-3, .b-comments .b-comment.level-4, 
.b-comments .b-comment.level-5, .b-comments .b-comment.level-6, .b-comments .b-comment.level-7,.b-comments .b-comment.level-8, 
.b-comments .b-comment.level-9, .b-comments .b-comment.level-10, .b-comments .b-comment.level-11
{
    position: relative;
}

.b-comments .b-comment.level-1:before, .b-comments .b-comment.level-2:before, .b-comments .b-comment.level-3:before, .b-comments .b-comment.level-4:before, 
.b-comments .b-comment.level-5:before, .b-comments .b-comment.level-6:before, .b-comments .b-comment.level-7:before,.b-comments .b-comment.level-8:before, 
.b-comments .b-comment.level-9:before, .b-comments .b-comment.level-10:before, .b-comments .b-comment.level-11:before
{
    content: '';
    display: block;
    position: absolute;
    height: 1px;
    top: 20px;
    right: calc(100% + 0.5em);
    border-top: 1px dashed #888;
    z-index: 1;
}

.b-comments .b-comment.level-1:after, .b-comments .b-comment.level-2:after, .b-comments .b-comment.level-3:after, .b-comments .b-comment.level-4:after, 
.b-comments .b-comment.level-5:after, .b-comments .b-comment.level-6:after, .b-comments .b-comment.level-7:after,.b-comments .b-comment.level-8:after, 
.b-comments .b-comment.level-9:after, .b-comments .b-comment.level-10:after, .b-comments .b-comment.level-11:after
{
    content: '';
    display: block;
    position: absolute;
    top: calc(20px - 1.5em);
    right: calc(100% + 0.5em);
    text-align: right;
    color: #888;
    background: #fff;
    height: 2em;
}

.b-comments .b-comment.level-1 {
	margin-left: 3em;
}

.b-comments .b-comment.level-1:before, .b-comments .b-comment.level-1:after  {
    width: 3em;
}

.b-comments .b-comment.level-1:after  {
    content: 'L1';
}

