css:
@-moz-document url-prefix("https://dou.ua/") {
.b-comments .b-comment .scroll-wrapper:target+.comment {
    margin-left: 0;
}

.b-comments .b-comment.level-1, .b-comments .b-comment.level-2, .b-comments .b-comment.level-3, .b-comments .b-comment.level-4, 
.b-comments .b-comment.level-5, .b-comments .b-comment.level-6, .b-comments .b-comment.level-7,.b-comments .b-comment.level-8, 
.b-comments .b-comment.level-9, .b-comments .b-comment.level-10, .b-comments .b-comment.level-11
{
    position: relative;
}

.b-comments .b-comment.level-1:before, .b-comments .b-comment.level-2:before, .b-comments .b-comment.level-3:before, .b-comments .b-comment.level-4:before, 
.b-comments .b-comment.level-5:before, .b-comments .b-comment.level-6:before, .b-comments .b-comment.level-7:before,.b-comments .b-comment.level-8:before, 
.b-comments .b-comment.level-9:before, .b-comments .b-comment.level-10:before, .b-comments .b-comment.level-11:before
{
    content: '';
    display: block;
    width: 1px;
    position: absolute;
    height: calc(100% + 3em);
    top: -1.5em;
    left: -1.2em;
    border-left: 1px dashed;
}

.b-comments .b-comment.level-1 {
	margin-left: 3em;
}

.b-comments .b-comment.level-1:before {
    border-color: #0BE000;
}

.b-comments .b-comment.level-2 {
	margin-left: 5em;
}

.b-comments .b-comment.level-2:before {
    border-color: #0ACC00;
}

.b-comments .b-comment.level-3 {
	margin-left: 7em;
}

.b-comments .b-comment.level-3:before {
    border-color: #09B800;
}

.b-comments .b-comment.level-4 {
	margin-left: 9em;
}

.b-comments .b-comment.level-4:before {
    border-color: #08A300;
}

.b-comments .b-comment.level-5 {
	margin-left: 11em;
}

.b-comments .b-comment.level-5:before {
    border-color: #078F00;
}

.b-comments .b-comment.level-6 {
	margin-left: 13em;
}

.b-comments .b-comment.level-6:before {
    border-color: #067A00;
}

.b-comments .b-comment.level-7 {
	margin-left: 15em;
}

.b-comments .b-comment.level-7:before {
    border-color: #056600;
}

.b-comments .b-comment.level-8 {
	margin-left: 17em;
}

.b-comments .b-comment.level-8:before {
    border-color: #045200;
}

.b-comments .b-comment.level-9 {
	margin-left: 19em;
}

.b-comments .b-comment.level-9:before {
    border-color: #033D00;
}

.b-comments .b-comment.level-10 {
	margin-left: 21em;
}

.b-comments .b-comment.level-10:before {
    border-color: #022900;
}

.b-comments .b-comment.level-11 {
	margin-left: 23em;
}

.b-comments .b-comment.level-11:before {
    border-color: #011400;
}
}
