css:
@-moz-document domain("manjaro.org") {
.container {
    position: relative;
    margin: 0 auto;
    width: 100%;
}

#content-part {
    width: 90%;
    float: left;
}

#content,
#content-part {
    padding: 0px 10px;
}

.pad {
    padding: 20px 20px 20px;
}

#sidebar {
    width: 7.5%;
    padding: 10px 0;
    z-index: 3;
    position: relative;
}

#sidebar.sidebar-right {
    float: right;
    margin-left: 25px;
}

pad {
    padding: 35px;
}

body {
    font-size: 18px;
}

.blog .entry, .single .entry, .archive .entry, .search .entry {
    margin: 0 0 10px 0;
}

.widget {
    color: #777;
    font-size: 14px;
    margin-bottom: 30px;
}

#footer-widgets {
    background: #fff;
    padding-top: 25px;
    padding-bottom: 20px;
}
}
