css:
@-moz-document domain("wuxiaworld.com") {
/* Main Color & Fonts*/
body.custom-background {
    background: rgb(75,70,70);
}

.post,
#comments,
.hentry,
.error-404,
.not-found,
.searchPage {
    background: #65594D !important;
    color: rgb(190, 185, 175);
    font-size: 16px;
}

/* Menu Colors */
#site-navigation ul li {
    border-right: 1px solid rgba(101,101,101,.4);
}
.main-navigation ul ul li {
    border-top: 1px solid rgba(126,125,125,.2);
}

.featured-title {
    border: 1px solid rgba(0, 0, 0, 0.7);
    border-bottom: none;
    margin-top: 0px;
    height: 50px;
    margin-bottom: 0px;
}

/* Chapter List on Main Page */
.stacked-list-item,
.stacked-list {
    background: #65594D;
    color: rgb(190, 185, 175);
    font-size: 16px;
}

.stacked-list {
    border-top: 1px solid rgba(0, 0, 0, 0.7) !important;
    border-bottom: 1px solid rgba(0, 0, 0, 0.7) !important;
    margin-bottom: 20px !important;
}

.stacked-list-item:hover {
    background-color: #2e2d2d;
    cursor: pointer;
}

.active > .collapsible-header, 
.active > .collapsible-body {
    background-color: #2e2d2d !important;
}

.right {
    border-color: rgba(0, 0, 0, 0.2) !important;
    background-color: #65594D !important;
}

.active:first-child .right {
    border-color: rgba(0, 0, 0, 0.2) !important;
}

.ww_action_click .right .ww_elapsed_time::after {
    z-index: 10;
    text-align: center;
    font-weight: 800 !important;
    background-color: #2e2d2d !important;
    border-right: 1px solid rgba(0, 0, 0, 0.7) !important;
    content: '˅'; /* Need to replace image with text to change color */
    border-left: none;
    top: 0px;
    height: 42px;
}

.title {
    border-left: 1px solid rgba(0, 0, 0, 0.7) !important;
}

/* Fix different hight for first expand button */
.collapsible:first-child .collapsible-header .ww_action_click .right .ww_elapsed_time::after {
    height: 41px !important;
}

.active .right .ww_elapsed_time::after {
    content: '˄' !important;
    border-bottom: none !important;
}

#content {
	padding-top: 20px !important;
}

.nav-links div {
    border: 1px solid rgba(0, 0, 0, 0.7) !important;
}

article,
#comments {
    border: 1px solid rgba(0, 0, 0, 0.7) !important;
}

a {
    font-size: 18px !important;
}

a,
a:hover,
a:visited,
a:link,
a:active {
    color: rgb(190, 185, 175);
    text-decoration: none !important;
    border: none !important;
}

.widget {
    background: #65594D;
    color: rgb(190, 185, 175);
    font-size: 18px !important;
	border: 1px solid rgba(0, 0, 0, 0.7) !important;
}

.widget a {
    font-size: 13px !important;
}

.widget .widget-title {
    background: #2e2d2d;
    color: rgb(190, 185, 175);
    font-size: 18px;
    font-weight: bold;
}

.widget-title {
    padding: 2.5px 30px;
}

#bar {
    background: #88A788;
}

hr {
    background-color: rgb(64, 62, 62);
}

#secondary {
    margin: 0 0 0 0!important;
}

/* Hide all advertisement blocks */
iframe,
body > div:nth-of-type(3),
#i_widget-29,
#text-10,
#search-5,
#ai_widget-28,
#bar-paypal,
#ai_widget-23,
#text-26,
#ai_widget-22,
#text-23,
#ai_widget-29,
#ai_widget-30,
#ai_widget-38,
#ai_widget-36 {
    display: none !important;
}

.site-content article {
    margin-bottom: 1rem !important;
    padding-bottom: 1rem;
}

.comment > article {
    border: none !important;
}

.collapsible-body article {
    border: none;
}
}
