css:
@-moz-document domain("translate.google.com"), domain("translate.googleusercontent.com") {
#gt-c, #google-infowindow, .gmnoprint{
	display: none!important;
    visibility: hidden!important;
}
#contentframe {
    top: 0!important
}
.notranslate:hover {
    background-color: transparent!important;
}
#primary, #page, #main, .type-page{
	width:100%;
}
.horizontal-wide{
	display: none;
}
.entry-header {
    position: relative;
    top: 27px;
    width: fit-content;
    margin: 0 auto;
}
.hentry{
	font-size: 22px;
}
h1{
	font-size: 28px;
}
.entry-content div div p:nth-child(3) span{
    color: rgba(0,0,0,.0);
    border: solid 2px red;
}
.entry-content div div p:nth-child(3):after{
    content: "<<<-Название скрыто, для отображения выделите]";
}
.entry-content div div p::first-letter{
	color: white;
    font-size: 1.2em;
}
body{
	font-family: Tahoma;
}
.entry-content div div p:nth-child(1) span a, .entry-content div div p:last-child span a{
	font-size:30px;
    border: solid 2px #d3d3d3;
    border-radius: 5px;
}
a{
	text-decoration:none !important;
}
#comments, #secondary, .beforeCommentsPage{
	display: none;
}
}
