css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://jira.diamond.ac.uk/secure/RapidBoard.jspa") {
  .js-kanplan-enable-inline-dialog { display: none !important; }
}

@-moz-document url-prefix("https://jira.diamond.ac.uk/secure/RapidBoard.jspa?rapidView=2007") {
  #ghx-header { background: url("https://raw.githubusercontent.com/Anthchirp/stylish/master/winter.png") 13px bottom no-repeat; padding-left: 90px; background-size: 61px 80px; }
  #ghx-board-name,.subnavigator-title { color: transparent; }
  .subnavigator-title::before { content: "Scisoft MX team status"; color: black; }
  #ghx-release { display: none; }

  .ghx-issue { border: 1px solid black; border-radius: 0 3px 3px 0; border-left: 0; }
  .ghx-issue .ghx-issue-content { min-height: 0; padding-left: 13px; padding-bottom: 2px; padding-top: 7px; }
  .ghx-issue .ghx-extra-fields { margin: 0; }
  .ghx-issue .ghx-extra-fields .ghx-extra-field { font-size: 10px; margin-top: 3px; font-weight: bold; color: #C84; }
  .ghx-issue .ghx-extra-fields .ghx-extra-field.ghx-fa { display:none; }
  .ghx-issue .ghx-flags { display: none; left: auto; right: 47px; top: 7px; }
  .ghx-issue.ghx-flagged { background-color: #ffeeee; }
  .ghx-issue.ghx-flagged .ghx-flags { display: block !important; }
  .ghx-issue .ghx-type { display: none; }
  .ghx-issue .ghx-days { z-index: 100; }
  .ghx-issue.ghx-done .ghx-key a { text-decoration: none }
  .ghx-issue-fields .ghx-summary .ghx-inner { overflow: visible; }
  .ghx-column-headers .ghx-column:nth-child(1) { border-color: blue; }
  .ghx-column-headers .ghx-column:nth-child(2) { border-color: yellow; }
  .ghx-column-headers .ghx-column:nth-child(3) { border-color: greenyellow; }
  .ghx-column-headers .ghx-column:nth-child(4) { border-color: green; }
  .ghx-columns .ghx-column:nth-child(1) { background-color: #EEF8FF; }
  .ghx-columns .ghx-column:nth-child(2) { background-color: #FFD; }
  .ghx-columns .ghx-column:nth-child(3) { background-color: #EFD; }
  .ghx-columns .ghx-column:nth-child(4) { background-color: #DFD; }
}

