css:
@-moz-document domain("moodle.intecbrussel.be") {
.pagelayout-frontpage, .pagelayout-incourse, .pagelayout-general, .pagelayout-course, .pagelayout-coursecategory, .pagelayout-admin, .pagelayout-mydashboard, .pagelayout-mypublic, .pagelayout-login, .pagelayout-report{
    background-color: black;
}
body{
    background-color: black;
}
body, h1, h2, h3, h4, h5, h6, p, ul, ol, dl, input, textarea{
    color: #4a8341;
}

#site-news-forum h2, #frontpage-course-list h2, #frontpage-category-names h2, #frontpage-category-combo h2{
    color: #b4e9b7;
}

.forumpost{
    background-color: rgb(0, 34, 44);
}

.forumpost .topic{
    background: rgb(37, 49, 53);
}

.forumpost .content{
    background-color: rgb(0, 34, 44);
}
span{
    background: rgb(37, 107, 107)!important;
    color: whitesmoke !important;
}

a{
    color: greenyellow;
}

a:hover, a:focus{
    color: green;
}

.block .content{
    background-color: rgb(0, 34, 44);
}

.top-bar, .block .title h2{
    background: rgb(37, 49, 53);
    color: whitesmoke;
    border-color: whitesmoke;
}

#page-footer{
	background-color: rgba(199,199,199,1);
}

#page-admin-course-manage .generalbox th, .editcourse .header, .results .header, #attempts .header, .generaltable .header, .environmenttable th, .forumheaderlist th{
    background: rgb(37, 49, 53);
}

table#explaincaps tbody > tr:nth-child(2n+1) > td, table#defineroletable tbody > tr:nth-child(2n+1) > td, table.grading-report tbody > tr:nth-child(2n+1) > td, table#listdirectories tbody > tr:nth-child(2n+1) > td, table.rolecaps tbody > tr:nth-child(2n+1) > td, table.userenrolment tbody > tr:nth-child(2n+1) > td, table#form tbody > tr:nth-child(2n+1) > td, form#movecourses table tbody > tr:nth-child(2n+1) > td, #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > td, .forumheaderlist tbody > tr:nth-child(2n+1) > td, table.flexible tbody > tr:nth-child(2n+1) > td, .generaltable tbody > tr:nth-child(2n+1) > td, table#explaincaps tbody > tr:nth-child(2n+1) > th, table#defineroletable tbody > tr:nth-child(2n+1) > th, table.grading-report tbody > tr:nth-child(2n+1) > th, table#listdirectories tbody > tr:nth-child(2n+1) > th, table.rolecaps tbody > tr:nth-child(2n+1) > th, table.userenrolment tbody > tr:nth-child(2n+1) > th, table#form tbody > tr:nth-child(2n+1) > th, form#movecourses table tbody > tr:nth-child(2n+1) > th, #page-admin-course-index .editcourse tbody > tr:nth-child(2n+1) > th, .forumheaderlist tbody > tr:nth-child(2n+1) > th, table.flexible tbody > tr:nth-child(2n+1) > th, .generaltable tbody > tr:nth-child(2n+1) > th{
    background-color: rgb(0, 34, 44);
}

.myclass h2, .mybutton, .mybutton-login, button, html input[type="button"], input[type="reset"], input[type="submit"]{
    background: rgb(37, 49, 53);
    color: greenyellow;
}

textarea[cols], input[size]{
    background-color: #4e6254;
    color: whitesmoke;
}

.loginbox{
    background-color: rgb(0, 34, 44);
}
select:focus, input[type="file"]:focus, input[type="radio"]:focus, input[type="checkbox"]:focus{
    color: green;
}

select, input[type="file"], input[type="radio"], input[type="checkbox"]{
    background: rgb(37, 49, 53);
    color: greenyellow;
}

button:focus, input.form-submit:focus, input[type="button"]:focus, input[type="submit"]:focus, input[type="reset"]:focus{
    color: green;
    background-color: rgb(0, 34, 44);
}

button:hover, input.form-submit:hover, input[type="button"]:hover, input[type="submit"]:hover, input[type="reset"]:hover{
    color: green;
    background-color: rgb(0, 34, 44);
}
.calendar-event-panel .yui3-overlay-content{
    background-color: rgb(0, 34, 44) !important;
}
#page{
    background-color: black;
}

.course-content ul.topics li.section{
    background-color: rgb(0, 34, 44);
}

.sitetopic .section .activity .activityinstance:hover, .course-content .section .activity .activityinstance:hover, .sitetopic .section .activity .contentwithoutlink:hover, .course-content .section .activity .contentwithoutlink:hover{
    background: rgb(37, 49, 53);
    color: green;
}

.path-grade-report-user .user-grade.generaltable .levelodd{
    background-color: rgb(0, 34, 44) !important;
}

.moodle-actionmenu[data-enhanced].show.nowrap-items .menu > li{
    background-color: rgb(0, 34, 44);
}

.moodle-actionmenu[data-enhanced].show .menu{
    background-color: rgb(0, 34, 44);
}
}
