css:
@-moz-document regexp(".*(https?://mysitex.com|/admin).*"), regexp(".*/util/.*\\.sx.*") {
BODY {
    background-color: #2B2B2B;
    color: #C8C8C8;
}

#__blockobj_notify {
    color: #2B2B2B;
}

SPAN.hover {
    color: #BB3C3C;
}

h4 {
    color: #BB3C3C;
}

HR {
    color: #BB3C3C;
}

input,
select,
textarea {
    background: #45494A;
    color: #C8C8C8;
    border: 1px solid #313335;
}

.star {
    color: #E01603;
}

/* Настройки отображения базового вида ссылок */
A {
    color: #C8C8C8;
}

A:active, A:hover {
    color: #BB3C3C;
}

/*Разные вариации на тему внешнего вида ссылок */
.black {
    color: #C8C8C8;
}

.b#red {
    color: #BB3C3C;
    background-color: #C8C8C8;
}

A.cyan {
    color: #0000ff;
}

A.bookmark {
    color: #C8C8C8;
}

A.bookmark:hover {
    color: #282C77;
}

A.searchRes {
    color: #9E2927;
}

A.menuL2 {
    color: #9E2927;
}

A.menuL2:hover {
    color: #6A90C8;
}

A.menu {
    color: #2B2B2B;
}

A.menu:active, A.menu:hover {
    color: #FFD8AF
}

A.head {
    color: #E01603;
}

A.head:hover {
    color: #B30000
}

A.nav2lev {
    color: brown;
}

A.nav3lev {
    color: #BB3C3C;
}

A.selected {
    color: #2B2B2B;
    background-color: #4961FF;
}

A.selected:active {
    color: #2B2B2B;
    background-color: #4961FF;
}

A.selected:hover {
    color: #2B2B2B;
}

.header1 {
    color: #2B2B2B;
}

.w {
    color: #2B2B2B;
}

.w2 {
    color: #183984;
}

.w3 {
    color: #E01603;
}

.frag {
    color: #BB3C3C;
}

.searchUrl {
    color: #666666;
}

.alarm {
    color: #ee0000;
    font-weight: bold;
}

.leg {
    color: #C8C8C8;
}

/*Стиль отображения ячейки таблицы содержащей заголовок объекта*/
.title {
    color: #C8C8C8;
}

.silver {
    background-color: #414C5E;
}

INPUT.disabled {
    background-color: #313335;
}

.toolbar {
    background: #3C3F41;
}

.controlbuttons {
    background-color: #2B2B2B;
    border-top: 2px #313335 groove;
}

button.util_over {
    background: #F4F3E4;
}

button.util {
    color: #C8C8C8;
}

.button_red, .button_blue, .button_green, .button_lightblue, .button_orange, .button_clear {
    background: #3C3F41;
    border: 1px solid #313335;
    color: #C8C8C8;
}

.button_clear {
    background: #2B2B2B none;
}

button.work, button.work_active {
    background: #2B2B2B;
    border: 1px solid #313335;
}

button.work_active {
    background: #313335 !important;
}

TD.work, TD.work_active {
    background: #2B2B2B;
    border: 1px solid #313335;
}

TD.work_active {
    background: #313335 !important;
}

.pRedicted {
    color: #2B2B2B;
    background: #e4e4e4 !important;
}

.highlight {
    border: 1px solid rgb(234, 206, 120);
    background: rgb(255, 222, 123);
}

/*Настройки стиля для строк в списке объектов*/
TR.obj {
    background-color: #2B2B2B;
}

TR.obj_over {
    background-color: #2B2B2B;
}

TR.obj_disabled {
    background-color: #FEF5E3;
}

TR.colorObj_over {
    background-color: #313335 !important;
}

TR.colorObj_disabled {
    background-color: #FEF5E3 !important;
}

/*Настройки стиля для строк в списке папок*/
TR.folder_thumb {
    background-color: #2B2B2B;
}

TR.folder_thumb:hover {
    background: #3C3F41;
}

.folder_thumb_title {
    color: #C8C8C8;
}

TD.obj, TH.obj {
    border-top: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

TD.objtitle_line {
    border-top: #313335 1px solid;
}

.thumbnails {
    border: 1px solid #313335;
    background-color: #2B2B2B;
}

.gridRow, .gridRow2 {
    background-color: #2B2B2B;
}

/* ячейки таблицы */
table.sort tbody td {
    border-left: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

/* стрелка слева в строке таблицы*/
table.sort .arrow {
    border-left: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

/*Правая граница таблицы*/
table.sort td.Rborder {
    border: 1px solid #313335;
}

/*Левая граница таблицы*/
table.sort td.Lborder {
    border: 1px solid #313335;
}

/* заголовки таблицы */
table.sort thead tr {
    background: #3C3F41;
}

table.sort thead th {
    border-left: #313335 1px solid;
    border-bottom: 1px solid #313335;
    border-top: 1px solid #313335;
}

table.sort thead td.tdth {
    border-bottom: 1px solid #313335;
    border-left: 1px solid #313335;
    border-top: 1px solid #313335;
}

table.sort thead td.filter {
    border-left: #313335 1px solid;
    border-bottom: 1px solid #313335;
}

table.sort_grid {
    background-color: #2B2B2B;
}

/*класс гридовой таблицы  уровень 2 */

/* ячейки таблицы */
table.sort_grid tbody td {
    border-bottom: 1px solid #313335;
    background-color: #2B2B2B;
}

/* стрелка слева в строке таблицы*/
table.sort_grid .arrow {
    border-bottom: 1px solid #313335;
    background-color: #2B2B2B;
}

/*Правая граница таблицы*/
table.sort_grid td.Rborder {
    border: 0;
    border-bottom: 1px solid #313335;
}

/* заголовки таблицы */
table.sort_grid thead tr {
    background-color: #2B2B2B;
}

table.sort_grid thead td.sort_grid_head {
    background-color: #2B2B2B;
    border-left: 1px solid #313335;
    border-bottom: 1px solid #313335;
    color: #2B2B2B;
}

/* заголовок отсортированного столбца */
table.sort_grid thead th.curcol {
    background-color: buttonshadow;
    color: #2B2B2B;
}

table.eatr_cal .cal_body {
    background-color: #2B2B2B;
    /*border:#313335 1px solid;  */
}

/* edit string attr*/
.editStringAttr {
    border: 1px solid #313335;
    background-color: #2B2B2B;
    color: #C8C8C8;
}

.errorTH {
    color: #BB3C3C;
}

TD.stackTrace {
    color: #000066;
}

DIV.error {
    color: #BB3C3C;
}

A.error {
    color: #BB3C3C;
}

A:HOVER.error {
    color: #BB3C3C;
}

A:ACTIVE.error {
    color: #BB3C3C;
}

.ramka {
    background-color: #2B2B2B;
}

.editBG {
    background-color: #2B2B2B;
}

.shaRed {
    color: #555555;
}

.shaRed_area {
    color: #555555;
    background-color: #2B2B2B;
}

.shaRed_line {
    color: #555555;
    background-color: #313335;
}

/*Настройки отображения вкладок формы поиска*/
/*активная закладка*/
.searchTab_a {
    background: #313335;
}

/*неактивные закладки*/
.searchTab_p {
    background: #2B2B2B;
}

.searchTab_p1 {
    background: #2B2B2B;
}

.t {
    background-color: #2B2B2B;
}

.v {
    background-color: #2B2B2B;
}

/*Цвет используемый в качестве фона в форме поиска, заголовке списка объектов, активной группе аттрибутов... */
.bg {
    background-color: #3C3F41;
}

/*Цвет используемый в качестве фона в строках списка объектов*/
.bg1 {
    background-color: #3C3F41;
}

.bg2, .inSearchParam {
    background-color: #3C3F41;
    color: #C8C8C8;
}

.bg3 {
    background-color: #2B2B2B;
}

.bg4 {
    background-color: #2B2B2B;
}

/* active bookmark in objlist page navigator */

.innerAttrsArea {
    border: 1px dotted #313335;
}

/*Активная группа*/
.tabA {
    background-color: #3C3F41;
    border: 1px solid #313335;
    color: #EEEEEC;
    border-collapse: collapse;
}

.tabALeft {
    background-color: #3C3F41;
    border: 1px solid #313335;
    color: #EEEEEC;
}

.tabAPLeft {
    background-color: #2B2B2B;
    border: 1px solid #313335;
    color: #EEEEEC;
}

/*Неактивные группы*/
.tabP, .tabPLeft, .tabW, .tabW2, .tabWLeft {
    background-color: #2B2B2B;
    border: 1px solid #313335;
}

.tabW, .tabW2 {
    border-bottom: 1px solid #313335;
}

.tabWLeft {
    border-right: 1px solid #313335;
}

.tab {
    background-color: #3C3F41;
    border: 1px solid #313335;
}

.tabLeft {
    background-color: #3C3F41;
    border-bottom: 1px solid #313335;
    border-right: 1px solid #313335;
}

.pageNavigator, .list, .login {
    background-color: #2B2B2B;
    border: 1px solid #313335;
    border-right: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

.pageNavigator tr {
    background-color: #3C3F41;
}

.pageNavigator th, .pageNavigator .s {
    color: #0083e9;
    background-color: #2B2B2B;
}

/* Стили разного рода границ */
.bord_r {
    border-right: 1px solid #313335;
}

.bord {
    border: 1px solid #313335;
}

.black_bord {
    border: 1px solid #313335;
}

/*styles for modal window*/
#overlay {
    background-color: #000;
}

div.expandGrid {
    background-color: #313335;
}

/*styles for spoilers in articles*/
.sp-wrap {
    background: #E9E9E6;
}

/*.sp-head-wrap {padding: 1px 5px 2px;border: solid #C3CBD1;border-width: 1px 1px 1px 2px;}*/
.sp-body {
    border: solid #313335;
    background: #2B2B2B; /* border-top-width: 0;*/
}

.sp-head_closed, .sp-head_opened {
    border: solid #313335;
}

/*styles for groups with viewType spoiler */
.spoiler-group {
    border: #313335 1px solid;
}

.spoiler-group .spoiler-title {
    color: #C3CBD1;
}

.spoiler-group .spoiler-switch {
    border: 1px solid #313335;
}

/* Auto-Complete Styles*/
.qi-m {
    border: 1px solid #313335;
    background-color: #2B2B2B;
}

.qi-fst {
    background-color: #3C3F41;
}

.qi-snd {
    background-color: #3C3F41;
}

.qi-t {
    border-top: 1px solid #313335;
    background-color: #3C3F41;
}

.qi-b {
    background-color: #0D293E;
    color: #2B2B2B;
}

.qi-waiting {
    background: #2B2B2B url('https://habrastorage.org/webt/hs/ib/p4/hsibp4nug1fcpqg3ww0qxu87zv8.gif') right no-repeat;
}

.qi-total, .qi-prev, .qi-next, .qi-from, .qi-to, .qi-disabled {
    color: #2B2B2B;
}

.qi-total {
    color: green;
}

A.qi-disabled {
    color: #2B2B2B;
}

.yellowBar {
    background-color: #313335;
    border: 1px solid #C3CBD1;
}

/*uses in wmadress handlers*/
.progress {
    background-color: #BB3C3C;
    color: #2B2B2B;
}

/*----- styles for icons List view-----*/
.selectedIcon {
    background-color: #2B2B2B;
    border: #313335 1px solid;
}

.iconListdiv {
    background-color: #2B2B2B;
}

table.icoTable {
    border: #313335 1px solid;
}

table.icoTable tr.even {
    background-color: #FCFCFC;
}

table.icoTable tr.odd {
    background-color: #3C3F41;
}

table.icoTable td {
    border-bottom: 1px solid #313335;
}

table.icoTable .iconNumber {
    border-bottom: 1px solid #313335;
}

table.icoTable .iconHolder tr {
    background-color: #3C3F41;
}

table.icoTable .iconHolder .activeIcon {
    background-color: #414C5E !important;
}

fieldset {
    border: 1px solid #313335;
}

h3.legend {
    background: #3C3F41;
}

/*------- selected objects bar --------------*/
#selectedObjectsBar {
    background: #2B2B2B;
}

#selectedObjectsBar .sob_hide div, #selectedObjectsBar .sob_show div {
    background: #3C3F41;
    border: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

.soBar_active, .soBar_hidden {
    background-color: #2B2B2B;
    border-bottom: 1px solid #313335;
}

/*------- / end of selected objects bar --------------*/
/*------- style for message to user --------------*/
.showMess_active {
    border: 2px solid #313335;
}

.showMess_active .messArea, .showMess_passive .messArea {
    background-color: #313335;
    /*  border:2px solid #313335;*/
}

.showMess_active .messCaption, .showMess_passive .messCaption {
    background: #3C3F41;
    border-bottom: 1px solid #313335;
}

.desc {
    color: #2B2B2B;
}

span.note {
    border-left: #313335 3px solid;
}

.popup {
    box-shadow: #2B2B2B 0 10px 20px;
    background: #2B2B2B;
    border: #313335 1px solid
}

/* border:#999999 1px solid;*/
.popup .close {
    color: #2B2B2B;
}

/*#3C3F41*/
#OLContainer2, #OLcontainer2 {
    border: #313335 20px solid;
}

#formBody #OLContainer2, #formBody #OLcontainer2 {
    background: #2B2B2B
}

.popup .c {
    background: #2B2B2B;
}

#formBody #OLContainer2 .c, #formBody #OLcontainer2 .c {
    background: #2B2B2B;
    border: #313335 10px solid
}

.pctpop {
    background: #2B2B2B;
}

* html .pctpop img {
    background: #2B2B2B;
}

.create_obj_ajax {
    background: #3C3F41;
    border: 1px solid #313335;
}

.create_obj_ajax button {
    border: 1px solid #313335;
}

.create_obj_ajax button.save {
    border: 1px solid #313335;
}

/* Элементы */
.txtminor {
    color: #696969;
}

.sxnote {
    background: #2B2B2B;
    border: 1px solid #313335;
}

.sxnote .close {
    color: #C8C8C8;
    background: #2B2B2B;
}

.acc {
    background: #2B2B2B;
}

.acc [data-disabled="true"] {
    color: #a09e9e;
}

.acc [data-disabled="true"]:hover {
    color: #a09e9e
}

.upload-box-progress-el {
    border-top-color: #313335;
    border-bottom-color: #313335;
}

.upload-box-progress-el.error {
    border-top-color: #f33;
    border-bottom-color: #a33;
}

.upload-box-progress-status__success {
    color: #00aa00;
}

.upload-box-progress-status__error {
    color: #ee0000;
}

.cke_bottom,
.cke_top {
    background: #45494A;
    border-top: 1px solid #313335;
    border-bottom: 1px solid #313335;
    box-shadow: 0 1px 0 #313335 inset;
    background-image: linear-gradient(to bottom, #45494A, #2B2B2B);
    color: #C8C8C8;
}

.cke_editable {
    background: #45494A;
}

.cke_chrome {
    display: block;
    border: 1px solid #313335;
}

.select2-container,
.select2-results__option
.select2 {
    background: #45494A;
    color: #C8C8C8;
}

.select2-container--default .select2-selection--single {
    background-color: #45494A;
    color: #C8C8C8;
    border: 1px solid #313335;
}

.select2-container--default .select2-results__option--highlighted[aria-selected] {
    background-color: #414C5E;
    color: #C8C8C8;
    border: 1px solid #313335;
}

.select2-dropdown, .select2-container--default .select2-selection--single {
    background-color: #45494A;
    border: 1px solid #313335;
}

.select2-container--default .select2-results__option[aria-selected=true] {
    background-color: #3C3F41;
}

/*list.css*/
.loader .loader-message{
    background: url("https://habrastorage.org/webt/lc/av/a_/lcava_yoredzembs_rh615yyimc.gif") no-repeat scroll 50% 1em #414C5E;
    border: 1px solid #313335;
}

.sx-list-title_cell {
    color: #C8C8C8;
}

.sx-list-bar {
    background-color: #414C5E;
}
.sx-list-bar-cell {
    border-right: 1px solid #313335;
}

.selected-in-list-info {
    background-color: #414C5E;
    border-bottom: 1px solid #313335;
}
/*to force this rule in styleblue.css*/
#selectedObjectsBar {
    background:#414C5E none;
}
.sob_hide .sob_hide-button {
    background: #414C5E url("https://habrastorage.org/webt/7k/hq/jn/7khqjnyapfn8ffjbajz3snt_5ty.gif") top repeat-x;
    border: 1px solid #313335;
    border-bottom: 1px solid #313335;
}

.sx-list-tools {
    background-color:#414C5E;
    border-top:1px solid #313335;
    border-bottom:1px solid #313335;
}
.folder-list-item {
    color: #C8C8C8;
}
.folder-list-item:hover{
    background-color: #45494A;
    border: 1px solid #313335;
}

}
