css:
@-moz-document url(http://h1z1showcase.com/) {

  /* Background */

  html {
	  background: #222 url(https://images.alphacoders.com/530/thumb-1920-530974.jpg) no-repeat center center fixed;
  }

  /* Box */

  .inner {
	  background-color: transparent;
	  box-shadow: none;
  }

  /* H1Z1 Logo */

  .img-responsive, .thumbnail>img, .thumbnail a>img, .carousel-inner>.item>img, .carousel-inner>.item>a>img {
	  display: none;
  }

  /* Showcase Text */

  .intro-text {
      display: none;
  }

  /* Buttons */

  .main_home_button {
	  border: 4px solid #383838;
	  border-radius: 0px;
	  background-color: rgba(56, 56, 56, 0.4);
  }

  .main_home_button:hover {
	  background-color: rgba(56, 56, 56, 0.8);
  }

  /* Button Text */

  .header_text_mid {
  	margin-bottom: 17px;
  }

  .header_text_small {
  	display: none;
  }

}

@-moz-document url-prefix(http://h1z1showcase.com/KOTK/) {

	/* Body */

	body {
		background-color: #222;
	}

	a {
		color: #fff !important;
	}


	/* Navbar Background */

	.navbar-default {
		background-color: #222222;
	}

	/* Navbar Text */

	.navbar {
		text-transform: none;
	}

	.navbar .navbar-nav {
		letter-spacing: 0.25px;
	}

	.caret {
		display: none;
	}

	/* Navbar Right Positioning */

	.steam_login_button_navbar {
		padding-left: 5px !important;
		padding-right: 5px !important;
	}

	.tooltip.bottom {
		display: none;
		/*margin-left: 45px; */
	}

	/* Navbar Search */

	.form-control {
		border: 2px solid #222;
		border-radius: 0px;
	}

	/* ────────────────────────────── */


	/* Header Logo */

	header {
		background: url(https://steamuserimages-a.akamaihd.net/ugc/543015853914564131/260CF5414D0A5FB4B0840508AF19BB1B13ABBD36/) no-repeat center center fixed;
	}

	header img {
		visibility: hidden;
	}

    /* Header Text */

	.intro-text {
		visibility: hidden;
	}

	/* ────────────────────────────── */

	/* Dropdown */

	.dropdown-menu {
		background-color: #222;
		border-radius: 0px;
	}

	/* ────────────────────────────── */


	/* Section 'Skins' */


	/* Twitter Feed */


	.timeline-Widget--edge {
		border-radius: 0px;
	}

	.timeline-Widget {
		border-radius: 0px;
		background-color: #2e2e2e;
	}

	.timeline-Header-title {
		color: #fff;
	}

	/* Section Text */

	hr.star-primary {
		visibility: hidden;
	}

	/* Countdown */

	.alert-info {
		box-shadow:  none !important;
	}

	/* Section Skin Items */

	.skin-item {
		background-color: #222;
	}

	.thumbnail {
		border-radius: 0px;
	}

	.fa {
		display: none;
	}

	p {
		color: #fff !important;
	}

	.btn-default {
		background-color: #222;
		border-color: #fff;
		box-shadow: none !important;
		border-radius: 0px;
	}

	.btn-default:hover {
		background-color: rgba(56, 56 ,56, 0.5);
		border-color: #000;
	}

	section h2 {
		color: #fff;
	}

	label {
		visibility: hidden;
	}



	/* Footer */

	footer .footer-below {
		background-color: #222;
	    border-top: 2px solid #fff;
	}

}
