css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Ouma Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base {
    background: url("https://pm1.narvii.com/6338/5c4a3b270057e93a037f8d393273eabaff2da094_hq.jpg") -15px top no-repeat !important;
    background-size: auto 125px !important;
}
#pet-portrait {
    background: url("https://pa1.narvii.com/6320/84fb581a532ca369f34aeb8d1c7c22dbb0b2baad_hq.gif") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Ouma Kokichi" !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Ouma te a mentido una vez mas";
}
#pet-info-racegender:before {
    content: "Humano" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food > img {
    background: url("https://fotos.subefotos.com/3be5a490015a8aae2ee0ac7a662b4b0co.jpg") !important;
    background-size: 60px 60px !important;
    margin-top: -5px !important;
    padding-left: 60px !important;
    padding-top: 60px !important;
    width: 0 !important;
    height: 0 !important;
}
#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: " Panta de uva" !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Ouma en tu perfil*/

#player-display-pet {
    background: url("https://fotos.subefotos.com/3723174d46312bfb2f16a7a435fc666fo.png") no-repeat;
    background-size: auto 100%;
    height: 484px !important;
    width: 194px !important;
}
#player-display-pet > img {
    display: none !important;
}

img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("https://fotos.subefotos.com/a02a220411a7a28143a6f45a55261915o.png");
    padding-left: 490px;
    padding-top: 185px;
    position: absolute;
    top: -0px;
    left: -40px;
}
