css:
@-moz-document domain('kissmanga.com') {

    /*[[navbuttons]]*/


    #divImage p img, #imgCurrent {
      max-width: 100% !important;
      min-width: 100% !important;
      height: auto;
    }
    #headnav + div{
        width: 100% !important;
    }

        #headnav + div div:first-of-type div:first-of-type > *:not(.barContent){
        display: block !important;
        font-size: 4em !important;
        width: auto !important;
        max-width: 100% !important;
        text-align: center !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }
    #headnav + div div:first-of-type > div{
        float: none !important;
        padding: 0 !important;
    }
    #headnav + div .btnPrevious,  #headnav + div .btnNext, #btnPrevious, #btnNext{
        width: 90px !important;
        margin-top: 8px !important;
        margin-left: 15px;
        margin-right: 15px;

    }

    #headnav + div div:first-of-type{
        width: 100% !important;
    }
    #navsubbar > p{
        text-align: center !important;
        width: 100% !important;
        font-size: 4em !important;
        height: auto !important;
    }

    #navsubbar {
        width: 100% !important;
        background: #0481bc !important;
        height: auto !important;
        line-height: 4em !important;
        margin-top: 20px;
        margin-bottom: 20px;
    }
    #navbar{
        height: auto !important;
        background: #161616 !important;
    }
    #navcontainer{
        width: 100% !important;
        display: table-row !important;
    }
    #head > h1{
        margin: 30px 0px 0px 0px !important;
        float: none !important;
    }
    #head > h1 > a{
        width: 100% !important;
        height: 13em !important;
        background: transparent url(http://kissmanga.com/Content/images/logo.png) center/auto 100% no-repeat !important;
    }
    #head > h3{
        margin: 48px 0 0 0 !important;
        float: none !important;
    }
    #head h3 > * {
        text-align: center !important;
        width: 100% !important;
        font-size: 2.2em !important;
    }
    #head h3 > * *{
        font-size: inherit;
    }
    #formSearch > div > a > img{
        width: 2.5em !important;
    }
    #imgSearch{
        width: 6em !important;
        margin-left: 0 !important;
        margin-right: auto !important;
        margin-bottom: 30px !important;
        float: right;
    }
    #selectSearch{
        width: auto !important;
    }
    #keyword{
        width: 55% !important;
    }
    #result_box{
        top: 2em !important;
        left: 10% !important;
        width: 45% !important;
        padding: 0.6em !important;
    }
    #result_box > a{
        line-height: 1.4em !important;
    }
    table.listing > * {
        font-size: 1.1em !important;
        line-height: 3em !important;
    }
    .chapterList table.listing > * {
        font-size: 0.9em !important;
        line-height: 3em !important;
    }
    #leftside .barContent .alphabet{
        margin-top: 20px;
     }
    #leftside .barContent .alphabet > a{
        font-size: 4em;
        padding: 20px;
        display: inline-block;
     }
    .chapterList table.listing tbody tr:nth-child(even){
        background: transparent;
        padding-top: 6px;
        padding-bottom: 6px;
    }
    .chapterList table.listing tbody tr:nth-child(odd){
        background: #222;
        padding-top: 6px;
        padding-bottom: 6px;
    }
    #container{
        display: table !important;
    }
    #leftside{
        float: none !important;
        display: table-footer-group !important;
    }
    #leftside .bigBarContainer .scrollable{
        min-height: 425px !important;
        height: auto !important;
    }
    #leftside .bigBarContainer .barContent, #leftside .banner .details{
        font-size: 2.5em !important;
        height: auto !important;
        line-height: 1.1em !important;
    }
    #leftside .bigBarContainer .scrollable div a{
        font-size: 1em !important;
        height: auto !important;
        line-height: 0.9em !important;
        margin-right:27px !important;
        width: 150px !important;
    }
    #leftside .bigBarContainer .scrollable div a img{
        height: 175px !important;
        width: 150px !important;
    }
    #rightside {
        float: none !important;
        display: table-header-group !important;
    }
    #rightside img:only-child{
        width: 40% !important;
        height: auto !important;
    }
    #rightside .barContent > * {
        font-size: 2.7em !important;
        line-height: 1.4em !important;
    }
    #rightside .barContent > * * {
        font-size: inherit !important;
        line-height: inherit !important;
    }
   #rightside .rightBox{
        width: 100% !important;
    }
    .barTitle {
        height: auto !important;
        font-size: 4em !important;
        padding: 0.4em !important;
        background: linear-gradient(#ccc, #0481bc) !important;
    }
    .barTitle *{
        font-size: inherit !important;
    }
    .barContent .bigChar{
        font-size: 3.2em !important;
    }
    #subcontent div div > * {
        position: relative !important;
        left: initial !important;
        top: initial !important;
        display: block !important;
        text-align: center !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }
    #subcontent div div > * {
        font-size: 3em !important;
        line-height: 1em !important;
    }
    #subcontent div div > * * {
        font-size: inherit !important;
        line-height: inherit !important;
    }
    #subcontent div > div > img {
        width: 50px !important;
        margin-bottom: 20px !important;
    }
    #subcontent div > a > img {
        width: 30% !important;
        height: auto !important;
        margin: 0 0 10px 0 !important;
        margin-top: 15px !important;
    }
    #subcontent div div, #subcontent div div.blue{
        margin: 0px !important;
        padding: 20px !important;
        float: none !important;
        display: block !important;
        width: 100% !important;
    }
    #subcontent div a img, #subcontent div div a img{
        float: none !important;
    }
    #leftside > .banner {
        background: transparent !important;
        height: auto !important;
    }
    #leftside > .banner > .details {
        background: #222 !important;
        height: auto !important;
        width: 100%;
    }
    #recently-nav a.prev, #recently-nav a.next{
            background-size: 100% !important;
            height: 1em !important;
            width: 1em !important;
            background-repeat: no-repeat !important;
    }
    #rightside .rightBox:nth-child(3){
        display: none !important;
    }
    #tabmenucontainer, #footcontainer, #headnav, #head, #container{
        width: 100% !important;
        margin: 0 !important;
    }
    #topHolderBox{
        width: auto !important;
        height: auto !important;
        padding: 0.3em !important;
    }
    #formSearch > p > * {
        margin-top: 20px !important;
    }
    #navcontainer a, #tabmenucontainer a{
        float: left !important;
        border-radius: 4px !important;
        font-size: 4em !important;
        width: auto !important;
        margin: 1px !important;
        padding: 0.5em !important;
        background: -webkit-linear-gradient(#777, #444) !important;
        background: -o-linear-gradient(#777, #444) !important;
        background: -moz-linear-gradient(#777, #444) !important;
        background: linear-gradient(#777, #444) !important;
    }
    #navcontainer li a#currentTab, #tabmenucontainer a.tabactive{
        background: -webkit-linear-gradient(#0481bc, #eee) !important;
        background: -o-linear-gradient(#0481bc, #eee) !important;
        background: -moz-linear-gradient(#0481bc, #eee) !important;
        background: linear-gradient(#0481bc, #eee) !important;
    }
    #tabmenucontainer li a:hover, #tabmenucontainer li a:active, #navcontainer li a:hover, #navcontainer li a:active{
        background: linear-gradient(#00a4f1, #fff) !important;
    }
    #headnav + div > div > a[href='#'] > img, #divComments ~ div > a[href='#'] > img{
        height: 1.6em;
    }
    .barContent.episodeList, #footer, #divBookmark, #headnav + div > div > a[href='#'], #divComments ~ div > a[href='#']{
        font-size: 2em !important;
        line-height: 1.3em !important;
    }
    #btnShowComments{
        width: auto !important;
        height: auto !important;
        padding: 0.5em !important;
        font-size: 2.1em !important;
    }
    div.pagination ul.pager li a, div.pagination ul.pager li.current {
        padding: 0.4em !important;
        font-size: 4em !important;
    }
    #headnav + div div:first-of-type > div.barTitle{
        padding: 0.4em !important;
    }


    #rightside .barContent > div > a + br {
        display: none !important;
    }
    #rightside .barContent > div > a:after {
        content: ' | ';
        color: #888;
        margin: 10px;
    }
    #rightside .barContent > div > a:last-child:after {
        content: '' !important;
    }
    #rightside .barContent > div > a {
        display: inline-block !important;
        padding: 7px !important;
        margin: 1px !important;
    }

    .arrow-general{
        display: none !important;
        opacity: 0 !important;
    }

    #genres {
        width: 100% !important;
      	padding-left: 20px !important;
      	padding-right: 20px !important;
    }

    #genres > li {
        padding: 15px !important;
    }
    #genres li a {
        background-size: 1em !important;
        padding-left: 1.05em !important;
    }
    #genres li label {
        height: auto !important;
        width: auto !important;
    }
  #container #leftside form label.lblSignUp {
    	float: none !important;
 	    width: 100% !important;
    	text-align: center !important;
 	}
    #formRegister #btnSubmit, #formLogin #btnSubmit, #formAdvanceSearch #btnSubmit, #formAdvanceSearch #btnReset{
        width: 5em !important;
        height: auto !important;
    }
    body #container #leftside form > div > div.g-recaptcha{
        width: 100% !important;
        margin-bottom: 40px !important;
    }
    body #container #leftside form > div > div.g-recaptcha iframe{
        margin: 0 !important;
    }
    body #container #leftside #g-recaptcha-response{
        display: none !important;
    }

    body #headnav + div div:first-of-type div:first-of-type > .barContent form {
        font-size: 1.2em !important;
        line-height: 1em !important;
    }
    body #headnav + div div:first-of-type div:first-of-type > .barContent form * {
        font-size: inherit !important;
        line-height: inherit !important;
    }
	body #headnav + div div:first-of-type div:first-of-type > .barContent form input {
  	  height: auto !important;
  	  width: 100% !important;
  	  float: none !important;
   	  margin: 0 !important;
	}
	body #headnav + div div:first-of-type div:first-of-type > .barContent form label.lblAdvanceSearch {
        width: 100px !important;
        margin-right: 20px !important;
    }
    body #headnav + div div:first-of-type div:first-of-type > .barContent form > div {
        margin-top: 40px !important;
        margin-bottom: 40px !important;
    }
    body #headnav + div div:first-of-type div:first-of-type > .barContent form > div:last-child, body #headnav + div div:first-of-type div:first-of-type > .barContent form > div {
        text-align: center !important;
        padding-left: 0 !important;
    	display: block !important;
     }
  #formRegister #email + div{
  		padding: 0 !important;
  }
    #container .listing tr.odd {
        background: #222 !important;
    }
    table.listing > * tr > td > img, #container .alphabet ~ div:not([class]) > img[title] {
        width: 60px !important;
    } 
    input#chbDoujinshi {
            width: 1em !important;
        height: 1em !important;
        font-size: 1em !important;
        margin-top: 40px;
        margin-left: 80px;
    }
    #adsIfrme5 ~ div:not([class]) {
        float: none !important;
        width: 100% !important;
    }
    #adsIfrme5 ~ div:not([class]) > div {
        float: none !important;
        margin: 20px 0px 20px 0px !important;
        text-align: center !important;
        width: 100% !important;
    }
    #adsIfrme5 ~ div:not([class]) > div:first-child > *{  
        display: block !important;
        font-size: 4em !important;
        width: auto !important;
        max-width: 100% !important;
        text-align: center !important;
        margin-left: auto !important;
        margin-right: auto !important;
    }


}

style_setting_options.value:
#navcontainer li[id]{
        display: none;
        opacity: 0;
}
#navcontainer li#liHome, #navcontainer li#liDS{
        display: inline-block !important;
        opacity: 1 !important;
}
