css:
@-moz-document domain(forum.tour-magazin.de) {
/* ------------------------------------------------------------
       @(#) userContent.css
       @(#) "Fifteen Shades of Grey" by seep
       @(#) Version 2016-05-17 14:13
       ------------------------------------------------------------ */
    
    html {
	background-color: #aaa !important;
    }
    
    .mainwrapper {
	background-color: #ddd !important;
    }
    
    /* Header */

    .above_body {
	background-color: #aaa !important;
    }
    
    .toplinks a:hover {
        background-color: #333 !important;
    }
    
    .toplinks .popupbody a {
	background-color: #ddd !important;
	color: #333 !important;
    }

    .toplinks .popupbody a:hover {
	background-color: #333 !important;
	color: #ddd !important;
    }

    img#logo {
	/* Original Size: 737 x 306*/
	width: 184px !important;
	height: 77px !important;
    }   

    div.navbar {
	background-color: #000 !important;
    }
    
    .card-search {
        width: 50% !important;
        left: 50% !important;
        /* height: 24px !important; */
        padding: 2px !important;
    }
    
    .input.search {
        font-size: 12px !important;
        font-style: normal !important;
        color: #000 !important;
        background-color: #eee !important;
    }
    
    /* Body */

    body,
    .mainwrapper,
    .clearfix {
	width: 95% !important;
	border: 1px #000;
    }	
    
    div.body_wrapper {
	background-color: #ddd !important;
    }
    
    /* Verzeichnisse */
    
    .threadbit .title {
	font-weight: normal !important;
	color: #999 !important;
    }

    .threadbit .sticky {
	background-color: #ccc !important;
    }

    .threadbit .threadtitle_unread {
	font-weight: normal !important;
	color: #000 !important;
    }

    .searchlisthead {
	background-color: #333 !important;
	border-color: #000 !important;
    }

    .toolsmenu {
	background-color: #bbb !important;
    }

    .newcontent_textcontrol {
	background-color: #333  !important;
    }

    .blockhead {
	background-color: #333 !important;
	border-color: #000 !important;
    }

    div.wysiwyg_block .formcontrols,
    div.wysiwyg_block .formcontrols .blockrow,
    div.wysiwyg_block .blockfoot
    {
	background-color: #eee !important;
	border-color: #000 !important;
    }
    
    #forums a.username {
        color: #333 !important;
    }

    .forumbit_post .forumrow, .forumbit_nopost .forumhead, .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead {
        box-shadow: -0px -0px 0px #ccc !important;
    }

    /* Posts */

    div.userinfo_noavatar {
	background-color: #ccc !important;
    }

    div.postbody {
	background-color: #eee !important;
    }
    
    .postbitignored.postbitim {
	height: 0px !important;
	margin-bottom: 0px !important;
	border: 0px !important;
    }
    
    .postbitdeleted.postbitim {
	height: 50px !important;
	margin-bottom: 0px !important;
	border: 0px !important;
    }

    div.bbcode_quote {
	border-color: #333 !important;
    }

    div.below_body {
	background-color: #aaa !important;
    }
    

    
    .pagination .popupctrl,
    .pagination a,
    .pagination .first_last a,
    .pagination .prev_next a
    {
	color: #000 !important;
	background-color: #ddd !important;
	border-color: #999 !important;
    }
    
    
    /* Symbol-Legende */

    #forum_icon_legend {
	background-color: #eee !important;
    } 

    #forum_icon_legend h5 {
	color: #fff !important;
	border-color: #999 !important;
	background-color: #000 !important;
    } 

    /* Benutzerkontrollzentrum Menu */
    #usercp_nav .blockrow .active {
	background-color: #aaa !important;
    } 
    
    
    /* Footer */

    .similar_threads {
	background-color: #ddd !important;
    }

    .thread_info_block.blockbody.formcontrols {
	background-color: #ddd !important;
    }

    .footer_time,
    .footer_copyright,
    .footer_morecopyright {
	color: #666 !important;
    }

    /* Erweiterte Suche */

    #searchtypeswitcher {
	border-bottom: 4px solid #666 !important;
    }

    #searchtypeswitcher li.selected a {
	background-color: #666 !important;
    }

    #searchtypeswitcher li a {
	background-color: #999 !important;
    }

    /* Mein Profil */
    
    #sidebar_container a {
        color: #333 !important;
    }
    
    .activitystream_block dd.selected, dd.userprof_module {
        background-color: #666 !important;
        border-color: #666 !important;
    }
    
    .memberprofiletabunder {
        background-color: #666 !important;;
    }
    
    li.activitybit a {
        color: #333 !important;
    }
}
