css:
@-moz-document domain("web.groupme.com"), domain("app.groupme.com") {
.chat-messages, .message, .chat-header, .chat-sidebar, .chat-sidebar *, .chat-input, .mousetrap, .dropdown-menu, .dropdown-menu a,
.form-horizontal, .form-actions, .device-notifications-toggle, .paste {
    background-color: #222 !important;
    color: #bbb !important;
}
.inline-downloader, input {
    background-color: #333 !important;
    color: #bbb !important;
}
.timestamp, .system .body {
    background-color: #222 !important;
    color: #f2f2f2 !important;
}
body {
    background-color: #000 !important;
    color: #F2F2F2 !important;
}
.navbar-inner, .dropdown-menu a:hover, button {
    background-color: #025 !important;
    color: #bbb !important;
}
.burger-box:hover, button:hover {
    background-color: #047 !important;
    color: #F2F2F2 !important;
}
.paste {
    border-left: rgba(255, 255, 255, 0.2) solid !important;
}
.chat-messages .timestamp-divider {
    background-color: #222 !important;
}
.chat-messages .timestamp-divider {
    background-color: #000 !important;
    color: #FFF
}
.message-composer {
    background-color: #000 !important;
    color: #FFF
}
.pillar {
    background-color: #000 !important;
    color: #FFF
}
h2.chat-name {
    color: #FFF
}
#tray .tray-header {
    background-color: #000 !important;
}
#tray .tray-header .row{
    background-color: #000 !important;
}
div.list-item{
    background-color: #000 !important;
    color: #FFF
}
.groupme-list .list-heading{
    color: #EEE
}
#tray .tray-list{
    background-color: #000;
}

/* dannation edits */
html {
    -webkit-tap-highlight-color: rgb(0, 10, 189);
}
#tray .tray-header h3 {
    color: #EEE
}
#tray .list-search {
    background-color: black;
}
#tray .list-search input {
    width: 345px;
}
.app-sidebar.open {
    width: 428px;
}
.app-sidebar.open ~ #page {
    padding-left: 428px;
}
#tray {
    width: 380px;
}
.navbar-inner, .dropdown-menu a:hover, button {
    background-color: rgba(24, 56, 192, 0.4) !important
}
#chats .chat-header {
    padding: 0px 0px 0px 0;
}
.logo {
    background-color: #000 !important;
}
.tab {
    background-color: #000 !important;
}
.accessible-focus {
    background-color: #000 !important;
}
.emoji-menu .emoji-preview {
    background-color: rgba(0, 0, 0, 0.5);
}
.emoji-menu .pack-container {
    background-color: rgba(0, 0, 0, 0.5);
    border-top: 0px solid #dddddd;
    border-bottom: 0px solid #dddddd;
}
.emoji-menu .pack-switcher {
    background-color: rgba(0, 0, 0, 0.5);
}
.emoji-menu .emoji-menu-pointer {
    background-color: rgba(0, 0, 0, 0.5);
}
.emoji-menu .pack-switcher .slider-control {
    background-color: rgba(0, 0, 0, 0.5);
}
.navbar-inner, .dropdown-menu a:hover, button {
    background-color: rgba(0, 0, 0, 0.5) !important;
    color: #fff !important
}
.edit-field form {
    background-color: rgb(0, 175, 240);
}
}
