css:
@-moz-document url-prefix("https://godvillegame.com/superhero") {

        .gr__godvillegame_com body {
            font-family: Helvetica;
            font-size: 85%;
        }
        .gr__godvillegame_com .p_bar {
            border-radius: 0 !important;
            border-color: #ccc !important;
        }
        .gr__godvillegame_com .p_val {
            border-radius: 0 !important;
        }
        .gr__godvillegame_com div.block {
            border-radius: 0;
            border: none;
            padding: 0;
            box-shadow: 0 0 3px rgba(0, 0, 0, 0.2);
        }
        .gr__godvillegame_com .block_title {
            background: rgb(100, 100, 100);
            width: 100% !important;
            height: 30px;
            padding-top: 6px;
            box-sizing: border-box;
            color: white !important;
            font-family: Helvetica;
        }
        .gr__godvillegame_com .c_col .block_title {
            margin-left: 0;
        }
        .gr__godvillegame_com .s_col .block_title {
            margin-left: 0 !important;
        }
        .gr__godvillegame_com span#imp_button {
            top: 5px;
            color: white;
        }
        .gr__godvillegame_com span#d_refresh {
            color: white;
            top: 5px;
        }
        .gr__godvillegame_com .fr_new_badge {
            background: orange;
            border: 1px solid;
        }
        .gr__godvillegame_com input#god_phrase {
            width: 100% !important;
            border-radius: 0;
            font-size: 9pt;
            color: #199BDC;
        }
        .gr__godvillegame_com input#voice_submit {
            border: none;
            padding: 5px;
            background: #199BDC;
            color: white;
            box-shadow: 0 0 2px rgba(0, 0, 0, 0.5);
        }
        .gr__godvillegame_com input#voice_submit:hover {
            background: orange;
            cursor: pointer;
        }
        .gr__godvillegame_com .block_content {
            background: rgb(255, 255, 255);
            /* Old browsers */

            background: -moz-linear-gradient(-45deg, rgba(255, 255, 255, 1) 0%, rgba(247, 247, 247, 1) 35%, rgba(255, 255, 255, 1) 100%);
            /* FF3.6+ */

            background: -webkit-gradient(linear, left top, right bottom, color-stop(0%, rgba(255, 255, 255, 1)), color-stop(35%, rgba(247, 247, 247, 1)), color-stop(100%, rgba(255, 255, 255, 1)));
            /* Chrome,Safari4+ */

            background: -webkit-linear-gradient(-45deg, rgba(255, 255, 255, 1) 0%, rgba(247, 247, 247, 1) 35%, rgba(255, 255, 255, 1) 100%);
            /* Chrome10+,Safari5.1+ */

            background: -o-linear-gradient(-45deg, rgba(255, 255, 255, 1) 0%, rgba(247, 247, 247, 1) 35%, rgba(255, 255, 255, 1) 100%);
            /* Opera 11.10+ */

            background: -ms-linear-gradient(-45deg, rgba(255, 255, 255, 1) 0%, rgba(247, 247, 247, 1) 35%, rgba(255, 255, 255, 1) 100%);
            /* IE10+ */

            background: linear-gradient(135deg, rgba(255, 255, 255, 1) 0%, rgba(247, 247, 247, 1) 35%, rgba(255, 255, 255, 1) 100%);
            /* W3C */

        }
        .gr__godvillegame_com div#map {
            width: 500px;
            height: 500px;
        }
        .gr__godvillegame_com #map .block_content {
            height: 455px;
        }
}
