css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://letsrun.com/forum), url-prefix(http://www.letsrun.com/forum) {


BODY {

  font-family: Verdana, Arial, Helvetica, sans-serif ! important;
  font-size: 10pt;
  color : black;
  background: white;

  }



A:link{

  color: #36393D ! important;
  font-weight: bold ! important;

  }



A:visited{

  color: #36393D ! important;

  }



A:hover  {

  color : #FF7400 ! important;

  }



/* main font of posts */ 

.BaseFont {

  font-family: Verdana, Arial, Helvetica, sans-serif ! important;

  font-size: 10pt ! important;

  color: #36393D ! important;

  }

/* font of date of posts, a few other things*/ 

.SmallFont {

  font-family: Verdana, Arial, Helvetica, sans-serif ! important;
  font-size: 8pt ! important;
  color: #36393D ! important;

  }

/* color of reply bar when posts are quoted*/ 

hr {

  color : #C3D9FF;

  }



.ForumFlag {

  color: #4096EE ! important;

  font-weight: bold;

  }





 /* color of tool with toggle, new posts, page, etc at top of index page. font color willbe overriden by links*/

.BoardTool {
 font-family: Verdana, Arial, Helvetica, sans-serif ! important;
 background-color: #FFFFFF ! important;
 padding: 2px ! important;
 font-size: 8pt ! important;

  }

.BoardTool td{
 width: 300px ! important;
}




  /* color of left hand side block in table posts also color of Subject/author block on main page*/

.ForumHeader {

  background : #C3D9FF ! important;

}



.ForumTable2 {

 background-color: #FFFFFF ! important;
 padding: 2px ! important;

}


.ForumTable1 {

 background-color: #EEEEEE ! important;
 padding: 2px ! important;

}

Font { 
 font-size: 8pt ! important;
 color: #36393D ! important;
 font-family: Verdana, Arial, Helvetica, sans-serif ! important;
 }


}
