css:
/* i really want this to be global */
@namespace url(http://www.w3.org/1999/xhtml);
body{
    background-image: url("https://images.duckduckgo.com/iu/?u=http%3A%2F%2Fcdn.wonderfulengineering.com%2Fwp-content%2Fuploads%2F2014%2F07%2FHi-Tech-Wallpaper-16.jpg&f=1");
    
    color: #eee;
    font-family: 'Open Sans', sans-serif;
  }
div.summ {
    max-width: 300px;
    margin: 15px;
    padding: 10px;
    border: 1px dotted #00a1d1;
    border-radius: 1em;
}


  
  a[href*="/"] span{
    font-weight: bold;
  }
  
  a[href*="/j"] span, a[href*="/p"] span{
    font-weight: normal;
  }
  
  a, a span{
    color: #9A42FF;
  }
  
  a:visited, a:visited span{
    color: #5AFF6A;
  }
  
  :link{
    color: #5f98c0;
  }
  
  .summ{
    border: none;
  }
  
  li#selected{
    background: #404040;
  }
  
  li#selected a{
    color: lightgray;
  }
  
  div.tabin{
    background: #242424;
  }
  
  table.out td{
    background-color: #303030;
  }
  
  table.out td.ok{
    background-color: green;
  }
  
  table.out td.no{
    background-color: red;
  }
  
  /*** IMAGES ***/
  
  /*16x16 icons*/
  img[src*="s1.jpg"], img[src*="s2.jpg"], 
  img[src*="c1.jpg"], img[src*="c2.jpg"]{
    width: 0;
    height: 0;
    padding: 8px;
  }
  
  img[src*="s1.jpg"] {
    background: url("http://i.imgur.com/3FTdPwM.png") no-repeat 0 0;
  }
  img[src*="s2.jpg"] {
    background: url("http://i.imgur.com/wdnJnpk.png") no-repeat 0 0;
  }
  
  
  /*Big Checkmarks*/
  img[src*="/c2big.jpg"]{
    width: 0;
    height: 0;
    padding: 32px;
  }
  
  img[src*="c2big.jpg"] {
    background: url("http://i.imgur.com/3FTdPwM.png") no-repeat 0 0;
  }
  
  
  /*Ace*/
  .ace_content{
    background: #333;
    width: 32px;
  }
  
  .ace_focus{
    color: #FFF !important;
  }
  
  /*Colors*/
  .ace_language, .ace_constant, .ace_identifier, .ace_line{
    color: #FFF;
  }
  
  .ace_chrome, .ace_support, .ace_function{
    color: #A5B4C6 !important;
  } 
  
  .ace_string{
    color: beige !important;
  }
  
  .ace_keyword{
    color: #D580FE !important;
  }
  
  .ace_comment{
    color: #ccc !important;
  }
  
  .ace_numeric{
    color: #CCFF00 !important;
  }
  
  .ace_boolean{
    color: #00B2FF !important;
  }
  
  button.go{
    background: #666;
    color: white;
  }
