@-moz-document url("chrome-extension://fjnbnpbmkenffdnngjfgmeleoegfcffe/popup.html") {
body{background: #000;width: 300px;height: 500px;padding-bottom: 50px;}
*{outline: 0px}
#top{padding: 0px;}
#header{
    padding: 12px 12px 0px 12px;
    border-bottom:none;
    position: relative;
    z-index:2;
}
.tabs-container header, #header,.tab-header,.tabs-container header .tab-header{
    background: #fff;
    color: #333;
    font-size: 13px;
}
.tabs-container header{
    z-index: 1;
	position: relative;
	box-shadow: 0px 2px 5px rgba(0,0,0,.2);
    border: none;
}
#title{display: none;}
#menu_button{margin:0px 0px 0px 12px}
#sitename{
    position: absolute;
    top: 6px;
    font-size: 13px;
    max-width: 160px;
    overflow: hidden;
    text-overflow: ellipsis;
}
/** menu **/
#menu_inner a{
    width: 100%;
    display: block;
    height: 36px;
    line-height: 36px;
    text-align: left;
    margin: 0px;
    padding: 0px 12px;
    box-sizing: border-box;
}
#menu_inner a:hover{background: #eee;color:#000;}
#menu, #menu_inner{
    top: 0px;
    right: 0px;
    opacity: 1;
    background: #fff;
    border-radius: 2px;
    padding: 0px;
    width: 100%;
    height: auto;
    box-sizing: content-box;
}
#menu{width: 140px;}

#menuImg{filter: invert();}
#menu_button:hover{background: #eee;}
/** on/off **/
#stylish_on, #stylish_off,.thumbnail_activate:after, .thumbnail_deactivate:after{
	font-size:0px;
    position: absolute;
    top: -5px;
    width: 22px;
    left: 0px;
    height: 22px;
    border-radius: 22px;
    background: #FFF;
    box-shadow: 0 2px 5px rgba(0,0,0,.3);
    transition: all .2s;
    content: " ";
}

#stylish_on, .thumbnail_deactivate:after{
    left: auto;
    right: 0px;
    background: #4D7FB2
}

.swipe-button, .thumbnail_activate, .thumbnail_deactivate{
    width: 35px;
    height: 12px;
    border-radius: 13px;
    border: none;
    font-size: 0px;
    background: #CDD1DA;
    padding: 0px;
	overflow: visible;
    box-shadow: none;
    position: relative;
}

/** tabs **/
.tabs-container header .tab-header{
	border: 0px;
}

.tabs-container header .tab-header{
	padding: 6px;
    width: 50%;
    box-sizing: border-box;
    height: auto;
}

/** list **/
.entry {
    background: #ffffff;
    box-shadow: none;
    border-radius: 0px;
    margin: 0px 0px 1px 0px !important;
    position: relative;
    padding: 6px;
}
#tab-item-installed, #tab-item-recommended, #installed{
	margin: 0px;
    width: 100%;
    height: 500px;
    box-sizing: border-box;
    padding: 0px;
}

#tab-item-installed, #tab-item-recommended{
	padding-bottom: 28px;
}

.installed .thumbnail{
	display: none;
}

.style-name{
	text-decoration: none;
    width: 100%;
    max-width: 100%;
    overflow: hidden;
    text-overflow: ellipsis;
}

.thumbnail_activate, .thumbnail_deactivate{
	position: absolute;
    top: 12px;
    right: 12px;
}
.styleButtons{
	left: 0px;
    padding: 12px;
    position: absolute;
}
.bottomIcons{
	padding: 0px;
    margin: 0px;
    margin-top: 6px;
    height: 32px;
}

.style-author{
	position: absolute;
    top: 0px;
    right: 0px;
    width: auto;
    max-width: 30%;
    padding: 12px;
}
/** footer **/
#find-styles-others{
	display: none;
}
#find-styles {
    font-size: 13px;
    margin-bottom: -5px;
    border-radius: 0px;
    background: #fff;
    color: #333;
    border: 0px;
    outline: 0px;
    box-shadow: 0px -2px 5px rgba(0,0,0,.2);
    position: absolute;
    width: 100%;
    bottom: 0px;
    height: 50px;
    line-height: 50px;
    padding: 0px;
    z-index: 2;
}


/** scroll **/
.maintab::-webkit-scrollbar, .maintab::-webkit-scrollbar-button {
    width: 5px;
    height: 5px;
}
.maintab::-webkit-scrollbar-thumb {
    background: #ddd;
    border: 5px;
    border-radius: 0px;
}
.maintab::-webkit-scrollbar-thumb:hover, .maintab::-webkit-scrollbar-thumb:active {
    background: #ccc;
}
.maintab::-webkit-scrollbar-track,.maintab::-webkit-scrollbar-track:hover,.maintab::-webkit-scrollbar-track:active {
    background: #eee;
    border: 5px;
    border-radius: 0px;
}
}
