css:
@-moz-document domain("hooktube.com") {

  :root {
    --bg-color: #111111;
    --highlight: #a4a4a4;
    --navbar-bg-color: #232323;
    --link-color: #D8D8D8;
    --link-hover-color: #D8D8D8;
    --channel-title-link-color: rgba(255, 255, 255, 0.74);
    --channel-title-link-color-hover: rgba(255, 255, 255, 1);
    --primary-color: rgba(255, 255, 255, 0.88);
  }

  body {
    background-color: #111111 !important;
  }

  #video-list > .row > * > a {
    font-weight: bold;
    font-family: Roboto, Arial, sans-serif;
  }

  .navbar {
    background-color:var(--navbar-bg-color);
    border-bottom: 1px solid #222;
  }

  .navbar-toggler-icon {
    background-image: url("data:image/svg+xml;charset=utf8,%3Csvg viewBox='0 0 30 30' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath stroke='rgba(255, 255, 255, 0.9)' stroke-width='3' stroke-linecap='round' stroke-miterlimit='10' d='M4 9h22M4 15h22M4 21h22'/%3E%3C/svg%3E") !important;
  }

  .jumbotron {
    background-color:var(--bg-color)!important;
    color:var(--highlight);
  }

  .container-fluid main {
    background-color:var(--bg-color);
  }

  .container-fluid {
    background-color:var(--bg-color);
  }

  .text-left {
    color:var(--highlight);
  }

  .text-right {
    color:var(--highlight);
  }
  .text-center {
    color:var(--highlight);
  }

  .video-title{
    color:var(--highlight);
  }

  .card {
    background-color: transparent !important;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
  }

  .card-title {
    color:var(--highlight);
  }

  .card-subtitle {
    color:var(--highlight);
  }

  .card-body {
    background-color:var(--bg-color);
    color:var(--highlight);

  }

  /* tag list */
  .badge {
    background-color: var(--link-color);
    color: var(--bg-color);
  }

  /* search box */
  #search-input {
    border: 1px solid #333 !important;
  }

  #search-input:focus {
    border-color: #80bdff;
  }

  #search-button {
    border: 1px solid #333;
    cursor: pointer;
  }

  .list-channel-title {
    color: var(--channel-title-link-color);
  }

  .list-channel-title:hover {
    color: var(--channel-title-link-color-hover);
  }

  .btn {
    color:var(--highlight);
    /* border: 2px solid var(--highlight); */
    cursor: pointer;
    border: none;
  }

  .btn:hover {
    background-color: #333;
  }

  .dropdown-menu.show {
    background-color:var(--bg-color);
  }

  .dropdown-item:focus {
    color:var(--highlight);
    text-decoration: none;
    background-color:var(--bg-color);
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out;
    transition: transform .2s ease-out;
  }

  .dropdown-item:hover {
    color:var(--highlight);
    text-decoration: none;
    background-color:var(--bg-color);
    -webkit-transform:scale(.9);
    -moz-transform:scale(.9);
    -o-transform:scale(.9);
    transform:scale(.9);
  }

  .show > a {
    outline: 0;
    color: #a4a4a4;
  }

  p {
    color:var(--highlight);
    background-color:var(--bg-color);
  }

  .modal-body {
    background-color:var(--bg-color);
  }

  *, *::after, *::before {

    box-sizing: inherit;
    color:var(--highlight);
  }

  .navbar-brand img {
    width:100px;
    height:50px;
    box-sizing:border-box;
    padding-left: 160px;
    background: url('data:image/png;base64,') left top no-repeat;
  }

  [type="reset"], [type="submit"], button, html [type="button"] {
    -webkit-appearance: button;
    color:var(--highlight);
    background-color:var(--bg-color);
  }

  .col-sm-9 {
    color:var(--highlight);
  }

  .text-primary {
    color:var(--highlight) !important;
  }

  a:hover {
    color: var(--link-hover-color);
    text-decoration: none;
  }

  a {
    color: var(--link-color);
  }

  .navbar-light .navbar-nav .active > .nav-link,
  .navbar-light .navbar-nav .nav-link.active,
  .navbar-light .navbar-nav .nav-link.show,
  .navbar-light .navbar-nav .show > .nav-link {
    color:var(--highlight);
  }

  .form-inline .form-control {
    background-color:var(--bg-color);
    border: 2px solid var(--highlight);
  }

  .modal-header {
    border-bottom: 1px solid #a4a4a4;
    background-color: #222;
  }

  .text-center {
    background-color: #222;
  }

  .modal-title {
    color: #222 !important;
  }

  .close {
    color: #a4a4a4;
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out;
    transition: transform .2s ease-out;
  }

  .close:active {
    color: #a4a4a4;
    text-decoration: none;
    background-color: #222;
    -webkit-transform:scale(.9);
    -moz-transform:scale(.9);
    -o-transform:scale(.9);
    transform:scale(.9);
  }

  .my-sm-0 {
    color: #a4a4a4;
    -webkit-transition:  -webkit-transform .2s ease-out;
    -moz-transition: -moz-transform .2s ease-out;
    -o-transition: -o-transform .2s ease-out;
    -ms-transition: -ms-transform .2s ease-out;
    transition: transform .2s ease-out;
  }

  .my-sm-0:active {
    -webkit-transform:scale(.9);
    -moz-transform:scale(.9);
    -o-transform:scale(.9);
    transform:scale(.9);
  }

  .modal-title {
    color: #a4a4a4;
  }

  .modal-title {
    margin-bottom: 0;
    line-height: 1.5;
    color: #a4a4a4 !important;
  }

  .modal-title {
    color: #a4a4a4 !important;
  }

  .alert-warning {
    color: #222;
    background-color: #a4a4a4;
    border-color: #fff;
  }

  .navbar-light .navbar-toggler {
    background-color: var(--navbar-bg-color);
    border: none;
    cursor: pointer;
  }

  #main-footer {
    border-top: 1px solid #333;
  }

  .row, .col {
    background-color: var(--bg-color) !important;
  }

  /* see front page */
  mark {
    color: white;
    background-color: var(--bg-color);
  }
}
