css:
@-moz-document domain("coub.com") {
.body-container {
    background-color: #1c252c;
    color: white;
}
.header.-scrolled>.header__bg {
    background: #ffffff;
    background-color: #4c606f;
}
button.sb.-st.-sq.box--inline.embed.mobile-hidden {
    color: white;
    background-color: #355771;
}
button.sb.-st.-sq {
    color: white;
    background-color: #355771;
}
html:not(.mobile) .sb.-st:hover {
    background-color: #000000;
    color: #ffffff;
    box-shadow: 0px 0px 0px 1px #000000;
}
.header.-transparent .header__create-btn .sb.create.cutter-white {
    display: inline-block;
    color: #bbbbbb;
    background-color: #1c252c;
}.header.-transparent .header__search .input-field {
    background: #1c252c;
}
.nav__item__inner.inline--container {
    color: #fffdfd;
}
.description__controls {
    position: absolute;
    top: 21px;
    right: 6px;
    color: #fffdfd;
}
span {
    color: #e7e6e7;
}
.header__bg {
    background-color: #354958;
}.box-card__meta {
    background-color: #4e697d;
    color: white;
}
a {
    color: #cccccc;
}
a.box--inline.coub__tags-tag {
    background-color: #2f3d48;
    color: #b1b1b1;
        border: 1px solid #3a3a3a;
}
.cb-edtr__upload-type.cb-edtr__upload-type--external.-ribbon.-half {
    background-color: #4f616f;
    color: #d2d2d2;
}
.cb-edtr__upload-type.cb-edtr__upload-type--local.-ribbon.-quarter {
    background-color: #4f616f;
}
.cb-edtr__upload-type.cb-edtr__upload-type--webcam.-ribbon.-quarter {
    background-color: #4f616f;
}
.cb-edtr__upload.-centered-text.-clear {
    background-color: #4f616f;
}
.dropdown__handler {
    background-color: #1c252c;
}
}
