additional_info:
works with this Greasemonkey script

// ==UserScript==
// @name        gmail mobile script
// @namespace   bhspiers
// @include     https://mail.google.com/mail/mu/*
// @version     1.1
// @grant       none
// ==/UserScript==


window.setTimeout(function(){

  document.querySelector('#tltbt .lc').innerHTML+='<div id="bhs_tabs" />';

    var bhs_ = document.querySelector('#bhs_tabs');
    bhs_.innerHTML += '<a href="#tl/priority/^smartlabel_personal" class="personal" />';
    bhs_.innerHTML += '<a href="#tl/priority/^smartlabel_social" class="social" />';
    bhs_.innerHTML += '<a href="#tl/priority/^smartlabel_promo" class="promo" />';
    bhs_.innerHTML += '<a href="#tl/priority/^smartlabel_notification" class="notification" />';

    window.setInterval(function(){
        if( document.querySelector('.fh').scrollTop<100 && document.querySelector('.qm').offsetParent!==null && (document.querySelector('.Mn')===null || document.querySelector('.Mn').offsetParent===null) ){
            // message pane at top
            // messages visible (not searching)
            // mask not visible (popup not open)
            // then check for new messages
          document.querySelector('.V.j.qg').click();
        }
        var lhs = document.querySelector('.kh');
        if(lhs.offsetWidth>725)
            lhs.classList.add('wide');
        else
            lhs.classList.remove('wide');
    },5000);
},1000);

css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://mail.google.com/mail/mu/") {
  #nav { display:none; }
  #views { top: 0 !important; }
  .ni { float: left; }

  .Kh { min-height: 0; padding: 1px 6px; }
  .xm { background-color: rgba(220,220,220,0.4); }
  
  .tr { display: none; }  /* ads */
  
  #tltbt .zc { text-align: left; margin-left: 30px }
  .Sl { font-size: 15px; font-weight:200; height: auto; margin-top: 0px; }
  .Sl b { font-weight:400; }
  .Vl::before { content:'('; }
  .Vl::after { content:')'; }
  .kk { position: absolute; right: 0; }
  .vm { float:left; height:auto; }
  .am { display: none; }
  .ym, .zm { -webkit-background-size: 22px 22px; width:26px; height:26px; }
  .Wg { margin-top: 1px; background-color: #aaa; color:#f3f3f3; font-weight: 300; }
  .Ul { line-height: 17px; padding-left: 2px; color: #999; font-weight:200; }
  .Ul::before { content: '- '; }
  .Tl { margin-left: 16px; font-weight:400; }
  .kk, .xm .Tl { color: #060606; }
  .Yl {
      float: none;
      position: absolute;
      top: 0px;
      left: 0;
      padding: 2px;
  }

  /*  stop the refresh button flashing  */
  .M.j.T.b.hc.Wm.Tm { width:26px!important; }
  .M.j.T.b.hc.Wm.Tm canvas { display: none!important; }
  .M.j.T.b.hc.Wm.Tm .V.j.qg { display: inline-block!important; }
  
  
  .go {
    z-index: 2;
    bottom: 0 !important;
  }
  .go .do { background: #ddd; }

  
  #bhs_tabs {
    float:left;
    margin-right:10px;
  }
  
  #bhs_tabs a {
    float:left;
    background-size: auto auto;
    -webkit-background-size: 23px 23px;
    width: 23px;
    height: 23px;    
  }
  #bhs_tabs a.personal {
    background-image: url(data:image/png;base64,);
  }
  #bhs_tabs a.social { 
    background-image: url(data:image/png;base64,); 
  }
  #bhs_tabs a.promo { 
    background-image: url(data:image/png;base64,);
  }
  #bhs_tabs a.notification { 
    background-image: url(data:image/png;base64,);
  }
  
  
  
  .wide .Kh { display: flex; }
  .wide .Tl { line-height: 28px; }
  .wide .Yl { padding: 4px 2px 0 2px; }  
  .wide .Sl { line-height: 28px; width: 180px; }
  .wide .wm { padding-right: 77px; overflow: hidden; }
  .wide .Ul { margin-left: 0px; line-height: 28px; }
  .wide .Wl { margin-top: 3px; margin-left: 10px; }
  .wide .kk { z-index: 5; }
  .wide .vm { max-width: 50%; }
}
