css:
@-moz-document domain("uned.es")
{
    /* Se quita fuente ASAP (se ve mal) y se cambian los colores */

.contenido_texto table.tabla-guias {background-color:#eee; font-family:Verdana, sans-serif;}
.contenido_texto table.tabla-guias td i, .contenido_texto table.tabla-guias td a i { color:#3C6DBF;}
.contenido_texto h4 {font-family:Verdana, sans-serif;}

.parrafodestacado{background:#f7f7f8 none repeat scroll 0 0;border-bottom:1px dashed #621681;border-top:1px dashed #621681;font-family:Verdana, sans-serif;margin:0 0 2em 0;padding:0.7em 0em 1em 0em;text-align:left;color:#666;}
.parrafodestacado strong{color:#28497F;}
li.list-group-item a:hover{color:#337ab7;}
div.tabla_localizacion table{font-family:Verdana, sans-serif;}
div.tabla_localizacion table td{color:#28497F;}
div.tabla_localizacion table td a{color:#3C6DBF;}
.tabla_localizacion table th{background:#4883E5 none repeat scroll 0 0;font-family:Verdana, sans-serif;}
.list-group-item.active.menulateral, .list-group-item.active.menulateral:focus{background-color:#4883E5;}
.list-group-item.active.menulateral:hover{background-color:#4883E5;}
.listamenu4, ul.enlaces_extra{font-family:Verdana, sans-serif;}
#oferta_estudios h3{background-color:#3C6DBF;}
h1.contenido_titulo{color:#bb4b00;font-family:Verdana, sans-serif;}
h6.contenido_titulo2, p.contenido_titulo2{color:#666;font-family:Verdana, sans-serif;}
h3.contenido_subtitulo{color:#3C6DBF;font-family:Verdana, sans-serif;}
p.contenido_subtitulo, div.contenido_texto h4.contenido_subtitulo{font-family:Verdana, sans-serif;}
a.dpto{color:#bb4b00;font-family:Verdana, sans-serif;}
.contenido_texto ul.list-group li.list-group-item a{font-family:Verdana, sans-serif;}
.tabla1.master table tr:first-child th{background-color:#4883E5;color:#FFF;}
div.tabla1 table, div.tabla_grados table{font-family:Verdana, sans-serif;}
div.tabla1 table td, div.tabla_grados table td{color:#28497F;}
div.tabla_grados table td a{color:#3C6DBF;}
div.tabla_grados table td a:hover{color:#3C6DBF;}
.tabla1 table th, .tabla_grados table th{background:#4883E5 none repeat scroll 0 0;font-family:Verdana, sans-serif;}
.tabla1 table th{background:#3C6DBF none repeat scroll 0 0;}
.contenido_texto .tabla1 th.hidden-xs{background-color:#4883E5;}
.list-group-item.active.tabla_asignaturas, .list-group-item.active.tabla_asignaturas:focus{font-family:Verdana, sans-serif;background-color:#3C6DBF;}
div.tabla_grados .list-group-item.active, div.tabla_grados .list-group-item.active:focus{font-family:Verdana, sans-serif;}
.list-group-item.active.tabla_asignaturas:hover{background-color:#3C6DBF;color:white;}
div.tabla_grados .list-group-item.active, div.tabla_grados .list-group-item.active:focus{background:#4883E5 none repeat scroll 0 0;}
div.tabla_grados .list-group-item.active:hover{background-color:#3C6DBF;color:white;}
td:nth-child(2) a{color:#3C6DBF;}
td:nth-child(2) a:hover{color:#4883E5;}
p.caption_tabla{font-family:Verdana, sans-serif;color:#777;}
.info_matricula a{color:#000 !important;font-family:Verdana, sans-serif;background-color:#d9d9d9;}
a.btn_matricula{color:#FFF;font-family:Verdana, sans-serif;}
.tit_guia_movil ul li a.btn_grado{color:#FFF;font-family:Verdana, sans-serif;}
.enl_oferta_estudios a{font-family:Verdana, sans-serif;}  

.listamenu4{background-color:#4883E5;}
.listamenu4 li a{background-color:#4883E5;}
.listamenu4 li a:hover{color:#072b20;}
.listamenu4 h4{color:#142440;}
.submenu{color:#fff;background-color:#4883E5;}
ul.listamenu4_nivel2{background-color:#4883E5;}
ul.listamenu4_nivel2 li a:hover{color:#072b20;}
.tit_guia_movil ul li form a{font-family:Verdana, sans-serif;}
.tit_guia_movil ul li a.btn{color:#FFF;font-family:Verdana, sans-serif;}
.tit_guia_movil ul li a.btn_volver_grado{font-family:Verdana, sans-serif;background-color:#bb4b00;}
.tit_guia_movil a.matricula{color:#fff;background-color:#F00;font-family:Verdana, sans-serif;}
.tabla1 table tr.semestre td, .tabla1 table tr.semestre td.show_movil{background-color:#4883E5;}
.tabla1 th:nth-child(2), .tabla1 th:nth-child(3), .tabla1 th:nth-child(4){background-color:#4883E5;}
ul.enlaces_extra li a.btn{font-family:Verdana, sans-serif;}
.enl_oferta_estudios a{font-family:Verdana, sans-serif;background-color:#3C6DBF;}

@media (min-width:767px){
.listamenu4_nivel2{font-family:Verdana, sans-serif;}
ul.enlaces_extra li a.btn{color:#FFF;font-family:Verdana, sans-serif;}

}

@media (min-width:992px){
ul.enlaces_extra li a.btn{font-family:Verdana, sans-serif;}
}

.list-group-item.active.menu_estudios, .list-group-item.active.menu_estudios:focus{background-color:#4883E5;font-family:Verdana, sans-serif;}
.list-group-item.active.menu_estudios.verde, .list-group-item.active.menu_estudios.verde:focus{background-color:#3C6DBF;}
.list-group-item.active.menu_estudios:hover{background-color:#4883E5;color:white;}
.list-group-item.active.menu_estudios2, .list-group-item.active.menu_estudios2:focus{background-color:transparent;color:#3C6DBF;border-top:1px solid #3C6DBF;font-family:Verdana, sans-serif;}
.list-group-item.active.menu_estudios2:hover{color:#3C6DBF;}
.rojo{color:#c1272d !important;}
.pag_estudios h1{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_estudios .bloque h2{color:#28497F;font-family:Verdana, sans-serif;}
.pag_estudios .bloque li{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_estudios .bloque li a{color:#3C6DBF;}
.pag_estudios .announcement-item ul li a, .pag_estudios .announcement-item-subtit a{color:#28497F;}
.pag_matricula .list-group-item.active, pag_matricula .list-group-item.active:focus{background-color:#4883E5;font-family:Verdana, sans-serif;}
.pag_matricula h1{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_matricula h2{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_matricula h3{color:#333;font-family:Verdana, sans-serif;}
.pag_matricula p{color:#666;font-family:Verdana, sans-serif;}
.pag_matricula h6.contenido_subtitulo{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_matricula ul.enlaces_extra{font-family:Verdana, sans-serif;}
.pag_matricula .bloque a.btn_matricula{font-family:Verdana, sans-serif;}
.pag_matricula .bloque p{font-family:Verdana, sans-serif;}
.pag_matricula .bloque ul{font-family:Verdana, sans-serif;}
.pag_g_combinados h1{color:#3C6DBF;font-family:Verdana, sans-serif;}
.pag_g_combinados h2{color:#bb4b00;font-family:Verdana, sans-serif;}
.pag_g_combinados a.link_underline{font-family:Verdana, sans-serif;}
.pag_g_combinados a.link_underline:hover{color:#337ab7;}
div.destacado{background-color:#598479;color:#FFF;font-family:Verdana, sans-serif;}
.pag_g_combinados .announcement-item ul li a, .pag_g_combinados .announcement-item-subtit a{color:#28497F;}

p.nota.naranja{color:#bb4b00;font-family:Verdana, sans-serif;font-weight:700;}
.pag_g_combinados h2 .list-group-item.active, .pag_g_combinados h2 .list-group-item.active:focus{font-family:Verdana, sans-serif;background-color:#3C6DBF;}
.pag_g_combinados .width_100{font-family:Verdana, sans-serif;}
.pag_g_combinados .cabecera_tabla{background-color:#4883E5;}
.pag_g_combinados div.cabecera_tabla + ul a{color:#3C6DBF;}

@media (max-width:767px){
.pag_estudios .bloque li a:hover{color:#3C6DBF;}
.list-group-item.active.info, .list-group-item.active.info{font-family:Verdana, sans-serif;background-color:#598479;}
.pag_g_combinados .enlaces_extra a.btn {font-family: Verdana,sans-serif;}
}
@media all and (min-width:767px) and (max-width:991px){
.pag_g_combinados .enlaces_extra a.btn {font-family: Verdana,sans-serif;}
}
@media (min-width:992px){
.pag_g_combinados .enlaces_extra a.btn {font-family: Verdana,sans-serif;}
}
}
