css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.pushsquare.com") {

div#page {
background-color: #010a14;
background: #010a14;
}

.block footer .more {
background-color: #0952AE;
}

dt, .block footer .more a, .text p, .thumbnails-f .syndicatedItem .video-title {
color: rgb(233, 233, 233);
}

div#body, div#content, .insert-sticky, .body-text .gallery, .body-text .picture {
background-color: #041d3f;
background: #041d3f;
}

.block.posts .item:nth-child(even), .container .style-simple .item-content, section.item.row2, .block.number-list .item:nth-child(even), .comments div.comments .comment:hover {
background-color: #031732;
background: #031732;
}

.container .item .title, .block.posts .item .title, .block.number-list .item .title, article.article section.title .title a, article.article section.byline .article-author a, .thumbnails-f .trc_rbox_header, .thumbnails-f .logoDiv a span, .related-articles h2.heading, .related-articles .item a:visited {
color: #F4F4F4;
}

.container .item .description, .thumbnails-f .syndicatedItem .branding, .author ul a {
color: #a1a1a1;
}

.container .item .title:visited span, .container .item .title:visited span.title, .container .item-group {
color: #bbb;
}

.container .style-list .item-medium .text {
color: rgb(233, 233, 233);
}

.container .style-list .item-low, .container .style-list .item-medium, .container .style-list .item-insert, .container .style-reviews .item-content, .author ul, .related-articles .item, article.article aside.see-also {
border-top: 1px solid #010a14;
}

.container .item .list li {
border-left: 1px solid #010a14;
}

.container .item .category, .container .style-list .item-high.item-1 .item-wrap, .trc_bigger .thumbnails-f .videoCube, .thumbnails-f .videoCube.syndicatedItem {
border-right: 1px solid #010a14 !important;
}

.block.posts .item, article.article div.body, article.article section.title, .thumbnails-f .trc_rbox_header, .article-recommendations, .comments .comment {
border-bottom: 1px solid #010a14;
}

.block.posts .item {
border: none;
}

.body-text h2 {
border-bottom: 2px solid #0952AE;
color: #F4F4F4;
}

.block.number-list .item:hover {
background-color: #010a14;
}

.block footer .more a:hover {
background: #F3F3F3;
color: #787878;
}

.container .item-group:hover {
background-color: #031732;
}

.container .item .title:visited span, .container .item .title:visited span.title, .container .item-group {
color: rgb(233, 233, 233);
}

.block footer .more {
border-top: 1px solid #041d3f;
}

.container .item .description {
color: #a1a1a1
}

.text a, .forums .text a, .accent, .forums .unread a, .forums .unread .item-title .icon, .forums .unread .forum-title .icon {
color: #0952AE;
}

.ccmsForm .button {
background-color: #0952AE;
}

.accent-bg, .ccmsForm .fm-fieldset legend, .ccmsForm .button:hover, .accent-bg-hover:hover {
background-color: #2E6CBA;
}

.text a, .forums .text a, .accent, .forums .unread a, .forums .unread .item-title .icon, .forums .unread .forum-title .icon, a.accent{
color: #fff;
}

textarea {
background-color: #031732;
color: #f4f4f4;
}

.ccmsForm textarea, .ccmsForm .textbox, .ccmsForm select {
border-color: #010a14;
}

}
