@-moz-document domain("avenoel.org") {
/* ==UserStyle==

@name Shadow AveNoël
@version 0.3
@description Theme sombre pour Avenoël + changement de l'interface
@author FoutreAuMiel / Chef
@homepageURL https://2sucres.org/profile/chef

==UserStyle== */

body {
    --mainColor: #23272a;
    --secondaryColor: #2c2f33;
    --spanColor: #acacac;
    min-width: 450px;
    overflow: auto;
    font-family: "roboto", sans-serif;
    font-size: 14px;
    color: #acacac;
    background-color: var(--mainColor) !important;
}
blockquote{
color : #afafba !important;
}
main.container .container-content section {
    background-color: var(--secondaryColor)!important;
}
/*changement haut de page et couleur */
.main-container .container-content section{
background : none;
}
.tex2jax_ignore {
    padding-top: 90px;
}
.primary-page-title {
    display: none
}
div.row.container-content {
    padding-top: 0px;
}
div.col-xs-12.col-sm-12.col-md-9 section {
    padding: 0px;
}
hr {
    display: none
}
.breadcrumb {
    padding: 5px 10px;
    margin-bottom: 10px;
    list-style: none;
    background-color: var(--secondaryColor);
    border-bottom: 1px solid #646667;
    border-radius: 0px;
}
.breadcrumb li {
    background-color: var(--secondaryColor);
}
.col-xs-12,
.pagination,
.col-md-2,
.form-inline,
.row,
.aside ul li,
.aside ul {
    background-color: var(--secondaryColor);
}
div.row.container-content {
    padding-left: 5px;
    background-color: var(--secondaryColor);
}
.col-md-10 {
    background-color: var(--secondaryColor);
    padding-right: 0;
    max-width: 400px;
}
div.col-md-10.col-md-offset-1 {
    background-color: var(--secondaryColor);
    padding-right: 0px;
    min-width: 83%;
    margin-left: 0px;
}
div.col-md-10.col-md-offset-1 .form-control.post-content {
    max-width: 100%;
    min-width: 100%;
    min-height: 33px;
}
/*------------------*/
/*button et formulaire*/

.btn, .btn-primary {
    
    border: 1px solid #646667;
    background-color: var(--mainColor);
    border-radius: 2px;
}
div.form-group.bbcodes .btn {

    color: #acacac;
    border:none !important;
    background:none !important;
}
div.form-group.bbcodes .btn:hover {

    color: #f1f1f1;
}

.forum-tools .btn {
    background: var(--mainColor);
    border: 1px solid #646667;
    color: #acacac;
    text-transform: uppercase;
    font-size: 0.9em;
}
.btn:hover,
.btn:focus,
.btn.focus,
.grey-btn.btn:hover{
    border: 1px solid rgb(130,70,70);
    color: #F5F5F5;
    background-color : rgb(100,40,40) !important;
    text-decoration: none;
}
div.form-group.preview {
    border: 1px dashed #646667;
    color: #acacac;
    background-color: var(--mainColor);
    padding: 1em;
    display: none;
    margin-left: 0px;
    width: 100% !important;
}
/*form.form-inline .form-group select.form-control {
    display: block;
    width: 90px;
    position: absolute;
    top: 0px;
    left: 170px;
}
form.form-inline .form-group input.form-control {
    width: 150px;
    position: absolute;
    top: 0px;
    left: 10px;
}
form.form-inline .form-group button.btn {
    position: absolute;
    top: 0px;
    left: 270px;
}*/

div.col-md-2.pull-right .pull-right a {
    background-color: var(--mainColor);
}
div.col-md-10.col-md-offset-1 form button.btn.btn-primary {
    border: 1px solid #646667;
    color: #F5F5F5;
    text-decoration: none;
}
div.col-md-10.col-md-offset-1 form h3 {
    background-color: var(--secondaryColor);
}
div.col-md-10.col-md-offset-1 form div.form-group.preview {
    background-color: var(--mainColor);
}
div.col-md-10.col-md-offset-1 form {
    background-color: var(--secondaryColor);
}
div.col-md-10.col-md-offset-1 form div.form-group {
    background-color: var(--secondaryColor);
}
div.col-md-10.col-md-offset-1 form div.form-group label {
    display: none
}
div.col-md-10.col-md-offset-1 form div.form-group.bbcodes {
    text-align: left;
    margin: 0;
    border: 1px solid #646667;
    background-color: var(--mainColor);
}
.form-control {
    display: block;
    width: 100%;
    height: 36px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.6;
    color: #acacac;
    background-color: var(--mainColor);
    background-image: none;
    border: 1px solid #646667;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.9);
    transition: border-color ease-in-out 0.5s, box-shadow ease-in-out 0.5s;
}
/*-------------------------------------*/
/*---------Spoiler----------------*/

.spoiler {
    margin-top: 5px;
}
.spoiler .spoiler-btn {
    visibility: hidden;
    margin-left: -50px;
}
.spoiler .spoiler-btn:after {
    visibility: visible;
    content: "[SPOILER]";
    color: #acacac;
    padding: 5px;
    font-weight: bold;
}
.spoiler .spoiler-content {
    border: 1px dashed var(--mainColor) !important;
    color: #acacac;
    background-color: rgba(35, 30, 30, 0.2) !important;
    padding: 1em;
    opacity: 0.7;
}
/*--------------------------------------*/

.pagination > li > a,
.pagination > li > span {
    position: relative;
    float: left;
    padding: 6px 12px;
    line-height: 1.6;
    text-decoration: none;
    color: #acacac;
    background-color: var(--secondaryColor);
    border: 1px solid #646667;
    margin-left: -1px;
}
/* Barre de navigation */

.navbar-inverse .navbar-nav > li > a {
    color: var(--spanColor);
}
.navbar-inverse {
    background-color: var(--mainColor);
}
.pagination > li > a:focus,
.pagination > li > span:hover,
.pagination > li > span:focus {
    z-index: 2;
    color: var(--spanColor);
    background-color: var(--mainColor);
    border-color: #646667;
}
.pagination > li > a:hover {
    background-color: var(--mainColor);
    color: #f1f1f1;
    border-color: #646667;
}
/* smiley */

.fancybox-skin {
    position: relative;
    background: var(--mainColor);
    color: #acacac;
    text-shadow: none;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
}
.onesignal-bell-container {
    display: none
}
/*bye la cloche chiante :hap:*/

.footer {
    border-top: 4px solid #B71C1C;
    position: absolute;
    bottom: 0;
    width: 100%;
    padding: 10px;
    font-size: 0.8em;
    background: var(--mainColor);
    text-align: center;
}
.bottombar ul {
    background: none;
    opacity: 0.9;
}
nav.visible-xs.visible-sm.bottombar {
    position: fixed;
    top: 10px;
    right: 0;
    margin-right: 70px;
    width: 200px;
    padding: 0;
    background: none;
    z-index: 1031;
}
/*-------------CHAT-----------------------*/

.chat-box .chat-message .chat-message-username a {
    --mainColor: #23272a;
    --secondaryColor: #2c2f33;
    --spanColor: #acacac;
    color: #acacac;
}
.chat-message-date {
    font-size: 12px;
}
.chat-box .chat-message {
    color: var(--spanColor);
    overflow: hidden;
}
.chat-form {
    background: var(--secondaryColor);
    padding: 20px;
}
.chat-box {
    height: 500px;
    overflow: auto;
    padding: 5px;
    ;
    list-style: none;
    font-size: 14px;
    word-wrap: break-word;
    border: 1px solid white;
}
/*-------------CHAT-----------------------*/

.chat-box .chat-message .chat-message-username a {
    --mainColor: #23272a;
    --secondaryColor: #2c2f33;
    --spanColor: #acacac;
    color: #acacac;
}
.chat-message-date {
    font-size: 12px;
}
.chat-box .chat-message {
    color: var(--spanColor);
    overflow: hidden;
}
.chat-form {
    background: var(--secondaryColor);
    padding: 20px;
}
.chat-box {
    height: 500px;
    overflow: auto;
    padding: 5px;
    ;
    list-style: none;
    font-size: 14px;
    word-wrap: break-word;
    border: 1px solid white;
}
/*--------Profile------------------*/

.profile .profile-title,
.profile .profile-wrapper,
.profile .profile-footer,
.profile .profile-biography {
    background: var(--secondaryColor);
    padding: 15px;
    border-radius: 5px;
}
.profile .profile-title {
    color: #acacac;
    text-align: center;
    margin: 0 0 15px 0;
}
/*-------------TOPIC---------------*/
ul.pagination.pagination-topic li{
display:inline-block;
margin : 0 auto;
}
ul.pagination.pagination-topic li.subactive{
background-color:rgb(100, 30, 30);
    border:none;
}
ul.pagination.pagination-topic li:hover{

background-color:rgb(100, 0, 0) !important;
}
.topic-moderation,
.topic-title {
    background-color: var(--secondaryColor);
}
.topic-message .message-header {
    border-bottom: 1px solid #545454;
    line-height: 25px;
    font-size: 14px;
    height: 35px;
}
.topic-message .message-header .message-date span {
    font-size: 10px;
}
.topic-message .message-header .message-username a {
    color: #acacac;
}
.topic-message .message-header .message-actions {
    margin: 0;
    display: inline-block;
    padding: 0;
    background: none;
}
.topic-message .message-header .message-date {
    font-size: 12px;
    background: none;
}
.topic-message {
    color: #b9b9b9;
    margin-bottom: 10px;
    padding: 0;
    overflow: hidden;
    display: flex;
}
.message-wrapper div.message-content {
    min-height: 100px;
}
.message-footer {
    margin-top: 10px;
}
article.row.topic-message {
    border: 1px solid #606060;
    background: rgba(100, 100, 100, 0.1);
}
article.row.topic-message.odd {
    border: 1px solid #606060;
    background: rgba(100, 100, 100, 0.20);
}
.odd .message-content-quote,
.message-content-quote {
    color: rgb(80, 80, 85);
    background: rgb(70, 70, 75);
    border-left: 3px solid rgb(100, 100, 100)
}
.odd .message-content-quote a,
.message-content-quote a {
    color: #afafaf;
}
.message-content-quote i {
    color: #999;
    font-size:14px;
    background: none;
}
/*.message-avatar img[src="/images/avatars/noavatar.png"] {
display : none
}
je fait pas de crabistouilles ( ͡° ͜ʖ ͡°) promis
*/
}

@-moz-document url-prefix("https://avenoel.org/forum"), url-prefix("https://avenoel.org/messagerie") {
/*FORUM et MP*/

div.col-md-10.col-md-offset-1 {
    background-color: var(--secondaryColor);
    padding-right: 10px;
    min-width: 83%;
    margin-left: 70px;
}
.primary-page-title {
    display: none
}
div.col-xs-12.col-sm-12.col-md-9 section ul:nth-of-type(1) {
    display: none
}
/*Liste des topics*/

.topic-icon img {
    width: 15px;
    background-color: transparent;
}
.topics-author a {
    color: #acacac;
}
a {
    color: #acacac;
    text-decoration: none;
}
a:hover {
    color: #f1f1f1;
    text-decoration: none;
}
.topics-title a:visited {
    color: rgb(100, 110, 110);
}
.topics-title a:visited:hover {
    color: rgb(150, 160, 160);
}
.pagination.pagination-topic a {
    color: #acacac;
}
.topics-author {
    background: var(--mainColor);
    text-align: center;
    width: 120px;
}
.topics {
    margin-bottom: 10px;
    border: 1px solid #646667;
}
.table > thead > tr > th,
.table > thead > tr > td,
.table > tbody > tr > th,
.table > tbody > tr > td,
.table > tfoot > tr > th,
.table > tfoot > tr > td {
    font-size: 15px;
    background: var(--mainColor);
    padding: 5px;
    line-height: 1.5;
    vertical-align: bottom;
    border-top: 1px solid #ddd;
}
.table > thead > tr > th {
    color: #a0a0a0;
    font-size: 13px;
    padding: 3px;
    vertical-align: bottom;
    border-bottom: 2px solid #646667;
}
tr.active,
tr.active td {
    background: rgba(100, 30, 30, 0.5) !important;
}
}

