css:
@-moz-document domain("literatureandlatte.com") {

html, h1, h2, h3, h4, div, p {
    font-family: "iA Writer Duospace" !important;
    font-weight: 400 !important;
    line-height: 1.8em !important;
}
.posts, .lastpost, .list-inner, .forumtitle, .icon .topics, .icon .lastpost, strong {
    font-family: "iA Writer Duospace" !important;
    color: #D5D5D5;
}
.forumtitle, .topictitle, .list-inner a.forumtitle, .list-inner a.topictitle {
    font-size: 1.4em !important;
    color: #2d1004;
}
.list-inner, .list-inner a, .icon .topics, .icon .lastpost {
    font-size: 0.9em !important;
    color: #D5D5D5;
}
.posts strong, .topics .lastpost span a, strong {
    color: #2d1004;
}
strong{
    font-weight: bold;
}
h1, h2, h3, h4 {
    line-height: 1.2em !important;
}
h2 {
    font-size: 2em !important;
    line-height: 1.2em !important;
}
.core {
    max-width: 1350px;
}
div.content {
    text-indent: 1.5em;
    line-height: 2.2em !important;
}
dd.lastpost span {
    font-size: 0.9em !important;
}
dd.lastpost {
    width: 290px;
}
dt.icon.forum_unread_subforum, dt.icon.forum_unread, dt.icon.topic_unread {
    background-size: 27px 27px !important;
    background-color: #FFF2F2 !important;
}
span.imageset.icon_post_target_unread {
    padding: 8px 8px 8px 8px;
    background-color: #F7693B !important;
    background-image: none;
}
.btn--red {
    background: #DAF7A6 !important;
    color: #2d1004 !important;
}
span.posthilit {
    background-color: #E1FFFD !important;
}
div.content span.posthilit {
    text-indent: 0em !important;
}
.form-row input[type="text"], .form-row input[type="date"], .form-row input[type="email"], .form-row input[type="search"], .form-row input[type="password"], .form-row input[type="tel"], .form-row input[type="url"], .form-row input[type="number"], .form-row textarea, .form-row select {
    font-family: "iA Writer Duospace" !important;
}
  }
