#primary #dismissible>#details ytd-video-meta-block>#metadata>#byline-container>#channel-name>div>div>#text{}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#byline-container>#channel-name>div>div>#text>a{font:18px/100% arial!important;color:#0af!important;}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#metadata-line               {max-height:initial!important;line-height:initial!important;overflow:visible!important;-webkit-line-clamp:initial!important;}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#metadata-line>:nth-child(1) {}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#metadata-line>#separator+*  {font:18px/100% arial!important;color:#1a1!important;}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#metadata-line>#separator+*+*{font:18px/100% arial!important;color:#aa1!important;}
#primary #dismissible>#details ytd-video-meta-block>#metadata>#metadata-line>:nth-child(4) {}
#primary #dismissible>#details ytd-video-meta-block>#additional-metadata-line{}
#primary #dismissible>#details .video-badge{}
#primary #dismissible>#details .title-badge{}
#primary #dismissible>#details #buttons{}
#primary #dismissible>#details>#menu{}
#primary #dismissible>#details>#menu>ytd-menu-renderer{}
#primary #dismissible>#details>#menu>ytd-menu-renderer:hover{background:#234!important;border-radius:50%!important;}
#primary #dismissible>#details>#menu>ytd-menu-renderer>#button{}
#primary #dismissible>#details>#menu>ytd-menu-renderer>#button>button{}
#primary #dismissible>#details>#menu>ytd-menu-renderer>#button>button>yt-icon{}
#primary #dismissible>#details>a                        {padding:0!important;margin:0!important;overflow:visible!important;}
#primary #dismissible>#details>a>h3                     {padding:4px 0 2px 6px!important;border-radius:0 0 20px 10px!important;}
#primary #dismissible>#details>a>h3>#video-title        {font:18px/100% arial!important;color:#c60!important;}
#primary #dismissible>#details>a:visited>h3.ytd-rich-grid-slim-media             {background:#020!important;}
#primary #dismissible>#details>a:visited>h3.ytd-rich-grid-slim-media>#video-title{color:#a5f!important;}
#primary #dismissed{}
#primary #interaction{}
  
  
  
  
  
  
  
  
  
  
  
  app-header#header #channel-container{height:initial!important;}
  #page-manager ytd-browse{background:#040!important;}
  
  /*header*/
  app-header-layout app-header#header{margin-top:30px!important;}
  
  /*banner*/#spacer{display:none!important;}
  /*channel info*/#channel-header.ytd-c4-tabbed-header-renderer{background:#000!important;padding-top:0px!important;}
  /*channel info*/#channel-title.ytd-c4-tabbed-header-renderer{color:#a11!important;}
  
  /*channel menu bar*/
  app-toolbar,
  app-toolbar>#tabs-container{height:initial!important;}
  app-toolbar>#tabs-container paper-tabs{height:30px!important;}
  app-toolbar>#tabs-container>#tabs-inner-container{background-color:#000!important;}
  
  /*channel menu tab*/
  app-toolbar #tabs{padding:0px!important;text-transform:initial!important;}
  app-toolbar #tabs>#tabsContainer paper-tab{height:initial!important;font-size:24px!important;line-height:100%!important;padding:4px 20px 2px!important;}
  /*channel menu search*/
  app-toolbar #tabs #tabsContent>ytd-expandable-tab-renderer>#form paper-input-container{padding:0px!important;}
  app-toolbar #tabs #tabsContent>ytd-expandable-tab-renderer>#form paper-input-container>.underline{display:none!important;}
  app-toolbar #tabs #tabsContent>ytd-expandable-tab-renderer>#form paper-input-container label,
  app-toolbar #tabs #tabsContent>ytd-expandable-tab-renderer>#form paper-input-container input{padding:0px!important;font-size:24px!important;}
  /*channel menu hover*/    
  app-toolbar #tabs #tabsContent>paper-tab:hover{background:#063!important;}
  app-toolbar #tabs #tabsContent>ytd-expandable-tab-renderer>button:hover{background:#063!important;border-radius:50%!important;}
  /*selected*/ app-toolbar #tabs #tabsContent>paper-tab.iron-selected{background:#050!important;}
  
  
  /*channel homePage title*/#title-container.ytd-shelf-renderer{height:initial!important;}
  /*channel homePage title*/#contents #title.ytd-shelf-renderer{color:#a11!important;font-size:30px!important;line-height:100%!important;max-height:initial!important;}
  /*channel homePage title*/#contents #title.ytd-shelf-renderer:hover{background:#000!important;}
  /*channel homePage item*/#contents.ytd-shelf-renderer{margin-top:0px!important;}
  /*channel homePage item*/#items.yt-horizontal-list-renderer{margin-bottom:0px!important;}

  
  
  #sf-popupMenu{background:#222!important;}
  #sf-popupMenu a:hover{background:#050!important;}
}
@-moz-document regexp("http(s)?://www\.youtube\.com/hashtag/.*"){
/*hashtag tag tag tagtagtag*/
ytd-two-column-browse-results-renderer{padding:0px 0 0!important;}
ytd-browse{}
ytd-browse>#header{}
ytd-browse>#header>*{}
ytd-browse>#header>*>#hashtag{font:30px/100% arial!important;color:#a11!important;}
ytd-browse>#header>*>#hashtag-info{}
ytd-browse>#header>*>#hashtag-info>#hashtag-info-text{font:24px/100% arial!important;}
#contents                        {margin:0 40px!important;}
#contents>ytd-rich-grid-row      {margin:0 0 40px 0!important;}
#contents>ytd-rich-grid-row:hover{transform:scale(1)!important;}
}
@-moz-document regexp("http(s)?://www\.youtube\.com/playlist.*"){
ytd-item-section-renderer>#contents{margin:0!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer{background:#020!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items{width:130%!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*                   {width:calc(25% - 20px)!important;margin:0 20px 30px 0!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>h3>a              {background:#000!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*:nth-child(2n)>h3>a{background:#000!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*:hover>h3>a        {background:#424!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-playlist-thumbnail  {width:100%!important;height:calc(118px * 1.625)!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-playlist-thumbnail>a{}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>h3  {font:20px/100% arial!important;margin:0!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>h3>a{font:20px/100% arial!important;color:#a11!important;padding:3px 0 2px 6px!important;border-radius:0 0 30px 15px!important;-webkit-line-clamp:initial!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-video-meta-block{}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-video-meta-block>#metadata{}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-video-meta-block>#metadata>#metadata-line{justify-content:center!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-video-meta-block>#metadata>#metadata-line>span{font:16px/100% arial!important;color:#aa1!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>ytd-badge-supported-renderer{}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>#menu{}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>#view-more        {margin:0!important;text-align:center!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>#view-more>a      {font:16px/100% arial!important;color:#c60!important;padding:4px 6px 3px!important;margin:4px 0 0!important;border-radius:30px!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>#view-more>a:hover{background:#424!important;}
ytd-item-section-renderer>#contents>ytd-grid-renderer>#items>*>#interaction{}
@media screen and (max-width:1100px){
    /*playlist resize*/
    ytd-browse{}
    ytd-browse>ytd-playlist-sidebar-renderer{display:none!important;}
    ytd-browse>ytd-two-column-browse-results-renderer{padding:0px!important;}
    ytd-browse>ytd-two-column-browse-results-renderer ytd-playlist-video-list-renderer{margin:0px!important;}
}
}




@-moz-document regexp("http(s)?://apis\.google\.com/u/0/_/hovercard/.*"){
  *{border:none!important;}
  /*comment icon hover*/.qpd~*{background:#000!important;}
}
@-moz-document regexp("http(s)?://support\.google\.com/youtube/answer/.*"){
  a{background-color:#000!important;}
  .hcfe{color:#ccc!important;font-size:22px!important;}
  .page{background:#030!important;}
}

