css:
@-moz-document url-prefix("http://talks.by/") {


.row-content-tut {
    font: 14px Verdana;
}

i.b-icon.icon-handDown, i.b-icon.icon-handUp {
    display: none;
}

.link_rate_down, .link_rate_up, .pagination a, .pagingPart a {
    display: inline-block;
    font-weight: 400;
    text-align: center;
    white-space: nowrap;
    vertical-align: middle;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    border: 1px solid transparent;
    font-size: 1rem;
    line-height: 1.5;
    border-radius: .25rem;
    transition: background-color .15s ease-in-out,border-color .15s ease-in-out,box-shadow .15s ease-in-out;
}

.pagingPart a, .pagination a {
    color: #888 !important;
    padding: .375rem .75rem !important;
    background-color: rgba(241, 241, 241, .6) !important; 
}

.pagingPart a:hover, .pagingPart a:active, .pagination a:hover, .pagination a:active {
    background-color: rgba(241, 241, 241, .9) !important;
    border-color: rgba(141, 141, 141, .3) !important;
    box-shadow: 0 0 0 0.2rem rgba(241, 241, 241, 1);
}



.link_rate_down .count, .link_rate_up .count {
    color: #fff;
    padding: .375rem .75rem;
}

/* "не нравится" */

.link_rate_down {
    background-color: #dc3545;
    border-color: #dc3545;
}

.link_rate_down:hover, .link_rate_down:active {
    background-color: #bd2130;
    border-color: #b21f2d;
    box-shadow: 0 0 0 0.2rem rgba(220,53,69,.5);
}

/* "нравится" */

.link_rate_up {
    background-color: #28a745;
    border-color: #28a745;
}

.link_rate_up:hover, .link_rate_up:active {
    background-color: #1e7e34;
    border-color: #1c7430;
    box-shadow: 0 0 0 0.2rem rgba(40,167,69,.5);
}


table.main {
    max-width: 900px;
    box-shadow: 0 3px 1px -2px rgba(0,0,0,.2), 0 2px 2px 0 rgba(0,0,0,.14), 0 1px 5px 0 rgba(0,0,0,.12);
    margin: 0px auto;
  background-color: white;
}


body {
  background-color: #d3d3d359;
}

}
