css:
@-moz-document url-prefix("http://www.dysfz.vip"),
url-prefix("https://www.dysfz.vip") {
  html {
    background: #f2f2f2 !important;
  }

  /* header */
  .top {
    background-color: #212121;
    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12);
  }

  .main {
    border-radius: 0;
    background-color: transparent;
  }
  .shadow {
    box-shadow: none;
  }

  .nav li a {
    height: 40px;
  }

  .nav li a:hover {
    color: #00e871;
    border-bottom: 2px solid #00e871;
    background-color: transparent;
  }

  .is-animate.style1>span {
    animation-name: none;
  }

  .top-search.fr {
    display: flex;
    display: -webkit-flex;
    justify-content: center;
    align-items: center;
  }

  .top-search input {
    border: none;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -moz-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -webkit-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -o-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    background-position: 100% !important;
    padding: 0 5px;
    height: 30px;
    background-color: rgba(255, 255, 255, 0.24);
  }

  .top-search input:focus {
    outline: 0;
    background-color: white;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  }

  .top-search button {
    background-color: #ddd;
    color: #212121;
    border: none;
  }

  .acc a:hover {
    text-decoration: none;
    color: #00e871 !important;
    border-bottom: 2px solid #00e871;
  }

  /* list page */
  .movie-list li {
    position: relative;
    border: none;
    background: #fff;
    padding: 10px 20px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    margin-top: 10px;
  }

  .movie-list li,
  .bar dl,
  .fl .main.shadow,
  .pageturn #tiao-btn,
  .pageturn a,
  .pageturn b {
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.16), 0 0 0 1px rgba(0,0,0,0.04);
    border-radius: 2px;
  }

  .movie-list li:hover, .bar dl:hover {
    box-shadow: 0 0 4px rgba(0,0,0,.14), 0 4px 8px rgba(0,0,0,.28);
  }

  .movie-list .des .txt {
    width: 450px;
  }

  .movie-list .des .pic {
    margin-left: 0;
  }
  .bar.fr .shadow:nth-child(1) {
    display: none;
  }

  .search input {
    border: none;
    border-radius: 3px;
    transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -moz-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -webkit-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    -o-transition: all 450ms cubic-bezier(0.23, 1, 0.32, 1) 0ms;
    background-position: 100% !important;
    z-index: -1;
    height: 39px;
    padding: 4px 0;
    margin: 10px
  }

  .search {
    margin: 20px auto;
  }
  .search input:focus {
    outline: 0;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
  }
  .search button {
    height: 43px;
    -webkit-border-radius: 2px;
    -webkit-user-select: none;
    border-radius: 2px;
    color: #757575;
    font-size: 16px;
    font-weight: bold;
    min-width: 54px;
    padding: 0 16px;
    text-align: center;
  }
  .search button:hover {
    background-image: -webkit-gradient(linear, left top, left bottom, from(#f8f8f8), to(#f1f1f1));
    background-image: -webkit-linear-gradient(top, #f8f8f8, #f1f1f1);
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    background-color: #f8f8f8;
    background-image: linear-gradient(top, #f8f8f8, #f1f1f1);
    background-image: -o-linear-gradient(top, #f8f8f8, #f1f1f1);
    border: 1px solid #c6c6c6;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1);
    color: #222;
  }

  .sort-box {
    top: 0;
    right: 20px;
  }

  #sortlist {
    border: none;
    -webkit-box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
    box-shadow: 0px 3px 1px -2px rgba(0, 0, 0, 0.2), 0px 2px 2px 0px rgba(0, 0, 0, 0.14), 0px 1px 5px 0px rgba(0, 0, 0, 0.12);
  }

  #sortlist a {
    -webkit-font-smoothing: antialiased;
    font-size: 1rem;
    text-decoration: inherit;
  }

  #sorthand {
    background-color: transparent;
    color: #212121;
    padding: 3px 1px;
    border-radius: 0;
    border-bottom: 1px solid rgba(0, 0, 0, 0.22);
    text-decoration: inherit;
  }

  /* details page */
  .fl .main.shadow {
    background-color: #fff;
  }

  .fl .main.shadow h1 {
    font-size: 1.3em;
    line-height: 1.3em;
  }

  .main h1 {
    padding: 15px 20px;
  }

  .inseparable a.fl,
  .inseparable a.fr {
    width: 20%;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
  }

  .info {
    padding: 0;
    padding-left: 16px;
    margin: 0 20px;
    border-left: 3px solid #ddd;
  }

  .resources,
  .bdsharebuttonbox:nth-of-type(2),
  .fl .main.shadow dt strong {
    display: none;
  }

  .fl .main.shadow dd {
    margin: 0;
  }

  .fl .main.shadow ul {
    list-style: none;
    padding: 0;
  }

  .fl .main.shadow .marks {
    margin-left: 596px;
    border: none;
    background-image: none;
    -webkit-box-shadow: none;
    -moz-box-shadow: none;
    box-shadow: none;
    -webkit-border-radius: 16px;
    -moz-border-radius: 16px;
    -o-border-radius: 16px;
    border-radius: 16px;
    height: 32px;
    line-height: 32px;
    padding: 0 12px;
    border: 0;
    background-color: #dedede;
    display: inline-block;
    color: rgba(0, 0, 0, .87);
    white-space: nowrap;
  }

  .pageturn a,
  .pageturn b {
    padding: 2px 8px;
    border: none;
    color: #212121;
    background: white;
  }

  .pageturn b {
    border: 1px solid #3f51b5;
    color: white;
    background: #3f51b5;
  }

  .pageturn div[style="float:right; font-size:14px;"] {
    padding-top: 2px;
    padding-bottom: 2px;
  }

  .pageturn div[style="float:right; font-size:14px;"] input {
    border: none;
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    background: transparent;
  }

  .pageturn div[style="float:right; font-size:14px;"] input:focus {
    outline: 0;
    border-bottom: 2px solid #3f51b5;
  }

  .pageturn #tiao-btn {
    height: 20px;
    padding: 2px 8px;
    text-align: center;
    text-decoration: none;
    background: white;
    color: #212121;
    border: none;
  }

  .dbscore, .imdbscore {
    color: #fff;
    border-radius: 2px;
    padding: 6px 8px;
    background: #3FC5C4;
    margin-top: 7px;
  }

  .dbscore b, .imdbscore b {
    color: #ffe34c;
    font-weight: 500;
  }
}

@-moz-document url-prefix("http://www.dysfz.cc/my/"),
url-prefix("https://www.dysfz.cc/my/") {
  .wraper >.menu.shadow {
    width: 280px;
    top: 42px;
    left: 0;
    background-color: #fff;
    box-shadow: 0 0 4px rgba(0,0,0,.28);
    padding-top: 40px;
    border-radius: 0;
    height: 100vh;
  }

  .avatar {
    padding: 24px;
  }

  .avatar b {
    font-size: 24px;
    line-height: 40px;
  }

  .menu ul {
    background: transparent;
    margin-top: 16px;
  }

  .menu ul li {
    line-height: 40px;
    height: 40px;
    border-top: none;
    text-align: left;
    padding-left: 24px;
  }

  .menu ul li a {
    width: 100%;
    height: 100%;
  }

  .menu ul li:hover {
    background: rgba(0,0,0,0.12);
  }

  .menu ul li a:hover {
    background: transparent;
  }

  .wraper >.sticky.shadow {
    position: fixed;
    left: 0px;
    top: 450px;
    width: 240px;
    background: transparent;
    border-radius: 0;
    z-index: 999;
    padding: 24px;
  }

  .wraper .my-main {
    padding: 40px;
    margin-left: 300px;
    width: 1100px;
  }

  .wraper .my-main >.shadow {
    border-radius: 2px !important;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14), 0 3px 1px -2px rgba(0,0,0,0.12), 0 1px 5px 0 rgba(0,0,0,0.2);
  }

  #picup {
     height: auto;
  }

  .main_pick {
    padding: 0;
    margin: 0;
    padding: 16px;
  }

  .main_pick li {
    display: flex;
    display: -webkit-flex;
    justify-content: space-between;
    align-items: center;
    width: 80%;
    margin: 0;
    border: none;
    box-shadow: none;
    height: 42px;
  }

  .main_pick li a {
    width: 90%;
  }

  .main_pick li a h4 {
    width: auto;
    text-indent: 0;
    margin-top: 0;
    margin-bottom: 0;
    width: 100%;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
  }

  .main_pick li .button {
    position: unset;
    margin-left: 16px;
    order: 2;
  }

  .main_pick li .button a {
    height: auto;
  }
}
