css:
@-moz-document url-prefix("https://twitcasting.tv") {
/* 共通設定
----------------------------------------------- */
html,
body,
div#mainwrapper{
	font-size: 13px !important;
	background: #111111 !important;
    color: #FFF !important;
}
div#wrapper>div.box{
    display: none;
}
div.box {
    border-top: none;
}
.tw-basic-page-left-column>h2, .tw-basic-page-left-column>h3 {
    border-bottom: 1px solid #282828;
}


/* ヘッダー
----------------------------------------------- */
div.global-nav{
    background: #232323 !important;
}
div.global-nav a:hover,
div.global-nav ul.global-actions>li:hover,
div.global-nav .nav-dropdown:hover,
div.global-nav .nav-dropdown-menu,
div.global-nav .nav-dropdown-menu-info li,
div.global-nav .nav-dropdown:hover .nav-dropdown-menu{
    background-color: #474747
}
div.global-nav .nav-dropdown .nav-dropdown-menu>li {
    border-bottom: none;
    border-top: 1px solid #fff
}
div.global-nav .nav-dropdown-menu-info li {
    border: 2px solid #474747 !important;
    background: #474747;
}
div.global-nav .nav-dropdown .nav-dropdown-menu>li>.nav-dropdown-info a{
    color:#fff;
}
div.global-nav .nav-dropdown-menu-info:after {
    border-bottom: 8px solid #474747;
}
div.global-nav .search .search-input{
    width: auto;
    color: #6A6A6A;
    background: #111111;
    border: 1px solid #303030;
}
div.global-nav .search .search-btn{
    background: #343434;
}
div.global-nav .new-count{
    background: #343434;
}
div.global-nav ul.global-actions>li.divider{
    display: none;
}


/* メニュー
----------------------------------------------- */
div#navcontainer{
    background: #111111;
}
div#nav{
    margin: 0 auto;
    padding:6px 0 0 0;
	margin: 0 auto 10px auto;
	border-bottom: 1px solid #282828;
}
div.global-nav p{
	color: #FFF !important;
}
ul#globalnav li.active {
    border-color: #282828;
    border-bottom-color: #282828;
    background: #282828;
}
ul#globalnav li:hover {
    background: #474747;
    border-bottom: 1px solid #474747;
}
div.global-nav .nav-dropdown .nav-dropdown-menu>li {
    border-bottom: none;
    border-top: 1px solid #111111;
}


/* サブメニュー
----------------------------------------------- */
div#submenu li {
    width: 240px;
    float: left;
    text-align: center;
    font-weight: normal;
    background:#111111 ;
    border-bottom: 3px solid #474747;
}
div#submenu li a:hover{
    background-color: #474747 !important;
}
div#submenu li.selected {
    padding: 10px 0;
    border-bottom: 3px solid #000 ;
    background: #111111;
    font-weight: bold;
}
div#submenu li.selected a {
    display: inline;
    color: #fff;
    text-decoration: none;
}


/* アイテムメニュー
----------------------------------------------- */
div#ptview .point span.num {
    color: #fff
}

div#itemboxcontainer>div{
    background: #111111 !important;
}
div#itembox {
    border-bottom: none;
}
.modal-header,.modal-footer{
    background-color: #232323;
}
.modal-body,.movietile{
    background-color: #111111;
}
.alert-point {
    background-color: #f5d184;
}
div.itemspec table td{
    border: 1px solid #fff;
    background-color: #111111;
}
.close {
    color: #fff;
    text-shadow: 0 1px 0 #fff;
    opacity:10;
}
.close:hover{
    color: #fff;
    text-shadow: 0 1px 0 #fff;
    opacity:10;
}
.tw-basic-table>tbody>tr>th {
	background-color: #232323;
}
.well {
	background-color: #232323;
}
.tw-basic-table>tbody>tr>th, .tw-basic-table>tbody>tr>td {
    border-bottom: none;
}


/* 説明欄
----------------------------------------------- */
div.author p{
    color: #fff !important;
}


/* コメントエリア
----------------------------------------------- */
td.comment .comment-text{
	color:#fff !important;
}
.smallsubtitle{
    display: none;
}
#commenttext {
	color:#fff;
	background: #343434;
    width: 218px !important;
}
form.commentpost button {
	background: #343434;
	color:#fff;
}
form.commentpost button:hover,table.commentlist tr.oldcomment{
	background: #111111;
	color:#fff;
}

/* トピック作成
----------------------------------------------- */
div#wrapper .btn-group>.btn {
    padding: 6px 16px;
    border: 1px solid #484848;
    color: #fff;
    background-color: #111111;
}
div#wrapper .btn-group>.btn:hover{
    background: #484848;
}
div#wrapper .btn-group>.btn.active {
    background: #484848;
}
div#wrapper .btn-group>.btn:last-child {
    background-color: #111111;
}
div#wrapper .btn-group>.btn.active {
    background: #484848 !important;
}
table.formtable {
    border-top: solid 1px #484848;
    border-bottom: solid 1px #484848;
    background-color: #111111;
    color: #fff;
}
table.listtable th, table.formtable th {
    background: #111111;
    color: #fff;
}
table.formtable td {
    border-bottom: 1px solid #484848;
}
.input-prepend, .input-append {
    margin-bottom: 0;
}


/* ランキング・ストア
----------------------------------------------- */
div.tabbox {
    background-color: #111111;
}
div.tabbox span,.fullname,
#wrapper .details{
	color:#fff !important;
}
table.listtable.meshtable th {
    background-color: #111111;
}
#wrapper .ticketbox {
	 background-color: #FFF;
	color: #000;
}


/* キートス
----------------------------------------------- */
.tw-kiitos-card {
    color: #fff;
    background-color: #232323;
}
.tw-kiitos-list-wrapper[data-two-column="true"] .tw-kiitos-card__meta {
    padding: 5px 10px 10px 10px;
}



/* フッター
----------------------------------------------- */
div#footer,
div#footercontainer{
    background-color: #232323 !important;
}

div#pagetop p{
    border: 1px solid #232323;
    background-color: #232323 !important;
}

div#footerwrapper,div#pagetop {
    background-color: #111111;
}
}
