css:
@namespace url(http://www.w3.org/1999/xhtml);
/*Rin Familiar*/
/* i really want this to be global */
/*Images - pictures*/

#pet-headerPortrait-base {
    background: url("https://static.zerochan.net/Matsuoka.Rin.full.1744368.jpg") -15px top no-repeat !important;
    background-size: auto 125px !important;
}
#pet-portrait {
    background: url("https://static.zerochan.net/Matsuoka.Rin.full.1515740.jpg") center no-repeat !important;
    background-size: auto 450px !important;
    height: 450px !important;
}
#pet-portrait > img[src*="/static/img/pet/icon/"] {
    display: none !important;
}
/*Infos*/

#pet-info-name:before {
    content: "Matsuoka Rin " !important;
    font-size: 20px !important;
}
#pet-info-name {
    font-size: 0 !important;
}
#pet-info-evolve > .on {
    font-size: 0 !important;
}
.pet-info-evolve-icon {
    font-size: 13px !important;
}
.pet-info-evolve-icon:after {
    content: " Rin se a enamorado mas de ti";
}
#pet-info-racegender:before {
    content: "Humano" !important;
    font-size: 16px !important;
    line-height: 20px !important;
}
#pet-info-racegender {
    font-size: 0 !important;
    margin-top: 5px;
}
img[src*="/static/img/f.png"] {
    background: url("http://www.eldarya.es/static/img/m.png") !important;
    padding-left: 14px !important;
    padding-top: 0px !important;
    width: 0 !important;
    height: 0 !important;
    margin-bottom: 5px !important;
}
/*Comida - food*/

#pet-info-food {
    font-size: 0 !important;
}
#pet-info-food:before {
    content: "" !important;
    font-size: 16px !important;
}
#pet-info-food > span {
    font-size: 16px !important;
}
/*Rin en tu perfil*/

#player-display-pet {
    background: url("https://fotos.subefotos.com/65c9b6873e68a8f8f58941a498c4d31co.png") no-repeat;
    background-size: auto 100%;
    height: 544px !important;
    width: 200px !important;
}
#player-display-pet > img {
    display: none !important;
}

img[src="/i18n/static/fr/logo.png"] {
    width: 0;
    height: 0;
    background: url("https://fotos.subefotos.com/55b79fa0cc8cbfda60952e14e86f955co.png");
    padding-left: 530px;
    padding-top: 185px;
    position: absolute;
    top: -0px;
    left: -90px;
}
