css:
@-moz-document url-prefix("https://news.ycombinator.com") {
body {
  background-color: #292929;
}

#hnmain {
  background-color: #292929;
}

.athing .title a, .yclinks a {
  color: #9a9a9a;
}


.itemlist .title a:visited, .yclinks a {
  color: #616161;
}

#hnmain > tbody > tr:first-of-type table {
  background-color: #777777;
}

#hnmain > tbody > tr:first-of-type table img {
  box-sizing: border-box;
  border: 1px black solid !important;
}

.comment * {
  color: #9a9a9a;
}

.comment a {
  color: #9a9a9a !important;
}

.comment a:visited {
  color: #616161 !important;
}

#hnmain img:first-child {
    filter: grayscale(100%);
}
}

@-moz-document url-prefix("https://hn.algolia.com") {
body {
  background-color: #292929;
}

.search-results {
  background-color: #292929;
}

.page-header a {
  color: #9a9a9a;
}


.main .search-results .item .item-main .item-title-and-infos h2 a {
     color: #9a9a9a;
}

#main > div > section > header {
  background-color: #292929;
  color: #9a9a9a;
}

#main > div > header {
  background-color: #292929;
  color: #9a9a9a;
}

.main .search-results em {
  background-color: #dadada !important; /* net net net :D */
}

.search-pagination li.active button {
  border-color: #dadada;
  color: #dadada;
}

footer {
  background-color: #292929;
}

footer a {
  color: #9a9a9a;
}

#main > div > section > section > section > div.item.ng-scope > div.item-main > div.item-content-wrapper p {
  color: #dadada;
}
}

@-moz-document url-prefix("https://news.ycombinator.com/login") {
body {
  color: #9a9a9a;
}

a {
  color: #9a9a9a;
}

a:visited {
  color: #616161;
}
}
