css:
/* i really want this to be global */
body, div, span, p, header, footer, b, h1, h2, h3, h4, h5, h6, section, form, input, select,table, code, tr, th, tb, td, pre, textarea, ul, li, ol, html, object, video, audio, iframe, article, section, hedder, footer,  [style*="url"]:not([style*="base64,R0lGODlhAQABAI"]), [style*="url"], [href="/"] {
    background-color: black!important;
    color: white!important;
    border-color: green!important;
    content: ""!important;
}
img {
 background-color:#8990a1!important;
    color:white!important;
}
a {
 background-color:black!important;   
 color:red!important;
}
button {
    background-color:black!important;
    color:green!important;
    border: 2px double green!important;
}

/* To deal with W3.css based web sites (mainly w3schools.com) */
.w3-theme-l5 {color:#000 !important; background-color:#f0f0f0 !important}
.w3-theme-l4 {color:#000 !important; background-color:#cccccc !important}
.w3-theme-l3 {color:#fff !important; background-color:#999999 !important}
.w3-theme-l2 {color:#fff !important; background-color:#666666 !important}
.w3-theme-l1 {color:#fff !important; background-color:#333333 !important}
.w3-theme-d1 {color:#fff !important; background-color:#000000 !important}
.w3-theme-d2 {color:#fff !important; background-color:#000000 !important}
.w3-theme-d3 {color:#fff !important; background-color:#000000 !important}
.w3-theme-d4 {color:#fff !important; background-color:#000000 !important}
.w3-theme-d5 {color:#fff !important; background-color:#000000 !important}

.w3-theme-light {color:#000 !important; background-color:#f0f0f0 !important}
.w3-theme-dark {color:#fff !important; background-color:#000000 !important}
.w3-theme-action {color:#fff !important; background-color:#000000 !important}

.w3-theme {color:#fff !important; background-color:#000000 !important}
.w3-text-theme {color:#000000 !important}
.w3-border-theme {border-color:#000000 !important}

.w3-hover-theme:hover {color:#fff !important; background-color:#000000 !important}
.w3-hover-text-theme {color:#000000 !important}
.w3-hover-border-theme:hover {border-color:#000000 !important}

.w3-white, .w3-light-gray, .w3-light-blue, .w3-pale-yellow, .w3-pale-red, .w3-green, .w3-blue, .w3-red {
    background-color: black!important;
    color: white!important;
}
