css:
@-moz-document regexp("https?:\\/\\/www\\.baidu\\.com\\/s.*") {
#content_right {
    display: none
}

#container {
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: 1280px !important;
    margin: 0 auto !important;
    padding-left: 0 !important;
}
#container > .result-molecule {
    display: flex;
    justify-content: center;
    margin: 0 !important;
    padding: 0 !important;
    width: 100%;
}
#content_left {
    padding: 0 !important;
    float: unset;
    margin: 0 auto !important;
    width: 1280px !important;
    overflow: hidden;
    display: flex;
    flex-wrap: wrap;
}
#content_left > div.c-container, #content_left > div.c-group-wrapper {
    flex-basis: calc(100% / /*[[columns]]*/ - 10px);
    width: calc(100% / /*[[columns]]*/ - 10px);
    height: auto;
    margin: 5px;
    overflow: hidden;
}
.new-pmd .c-span12 {
    width: calc(100% - 6px);
    margin-right: 0 !important;
    padding: 5px 5px 5px 1px;
}
.new-pmd .c-border {
    margin: 0 !important;
}
#rs_new {
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}
#rs_new > :first-child {
    flex-basis: 100%;
    display: flex;
    justify-content: center;
}
}
