css:
@-moz-document regexp("^https?:\\/\\/*\\.*qiita\\.com.*"),domain("qiita.com")
{

/* Base */
body.with-js  {
  background-color: #555;
}
.with-js #main {
  background-color: #222;
  box-shadow: 0 0 5px #333;
  color: #ccc;
}
.with-js ul.nav {
  border-color: #333;
}
.with-js span.s1 {
  color: #c00;
  font-weight: bold;
}
.with-js pre {
  background: linear-gradient(#ddd, #fff);
  border-top: 1px solid #fff;
  box-shadow: 0 0 10px #000;
  font-family: inconsolata;
  font-size: 2rem;
}
.with-js blockquote p {
  color: #79b74a;
}
/* Header */
.ArticleAsideHeader {
  background-color: #59972a;
  border-color: #79b74a;
  color: #ccc;
}
.ArticleMainHeader {
  background: linear-gradient(#59972a, #79b74a);
}
.ArticleMainHeader__title {
  text-shadow: 0 0 15px #39770a, 0 0 5px #050;
}
/* Code */
.with-js code {
  background: linear-gradient(#39770a, #29670a);
  box-shadow: 0 0 10px #000;
  color: #c9ff9a;
  text-shadow: 0 0 5px #000;
}
.with-js a code {
  color: #99d76a !important;
}
.with-js a code::after {
  content: " >>";
}
.with-js .code-frame {
  background: linear-gradient(#59972a, #79b74a);
}
.with-js .code-lang {
  color: #09370a;
}
/* Form */
.commentForm_body textarea {
  background: linear-gradient(#333, #444);
  border-color: #444;
  border-top: 1px solid #666;
  box-shadow: 0 0 10px #000;
}
.commentForm_contentHeader,
.commentForm_body,
.commentForm_tab,
.preview-area,
.commentForm_imageUploader {
  background: linear-gradient(#333, #444);
  border-color: #000;
  border-top-color: #555;
  color: #999;
}
/* Side */
.with-js header.sharedHeader {
  background-color: #000;
  box-shadow: 0 0 15px #000;
}
.with-js img.itemsShowAuthorInfo_userIcon {
  border: 2px solid #79b74a;
  border-radius: 5px;
}
.comment_content {
  color: #333;
}
/* Footer */
.with-js .footer {
  background: linear-gradient(#000, #222);
}
.with-js .footer_container {
  border-color: #79b74a;
}
.with-js .footer_container a {
  color: #59972a;
}
.itemsShowAuthorInfo a {
  color: #79b74a;
}
.itemsShowBody_articleColumnFooter {
  background: linear-gradient(#333, #444);
  border-top-color: #555;
}

.markdownContent h1 {
  border-bottom: dotted 2px #39770a;
  color: #79b74a;
}
.markdownContent h2 {
  border-bottom: dotted 2px #39770a;
  color: #59972a;
}
}
