css:
@-moz-document domain("nucleo.heinous.industries") {
#sidebar-wrapper {background: #080808;}
.sidebar-nav li {color: #39f;}
.sidebar-brand {
    font-weight: bold;
    text-shadow: 0 0 3em #00f, 0 0 2.5em #a5f, 0 0 2em #a5f, 0 0 1em #ff0, 0 0 0.2em #0ff;
    color: #000 !important;
}


body {
    background-color: #111 !important;
    color: #aaa !important;
}

h2 {color: #3aa;}
h3 {color: #aa3;}
ul {color: #c9c;}
input {color: #9cc !important; background: #002 !important; border: solid 1px #9cc !important;}
select {color: #aaa !important; background: #200 !important; border: solid 1px #000 !important;}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:focus, .nav-tabs>li.active>a:hover {
    color: #999;
    background-color: #333;
    border: 1px solid #444;
}

.alert-danger {
    color: #922;
    background-color: #101;
    border-color: #211;
}

.btn-default {
    color: #5a5;
    background-color: #282828;
    border-color: #222;
}

.btn-default.active, .btn-default:active, .open>.dropdown-toggle.btn-default {
    color: #0ff;
    background-color: #020;
    border-color: #030;
}


.panel {
    background-color: #333;
    border: 1px solid transparent;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0,0,0,.05);
    box-shadow: 0 1px 1px rgba(0,0,0,.05);
}

.progress {background-color: #111;}
.progressbar-font {color: #999;}
.progress-bar {background-color: #259;}

.panel-default>.panel-heading {
    color: #eee;
    background-color: #444;
    border-color: #000;
}

.panel-warning>.panel-heading {
    color: #faa !important;
    background-color: #683818;
    border-color: #000;
}

.panel-success>.panel-heading {
    color: #dff0d8;
    background-color: #3c763d;
    border-color: #000;
}

.thumbnail {
    color: #eee;
    background-color: #444;
    border-color: #000;
}
}
