css:
@-moz-document domain("mazii.net") {
body {
    margin-bottom: 20px;
}
header.app-bar {
    background-color: #2e3033;
    border-radius: 0 !important;
}
nav.navbar.navbar-fixed-top {
    background-color: #2e3033;
    height: 56px!important;
}
body {
    background-color: #33353a;
}
.mobile-app-marketing {
    display: none;
}
#chat-module {
    display: none;
}
li.active {
    background-color: #111a23;
}
.nav>li {
    border-radius: 15px;
}
#search-button {
    border: none;
    background-color: transparent;
}
#search-text-box {
    color: #d8d8d8 !important;
    padding-top: 10px;
    margin-top: 5px;
    margin-bottom: 5px;
    border-color: #212223;
    border-radius: 20px;
    background-color: #212223;
}
#clear-search-text {
    margin-top: 5px;
}
#show-draw-kanji {
    margin-top: 5px;
}
#tab0 {
    border: none;
    color: #a4a4a5;
    background-color: transparent;
}
#tab1 {
    border: none;
    color: #a4a4a5;
    background-color: transparent;
}
.tab-active {
    text-shadow: 0px 1px #212223;
    color: #ea4931 !important;
    font-weight: bold;
}
#tab2 {
    border: none;
    color: #a4a4a5;
    background-color: transparent;
}
#tab3 {
    border: none;
    color: #a4a4a5;
    background-color: transparent;
}
#tab4 {
    border: none;
    color: #a4a4a5;
    background-color: transparent;
}
#tab0:focus {
    outline: none;
}
#tab1:focus {
    outline: none;
}
#tab2:focus {
    outline: none;
}
#tab3:focus {
    outline: none;
}
#tab4:focus {
    outline: none;
}
.btn-danger {
    color: #ed4025 !important;
    background-color: transparent !important;
    border: none !important;
    font-weight: bold;
}
.btn:active {
    box-shadow: 0 0 !important;
}
.btn-sm {
    color: #a4a4a5;
    border-radius: 25% !important;
    border-color: #424242 !important;
    background-color: #666666 !important;
}
.btn-sm:hover {
    border-color: #636363 !important;
    background-color: #848484 !important;
}
.word-container {
    border-radius: 10px;
    background-color: #212223;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
#model-web-98746 {
    display: none;
    border-radius: 10px;
    margin-bottom: 10px;
    font-size: 14px !important;
    background: #2F3136 !important;
}
#model-web-98746:focus {
    outline: none;
}
#collapseFeedback-98746 {
    color: #d8d8d8 !important;
    border-radius: 10px;
    background-color: #2E3033;
    box-shadow: 2px 3px 10px #1b1b1c !important;
    padding-top: 10px;
}
#accordion>p {
    display: none;
}
.result-kanji-search-word {
    margin-top: -10px !important;
}
.panel-heading {
    color: #d8d8d8 !important;
    border-radius: 5px;
    margin-bottom: 15px !important;
    background-color: #212223 !important;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.panel-collapse {
    border: none!important;
    box-shadow: none !important;
    background-color: transparent !important;
}
.ng-scope {
    box-shadow: none !important;
    border-radius: 15px !important;
}
.result-kanji-search-word>.panel-group>.ng-scope {
    background-color: transparent !important;
}
.panel-body {
	border: none !important;  
}
.box-notify-mean {
    display: none;
}
#view2 {
    display: none;
}
.japanese-char {
    color: #a4a4a5;
}
.han-viet-word {
    color: #a4a4a5;
}
.mean-fr-word {
    color: #6197F1;
}
.mean {
    color: #d8d8d8 !important;
}
.word_ja {
    color: #d8d8d8 !important;
}
.marketing {
    display: none;
}
.suggest-title {
    margin-top: 15px !important;
    color: #d8d8d8 !important;
}
.synonyms-result {
    color: #d8d8d8 !important;
    border-radius: 0 0 10px 10px;
    background-color: #2E3033;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.suggest-box {
    border-radius: 10px;
    background-color: #212223;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.phonetic {
    color: #d8d8d8 !important;
}
.main-word {
    color: #ef1c42 !important;
    font-size: 30px !important;
    margin: 0 3px 10px 0 !important;
}
p.ja {
    color: #ef1c42 !important;
    font-size: 30px !important;
    margin: 0 3px 10px 0 !important;
}
p.ja+i.audio-word {
    padding-top: 6px;
    font-size: 28px;
}
.mean-suggest {
    color: #6666f4 !important;
}
.add-mean-user>form>textarea {
    display: none;
}
.check-example-directive {
    display: none;
}
.list-kanji>div:nth-child(2)>.ng-isolate-scope>.kanji-container>div:nth-child(2)>.kanji-draw {
    background-color: transparent !important;
    border: none !important;
    box-shadow: none !important;
}
.kanji-main-infor {
    border-radius: 10px;
    background-color: #212223;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.kanji-container>div:nth-child(1)>.kanji-main-infor>div:nth-child(2)>span:nth-child(2) {
    color: #ef1c42 !important;
    font-size: 20px !important;
}
.button-collapse {
    border: none !important;
}
.kanji-main-infor:nth-child(8)>p {
    border: none !important;
}
.fa-angle-double-down {
    color: #d8d8d8 !important;
    font-weight: bold;
    font-size: 20px;
}
.fa-angle-double-up {
    color: #d8d8d8 !important;
    font-weight: bold;
    font-size: 20px;
}
ul.list-collapse>li {
    color: #d8d8d8 !important;
}
.short-mean {
    color: #d8d8d8 !important;
}
.level {
    color: #d8d8d8 !important;
}
.pronoun-item {
    color: #d8d8d8 !important;
}
.comp-detail {
    margin: 5px 0 5px 0;
    color: #d8d8d8 !important;
}
.draw-single-kanji {
    border-color: #666666 !important;
    background-color: #666666 !important;
    margin: 0 7px 7px 7px;
}
.draw-single-kanji:active {
    border-color: #dbdbdb !important;
    background-color: #fff !important;
    outline: none !important;
}
.selected {
	background-color: #d8d8d8 !important;
}
.btn:active {
    outline: none !important;
}
.btn:focus {
    outline: none !important;
}
.example-kanji {
    color: #cccccc;
    background-color: #2E3033;
    border-radius: 10px;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.example-kanji>table {
	margin-bottom: 0;   
}
.example-kanji>table>tbody>tr {
    height: 25px;
}
.example-kanji>table>tbody>tr>td {
    font-size: 16.5px;
}
.example-kanji>table>tbody>tr:last-child {
    border: none !important;
}
.close-modal-jlpt {
    background-color: transparent !important;
    color: #ff3030 !important;
    padding-bottom: 5px !important;
}
.close-modal-jlpt:active {
    outline: none;
}
.modal-dialog {
    border-radius: 10px;
}
.modal-jlpt {
    border-radius: 10px;
}
.modal-content {
    border-radius: 10px;
}
.modal-body {
    border-radius: 10px;
}
.show-panel-mean-of-user {
    color: #d8d8d8 !important;
    border-radius: 10px;
    background-color: #2E3033;
    box-shadow: 2px 3px 10px #1b1b1c !important;
    padding-top: 10px;
}
.show-panel-mean-of-user>.wrapper>div:last-child>.user-mean {
	border-bottom: none !important;
    padding-bottom: 15px;
}
.number-report {
    color: #c6c6c6 !important;
    border: none;
    font-size: 15px !important;
    padding-top: 25px !important;
}
.number-report>p {
    margin-bottom: 0 !important;
}
.mean {
    font-size: 17px !important;
    margin: 10px 0 0 0;
}
.user-like {
    color: #2fc43b;
}
.user-dislike {
    color: #c4362f;
}
.panel>.panel-collapse>.panel-body>.ng-isolate-scope>.kanji-container>.kanji-main-infor>.pronoun-item>span:nth-child(2) {
    font-size: 20px;
    text-transform: lowercase;
    padding-left: 10px;
}
.tab-container>.result-word>.words-list>.ng-scope>div+ng-feedback+ng-word-ja+.widget-container {
    background-color: #2E3033;
    box-shadow: 2px 3px 10px #1b1b1c !important;
}
.verb-conjugation-container>.verb-title {
    font-size: 18px;  
    color: #ce4444;
    padding-left: 3px;
    margin-bottom: 10px;
}
.verb-conjugation>tbody>tr {
    height: 45px;
    border-color: #4f4f4f !important;
}
.verb-conjugation>tbody>tr>td:nth-child(1) {
	font-size: 16px;       
    color: #aaaaaa;
}
.verb-conjugation>tbody>tr>td:nth-child(2) {
	font-size: 16px;       
    color: #d8d8d8;
}
.verb-conjugation>thead>tr {
	height: 35px;
}
.verb-conjugation>thead>tr>th {
    font-size: 18px;  
    color: #ce4444;
}
.kanji-draw-again>button {
    color: #ededed;
    background-color: #c93434 !important;
	border-radius: 10px !important;
    border: none;
    box-shadow: none !important;
}
.kanji-draw-again>button:hover {
    background-color: #dd4949 !important;
}
.kanji-draw-again>button:active {
    background-color: #962d2d !important;
}
path {
	stroke: #bfbfbf !important;
}
.info-user-login {
    border-radius: 0 !important;  
}
.info-user-login>.btn {
	border-radius: 15px !important;    
}
.examples-list {
    background-color: #2E3033 !important;    
	padding-top: 5px !important;      
}
.examples-list>ng-example:last-child>div {
    border: none;    
    padding-bottom: 0 !important;
}
.example-index {
	color: #d6d6d6 !important;   
    margin-right: 10px;
}
.example-container {
	border-radius: 0 !important;
    border-color: #565656 !important;
    margin-bottom: 5px;
    padding-bottom: 9px !important;
    padding-left: 10px !important;
}
.example-word {
	padding-top: 13px !important;
}
.word-container+ng-feedback>div:first-child {
	margin-top: -37px !important; 
}
.audio-word {
    padding-top: 1px;
	font-size: 28px;   
}
.suggest-box {
    position: relative;
    z-index: 10 !important; 
}
.suggest-box+div {
    position: relative;
    z-index: 1 !important; 
    margin-top: -35px;
}
.search-input-container>div:nth-child(2)>input {
    box-shadow: none !important;
    transition-property: box-shadow !important;
    -webkit-transition-property: box-shadow !important;
    transition: .35 ease-in-out !important;
    -webkit-transition: .35 ease-in-out !important;
    transition-duration: .125s;
    -webkit-transition-duration: .125s;
}
.search-input-container>div:nth-child(2)>input:hover, .search-input-container>div:nth-child(2)>input:focus {
	box-shadow: 0px 3px 20px #18161c !important;
}
.img-logo {
    margin-left: 20px;
}
}
