#header {
/*     background: url("https://i.imgur.com/YVsBcl2.jpg") no-repeat transparent; */
    background-color: #1B1B1D!important;
    background-size: 20% auto!important;
    background-position: 95% 100% !important;
}

#upper_header{
    background: url("https://i.imgur.com/kxVmXXi.png") transparent no-repeat!important;
    background-size: 10% auto!important;
    background-position: 24% 110%!important ;
}

