    background: url("https://i.imgur.com/TgHYCvh.png") transparent no-repeat;
    background-size: 30% 60%!important;
    background-position: -1% -50% !important;
} */

#upper_header div {
    opacity: 1;
  	transition: all 1.5s linear;
    
}

#upper_header div:hover {
    opacity: 0;
}


/* BODY */
#body, #streams, #downloads, #comments, #footer {
    background:#2A2A2E;
    border: 1px  solid #2A2A2E;
}


















/* BODY TITLES FONT */
#top_block h1, #slink, h3, #drama_updates tbody tr td div a, #drama_updates tbody tr td ul li a, h3 a, #content h1{
    color: #F9FBF6!important;
}

/* BODY FONTS */
#genre_list ul li a, #drama_releases h2, #drama_updates tbody tr td ul li a, .descr, span, #videos ul li a, .series_index tbody tr td a, #updates tbody tr td div a, #slink a #comments h3{
    color: silver!important;
}

/* VISITED */
#body a:visited {
    color: crimson!important;
}

/* HOVER */
#body a:hover{
    color: yellow!important;
}

/* HEADER FONTS */
#lower_header div, #menu li a, .box h2, #drama_releases h2, #series_info, #series_info .left_col img, #videos, #comments_heading{
    background: #222225!important;
    border: 1px  solid #2A2A2E!important;
    color: wheat!important;
}

/* BODY FONTS */
#drama_releases, div.box, #container li:nth-child(2n+1){
    background:#2A2A2E!important;
	color: white!important;
    font:12px "YouTube Noto",Roboto,arial,sans-serif;

}      

/* CONTAINER FONTS */
.box > div, #container a, .comment{
    background:#2a2a2e!important;
	color: silver!important;
	border: 1px  solid #2A2A2E;
}


/* CONTAINER STYLE */
#container #rr li, #container #rs li {
    background-image:none!important;
    list-style: inside;
    list-style-type: circle;
}

/* PAGINATION FONT */
.button-content {
	color: black!important;
}

}
