css:
@-moz-document domain("youtube.com") {
.fm2-title-border-box-gray,
.fm2-title-border-box-blue,
*[class="statModule-title statModule-title-open yt-rounded"],
*[id="statmodules_inbox"],
.homepage-block-heading,
#bar,
.nav-item {
    background: #fff url('http://i256.photobucket.com/albums/hh168/Q9R42/PinkFade.png') repeat-x left top !important;
}
* {
    font-size: 14px !important;
    font-family: rosemary !important;
    color: #FF1493 !important;
}
.searchField,
#comment_entry_box_text,
#user_videos-query,
input[type="text"],
#itsalltext_generated_id_comment_1 {
    -moz-appearance: none!important;
    border: 1px !important;
}
body:before {
    /*+++*/
    
    content: "";
    position: fixed;
    width: 100%;
    height: 100%;
    opacity: 0.4;
    background: url('https://data.whicdn.com/images/80463197/original.jpg') fixed;
    background-repeat: repeat;
}
body {
    /*--background: url('https://data.whicdn.com/images/80463197/original.jpg') fixed !important;--*/
    
    background: transparent !important;
    /*+++*/
    
    color: #000 !important;
}
#masthead-container {
    background: transparent !important
}
/*+++*/

.video-title {
    color: #DFA7CF !important;
}
.hLink {
    color: #F6C8EB !important;
}
DIV#chrome-promo,
A.chrome-link,
#side-announcement-box {
    display: none !important;
}
*[class="clear feedmodule-border-gray yt-rounded"],
*[class="clear feedmodule-border-blue yt-rounded"],
*[class="homepage-side-block"],
.fm2-top-cap {
    border-top: none !important;
}
.feedmodule-normal {
    background: transparent !important;
}
.img-blue-bottom-cap {
    height: 1px !important;
    margin-bottom: 6px !important;
}
.feedmodule-data {
    border-top: none !important;
}
a {
    color: #000;
}
.feedmodule-upper-left-right-border,
.feedmodule-upper-left-right-border-blue {
    border: 1px solid #bbb !important;
    -moz-border-radius-bottomleft: 6px !important;
    -moz-border-radius-bottomright: 6px !important;
    border-bottom-left-radius: 6px !important;
    /*+++*/
    
    border-bottom-right-radius: 6px !important;
    /*+++*/
}
*[class="statModule-title statModule-title-open yt-rounded"] {
    height: 24px !important;
    overflow: visible !important;
    padding-bottom: 8px !important;
    position: relative !important;
    background-position: -4px -2px !important;
}
*[class="module-item-wrapper-inner yt-rounded"] {
    border-top: none !important;
}
.homepage-block-heading {
    background: #FDB9DE;
    margin-top: -11px !important;
    margin-left: -16px !important;
    -moz-border-radius: 8px 8px 0 0 !important;
    border-radius: 8px 8px 0 0 !important;
    /*+++*/
    
    width: 278px !important;
    height: 19px !important;
    color: #000 !important;
    border-top: #BFBFBF 1px solid !important;
    border-left: #BFBFBF 1px solid !important;
    border-right: #BFBFBF 1px solid !important;
    background-position: 2px 8px !important;
  
    border-bottom: none !important;
}
}
