css:
@-moz-document domain("www.ilbe.com") {
div, .header, .smallBox .messageBox, .boardRead .fileAttached, p, textarea, .replyBox .replyItem, body, #content, tbody > tr > td, table > tr > th, thead > tr > th, #widget, h1, #contentWrapper {
    background: black !important;
}

.notify_normal {
	border: 2px solid #000;
}

.xe_content, textarea {
    color: white;
}

div, p, .boardList th a, .boardList thead tr th div, .boardList td.author, .latest_white ul li .date, .widgetBoxHeader a, .latest_white ul li .title a, .boardList td.title, .boardList td.title a, #content > div.content_margin > form > table > tbody > tr {
    color : #ffffff91 !important;
}

.boardList thead tr th div {
    font : bold;
}

#header h1 {
    background: transparent!important;
    color: white;
}

.boardRead .titleAndUser .title a {
	color:white;
}

.notice-item > p, .notify_normal .notice-item-close {
    float: right;
		
}
.notice-item > p {
    width: 90%;
}

.notice-item .notify_normal{
    padding-bottom: 25px;
}

#header {
    background: url(http://www.ilbe.com/layouts/elkha_ilbe/img/headerBg2.png) no-repeat !important;
    background-position: 0 33px !important;
}
}
