css:
@-moz-document domain("atelier801.com") {
/* NIGHTHOLDER THEME BY ESHKATION */

body {
    background-color: #2F3136;
	color: hsla(0,0%,100%,.7);
    font-family: Segoe Ui;
    font-weight: 100;
}

.accordion-group img[src='img/icones/16/on-offbis1.png'],
.accordion-group img[src='img/icones/16/on-offbis2.png'] {
    display: none;
}

a[href^='/reports?c='] {
    background-color: rgba(250, 150, 10, 0.4);
    height: 22px;
}

a[href^='/reports?c=']:hover {
    background-color: rgba(250, 150, 10, 0.6) !important;
}

a[href^='/reports?c=xx'] {
    background-color: rgba(60, 135, 175, 0.4);
}

a[href^='/reports?c=xx']:hover {
    background-color: rgba(60, 135, 175, 0.6) !important;
}

.barre-navigation  {
    border-radius: 0px;
    background-color: rgba(68, 68, 68, 0.2);
    font-family: Segoe Ui;
    font-weight: 100;
}

#formulaire_deconnexion > button {
    margin-left: 10px;
}

.badge {
    border-radius: 0px;
    padding-left: 10px;
    background-color: transparent;
    font-family: Segoe Ui;
    font-weight: 100;
    text-shadow: none;
}

.bouton-like {
    vertical-align: middle;
    position: relative;
}

.bouton-like > .coeur {
    position: relative;
    top: 3px;
}

.bouton-profil-avatar {
    display: inline-block;
}

.bouton-profil-nom {
    font-family: Segoe Ui;
    font-weight: 100;
}

.bouton-profil-nom:hover {
    filter: brightness(100%);
}

.btn {
    text-shadow: none;
    background-image: none;
    border-bottom-color: transparent;
    border-top-color: transparent;
    -webkit-box-shadow: none;
    border-radius: 0px !important;
    box-shadow: none;
}

.btn-message {
    font-family: Segoe Ui;
    font-weight: 100;
    min-width: 50px;
    background-color: rgba(30, 35, 40, 0.3);
    text-transform: lowercase;
}

.btn-message:hover,
.btn-message:focus {
    background-color: rgba(30, 35, 40, 0.5);
    box-shadow: none;
    outline: none;
}

.btn-message.active {
    background-color: rgba(30, 35, 40, 1);
}

.cadre {
    color: hsla(0,0%,100%,.7);
    font-family: Segoe Ui;
    font-size: 13px;
    border-radius: 0px !important;
}

.cadre legend {
	color: #c9c6c1;
    border-bottom: 0px;
    font-family: Segoe Ui;
    font-weight: 100;
    text-align: center;
}

.cadre-auteur-message {
    text-align: center;
}

.cadre-auteur-message-date {
    color: hsla(0,0%,100%,.7);
}

.cadre-code {
    background-color: #282C34;
    font-family: 'Lucida Console';
    font-size: 11px;
}

.cadre-code .colonne-numeros-lignes-code, .colonne-lignes-code, .colonne-lignes-code * {
    font-family: 'Lucida Console';
    font-size: 11px;
    font-weight: 100;
}

.cadre-code .mot-cle-lua-code {
    color: #C678DD;
}

.cadre-code .chaine-caracteres-lua-code {
    color: #98C379;
}

.cadre-code .indication-langage-code {
    font-family: Segoe Ui;
    font-weight: 100;
    font-size: 13px;
}

.cadre-code .colonne-numeros-lignes-code {
    border: 0px;
    border-right: 1px solid #4B5262;
    margin-right: 2px;
    color: #4B5262;
    opacity: 1;
}

.cadre-code hr {
    border-bottom: 1px solid #4B5262;
}

.cadre-conversation,
.cadre-resultat-recherche {
    border-left-color: #A6A6A8;
    background-color: #2F3136;
    border-radius: 0px;
}

.cadre-defaut,
.cadre-staff-prive {
    border-radius: 0px;
    background-color: #282828;
}

.cadre-dernieres-images-utilisateurs {
    background-color: #202225;
}

.cadre-formulaire,
.cadre-repondre {
    background-color: #202225;
    border-radius: 0px;
    border: 0px;
    overflow: visible;
}

.cadre-forum {
    background-color: rgba(50, 55, 65, 0);
    border-radius: 0px;
    overflow: hidden;
}

.cadre-forum.cadre-relief {
    border-bottom: 0px !important;
}

.cadre-forum-titre {
    border-bottom: 2px solid hsla(0,0%,100%,.7);
    font-family: Segoe Ui;
    font-weight: 100;
}

.cadre-forum-titre * {
    text-shadow: none !important;
}

.cadre-forum-titre > a > img {
    display: none;
}

.cadre-jeu {
    background-color: #202225;
    border-radius: 0px;
    padding: 0px;
}

.cadre-message {
    background-color: #36393E;
    border-radius: 0px;
}

.cadre-message.cadre-message-like {
    background-image: none;
    border-left: 2px solid #C678DD;
}

.cadre-message table {
    border: 0px;
}

.cadre-message-contenu table > tbody > tr:first-child {
    border-bottom: 1px solid rgba(200, 195, 190, 0.6);
    font-weight: 400;
}

.cadre-message-contenu table > tbody > tr {
    border-bottom: 1px solid rgba(200, 195, 190, 0.1);
}

.cadre-message-contenu table > tbody > tr:hover {
    border-bottom: 1px solid rgba(200, 195, 190, 0.3);
}

.cadre-message-contenu table > tbody > tr > td {
    border: 0px !important;
    padding-right: 10px;
    padding-bottom: 0px;
    padding-left: 10px;
    font-family: Segoe Ui !important;
    font-weight: 100;
}

.cadre-message-message {
    color: #C9C9CB;
    font-family: Segoe Ui;
    font-weight: 100;
}

.cadre-message-supprime {
    background-color: rgba(220, 45, 80, 0.1);
    border-radius: 0px;
    border-left: 2px solid #DB2B4F;
}

.cadre-message-supprime .cadre-message {
    background-color: rgba(220, 45, 80, 0.2);
    border-left: 2px solid #DB2B4F;
}

.cadre-presentation {
    border-radius: 0px;
    background-color: rgba(30, 30, 30, 0.2);
    margin: 20px;
    padding: 10px;
}

.cadre-quote {
    background-color: rgba(250, 170, 30, 0.1);
    border-radius: 0px;
    border-left: 2px solid #FAA61A;
}

.cadre-quote small {
    color: inherit;
    font-weight: bold;
}

.cadre-quote small:before {
    content: '@';
}

.cadre-relief {
    border-top: 0px;
    border-bottom: 2px solid #1F2123;
}

.cadre-resultat-recherche {
    background-color: rgba(40, 45, 50, 1);
    border-left: 2px solid #282C34;
    padding: 0px;
}

.cadre-section {
    background-color: transparent;
    border-radius: 0px;
    border-bottom: 1px solid rgba(200, 195, 190, 0.3);
}

.cadre-section-titre-mini,
.cadre-section-titre {
    text-shadow: none;
    font-family: Segoe Ui;
    font-weight: 100;
}

.cadre-section-titre-mini > .img24 {
    display: none;
}

.cadre-section-titre > .img32 {
    display: none;
}

.cadre-signalement {
    border-radius: 0px;
}

.cadre-spoil {
    background-color: rgba(40, 45, 50, 0.9);
    border-radius: 0px;
    border: 0px;
    border-left: 2px solid #282C34;
}

.cadre-staff-prive {
    background-color: #1A1A1A;
}

.cadre-sujet {
    border-radius: 0px;
    background-color: rgba(27, 119, 66, 0.4);
    border-left: 2px solid #1B7742;
    border-bottom: 0px;
    color: #009d9d;
}

.cadre-sujet-date {
    filter: grayscale(100%);
}

.cadre-sujet-titre {
    vertical-align: middle;
    font-family: Segoe Ui;
    font-weight: 100;
}

.cadre-sujet-titre-sombre {
    color: #BDBDBC;
}

.cadre-sujet-titre-sombre:hover {
    filter: brightness(150%);
    color: #BDBDBC;
}

.cadre-type-auteur-admin,
.cadre-type-auteur-admin:hover,
.cadre-type-auteur-admin:active {
    color: #f64747;
}

.cadre-type-auteur-mapcrew,
.cadre-type-auteur-mapcrew:hover,
.cadre-type-auteur-mapcrew:active {
    color: #4183d7;
}

.cadre-type-auteur-moderateur,
.cadre-type-auteur-moderateur:hover,
.cadre-type-auteur-moderateur:active {
    color: #f4d03f;
}

.cadre-type-auteur-sentinelle,
.cadre-type-auteur-sentinelle:hover,
.cadre-type-auteur-sentinelle:active {
    color: #00ff7f;
}

.cadre-type-auteur-joueur,
.cadre-type-auteur-joueur:hover,
.cadre-type-auteur-joueur:active {
    color: #BDBDBC;
}

.cadre-upload-image-utilisateur {
    height: 150px;
    background-color: #202225;
    background-image: none;
    border-radius: 0px;
}

.composant-selection-image-utilisateur-upload {
    height: 100px;
    background-color: #484B51;
    background-image: none;
}

.composant-selection-image-utilisateur-upload:hover {
    background-color: #484B51;
    background-image: none;
    color: #FFFFFF;
}

.composant-selection-image-utilisateur-upload:hover .empeche-selection-texte {
    opacity: 1;
}

.contenant-image-utilisateur-grille-sans-marge {
    background-color: #2A2C31;
}

.empeche-selection-texte {
    color: #FFFFFF;
    opacity: 0.3;
}

.empeche-selection-texte:hover {
    opacity: 1;
}

.cadre-utilisateur {
    border-radius: 0px;
    background-color: #202225;
    padding-top: 7px;
    font-family: Segoe Ui;
}

.control-label {
	color: #c9c6c1;
    font-family: Segoe Ui;
    font-weight: 100;
}

.date-resultat-recherche > span,
.date-resultat-recherche .cadre-type-auteur-joueur {
    color: rgba(230, 230, 230, 0.8) !important;
    font-family: Segoe Ui;
    font-weight: 100;
}

.dropdown-menu {
    border-radius: 0px;
    margin-left: -1px;
    box-shadow: none;
    -webkit-box-shadow: none;
    padding: 0px;
}

.dropdown-menu .divider {
    border-bottom: 1px solid #1B1B1B;
    height: 0px;
}

.element-footer > a {
    color: #c9c6c1 !important;
    opacity: 0.7;
    font-family: Segoe Ui;
}

.element-footer > a:hover,
.element-footer > a:active {
    opacity: 1;
}

.font-xxl {
    font-family: Segoe Ui;
    font-weight: 100;
}

footer {
    background-color: #202225;
    vertical-align: middle;
}

#footer > div.container-fluid > div > div.span8 > p {
    margin-bottom: 0px;
    vertical-align: middle;
}

hr {
    border-top: 0px;
    border-bottom: 1px solid rgba(200, 195, 190, 0.6);
    margin-top: 5px;
    margin-bottom: 5px;
}

input {
    border-radius: 0px !important;
}

img[src='img/icones/16/on-offbis1.png'],
img[src='img/icones/16/on-offbis2.png'] {
    display: block;
	background-color: #43B581;
	width: 5px;
	height: 5px;
	border-radius: 50%;
	padding: 0px 5px 0 0;
	overflow: hidden;
	box-sizing: border-box;
	position: absolute;
	top: 3px;
	left: -5px;
}

img[src='img/icones/16/on-offbis1.png'] {
	background-color: #747F8D;
}

.element-bouton-profil img[src='img/icones/16/on-offbis1.png'],
.element-bouton-profil img[src='img/icones/16/on-offbis2.png'] {
    top: 10px;
    left: auto;
}

.nom-utilisateur-liste-discussion img[src='img/icones/16/on-offbis1.png'],
.nom-utilisateur-liste-discussion img[src='img/icones/16/on-offbis2.png'] {
    top: 4px;
    left: 17px;
    border: 1px solid #202225;
    width: 7px;
	height: 7px;
	border-radius: 50%;
	padding: 0px 5px 0 0;
}

.nom-utilisateur-liste-discussion > img[src^='http://avatars.atelier801.com/'],
.nom-utilisateur-liste-discussion > img[src^='https://avatars.atelier801.com/'] {
    border-radius: 50%;
}

img[src='/img/icones/cadenas.png'] {
    src: url('http://img.atelier801.com/1024f321.png');
}

img[src='/img/logo-atelier801-bleu.png']{
    filter: grayscale(100%);
}

img[src='/img/icones/plus24-2.png'],
img[src='/img/icones/moins24-2.png'] {
    display: none;
}

.label {
    border-radius: 0px;
    padding: 2px;
    height: 20px;
    box-shadow: none;
    text-shadow: none;
    font-family: Segoe Ui;
    font-weight: 100;
    padding-left: 6px;
    padding-right: 6px;
    min-width: 54px;
    text-align: center;
}

.label-message {
    background-color: #36393E;
}

.label-warning {
    background-color: rgba(250, 150, 10, 0.6);
}

.lien-blanc {
    font-family: Segoe Ui;
    font-weight: 100;
}

.lien-blanc,
.nombre-messages-lu {
    color: #BDBDBC;
    transition: color 500ms;
}

.lien-blanc:hover,
.nombre-messages-lu:hover {
    color: #7F7F81;
}

.lien-blanc-gras {
    font-weight: 100;
}

.line-striped {
    background-color: transparent;
}

.menu-secondaire {
    background-color: #282828;
    padding-left: 5px;
    font-family: Segoe Ui;
    font-weight: 100;
}

.message-resultat-recherche {
    border-radius: 0px;
    background-color: #36393E;
}

.modal {
    background-color: #2F3136;
    border: 1px solid #202225;
    border-radius: 0px;
}

.modal-header,
.modal-body {
    border-bottom: 1px solid #25272B;
}

.modal-header > h3 {
    font-family: Segoe Ui;
    font-weight: 100;
}

.modal-footer {
    background-color: #2A2C31;
    border-top: 0px;
    box-shadow: none;
}

.navbar {
    font-family: Segoe Ui;
}

.navbar .brand {
    font-family: Segoe Ui;
}

.navbar-inverse .navbar-inner {
    background-image: none;
    box-shadow: none;
    border-bottom: 0px;
}

.navigation-resultat-recherche {
    background-color: #2C323C;
}

div.navigation-resultat-recherche {
    margin-bottom: 20px;
}

.nav-tabs {
    border-bottom-color: #424243;
}

.nav-tabs > li > a {
    margin-right: 0px;
    border-right-width: 0px;
    border-left-width: 0px;
    color: #BDBDBC;
    border-radius: 0px;
}

.nav-tabs > li > a:hover {
   border-bottom: 1px solid #424243;
    background-color: rgba(240, 240, 240, 0.8);
}

.nav-tabs > .active > a,
.nav-tabs > .active > a:hover,
.nav-tabs > .active > a:focus {
    border-radius: 0px;
    background-color: #202225;
    border: 1px solid #424243;
   border-bottom: 1px solid #424243;
	color: #c9c6c1;
}

.nombre-messages {
    font-weight: 100;
    font-family: Segoe Ui;
}

.nombre-messages-nouveau {
    color: #18A661;
}

.nombre-messages-nouveau:hover {
    filter: brightness(80%);
}

.nombre-messages-reponses {
    color: #C5B022;
}

.nombre-messages-reponses:hover {
    filter: brightness(80%);
}

.pull-right {
    margin-top: 1px;
}

.rang-prestige {
    color: #e26a6a;
    font-family: Segoe Ui;
    font-weight: 100;
}

.row-signalement {
    border-radius: 0px;
    background-color: #282828;
}

.row-signalement > div > div:nth-child(1) > div {
    background-color: #282828;
    padding-right: 20px;
    padding-bottom: 10px;
}

span[style='color:#C2C2DA;'] {
    color: #C7C5BD !important;
}

.span12 > .cadre-sujet {
	border: 0px;
    border-bottom: 1px solid rgba(200, 195, 190, 0.3);
    background-color: #2F3136;
}

.span12 > .cadre-sujet .cadre-sujet-titre:hover {
    filter: brightness(100%);
}

.span12 > .cadre-sujet-postit {
    border-left-color: #7D7D7D;
    background-color: #404040;
}

table.dataTable tbody tr,
.table-striped tbody tr:nth-child(odd) td,
.table-striped tbody tr:nth-child(odd) th {
    background-color: #282828;
}

.cadre-staff-prive table.dataTable tbody tr,
.cadre-staff-prive .table-striped tbody tr:nth-child(odd) td,
.cadre-staff-prive .table-striped tbody tr:nth-child(odd) th {
    background-color: #1A1A1A;
}

.table-datatable > tbody > tr td{
    border-bottom: 1px solid rgba(200, 195, 190, 0.3) !important;
}

.table-cadre .cadre-message-message {
    color: hsla(0,0%,100%,.7);
    font-family: Segoe Ui;
    font-size: 13px;
    font-weight: 100;
}

.table-section {
    background-color: transparent;
}

.table-section {
    overflow: hidden;
}

.table-section:after {
    position: relative;
    content: ' ';
    border-bottom: 1px solid rgba(200, 195, 190, 0.3);
    display: block;
    min-width: 400%;
}

.table-section-cellule {
    font: Segoe Ui;
    font-weight: 100;
}

.table-sanctions {
    border-bottom: 0px;
}

.table-sanctions  > tbody > tr > td {
    border-top: 0px;
}

textarea {
    border-radius: 0px;
}

textarea:focus, input[type="text"]:focus, input[type="password"]:focus, input[type="datetime"]:focus, input[type="datetime-local"]:focus, input[type="date"]:focus, input[type="month"]:focus, input[type="time"]:focus, input[type="week"]:focus,
input[type="number"]:focus, input[type="email"]:focus, input[type="url"]:focus, input[type="search"]:focus, input[type="tel"]:focus, input[type="color"]:focus, .uneditable-input:focus {
    border-color: transparent !important;
    box-shadow: none !important;
}

.texte-jeu {
    font-size: 13px;
    font-family: Segoe Ui;
}

.titre-spoiler {
    color: #828997;
}

}
