css:
@-moz-document url-prefix(https://new.optima-osvita.org) {

@keyframes pulse {
	0% {
		background-color: dodgerblue;
	}
    
	50% {
		background-color: #4aa6ff;
	}
    
	100% {
		background-color: dodgerblue;
	}
}

@keyframes pulse2 {
	0% {
		border-top: 2px solid dodgerblue;
		border-bottom: 3px solid dodgerblue;
	}
    
	50% {
		border-color: #4aa6ff;
		background-color: #4aa6ff;
	}
    
	100% {
		border-top: 2px solid dodgerblue;
		border-bottom: 3px solid dodgerblue;
	}
}

body {
	background-color: deepskyblue;
	background: linear-gradient(to bottom, deepskyblue, #fff);
}

.navbar,
.navbar-inner,
#essentialmenus {
	background-color: dodgerblue;
	transition: 0.2s;
	animation: pulse 5s linear infinite;
}

#page-footer {
	background-color: dodgerblue;
	transition: 0.2s;
	animation: pulse2 5s linear infinite;
	border-top: 2px solid dodgerblue;
	border-bottom: 3px solid dodgerblue;
}

body button,
body .btn-default,
body a.btn-default,
body input.form-submit,
body button.btn-cancel,
body input[type="reset"],
body input[type="button"],
body input[type="submit"],
body .submit.buttons input[name=cancel],
body #notice .singlebutton+.singlebutton input {
	color: #fff;
	text-shadow: none;
	background-color: dodgerblue;
	background-image: -moz-linear-gradient(bottom, dodgerblue, rgba(43, 156, 188, .25));
	background-image: -webkit-gradient(linear, 0 100%, 0 0, from(#30add1), to(rgba(43, 156, 188, .25)));
	background-image: -webkit-linear-gradient(bottom, dodgerblue, rgba(43, 156, 188, .25));
	background-image: -o-linear-gradient(bottom, dodgerblue, rgba(43, 156, 188, .25));
	background-image: linear-gradient(to top, dodgerblue, rgba(43, 156, 188, .25));
	transition: 0.2s;
}

#page form .form-buttons #fgroup_id_buttonar,
#page form #fgroup_id_buttonar #fgroup_id_buttonar,
#page form table#form td.submit #fgroup_id_buttonar,
#page form .path-admin .buttons #fgroup_id_buttonar,
#page form #fitem_id_submitbutton #fgroup_id_buttonar,
#page form div.backup-section+form #fgroup_id_buttonar,
#page form .fp-content-center form+div #fgroup_id_buttonar,
#page form fieldset:last-of-type.hidden #fgroup_id_buttonar {
	background-color: dodgerblue;
}

#page form #fitem_id_submitbutton {
	background-color: dodgerblue;
}

.breadcrumb-button input[type="submit"],
.breadcrumb-nav ul.breadcrumb.style1 li {
	background-color: dodgerblue;
	border-color: dodgerblue;
}

.breadcrumb-nav ul.breadcrumb.style1 li:after {
	border-left-color: dodgerblue;
}

::-webkit-scrollbar,
::-webkit-scrollbar-track,
::-webkit-scrollbar-track-piece {
	background: linear-gradient(to bottom, #4cd2ff, deepskyblue);
}

::-webkit-scrollbar {
	width: 10px;
}

::-webkit-scrollbar-corner,
::-webkit-scrollbar-thumb {
	background: dodgerblue;
	border-radius: 0.5em;
}

object {
	width: 800px;
    height: 800px;
}

}
