@-moz-document domain("roblox.com") {
    /* i really want this to be global */
    
    .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard {
        display: none !important;
    }
    .ad,
    .ad *,
    .img_ad,
    .ad-annotations,
    #google_image_div,
    #aw0,
    #AdvertisingLeaderboard {
        display: none !important;
    }
    /*Ad removal */
    /* Start http://www.cursors-4u.com */
    
    body,
    a:hover {
        cursor: url(data:image/xicon;base64,), progress !important;
    }
    /* End http://www.cursors-4u.com */
    
    #rbx-body {
    background-color: #E3E3E3;
    color: #0c3d5d;
    font-family: "Source Sans Pro",Arial,Helvetica,sans-serif;
    font-weight: 300;
    font-size: 16px;
    width: 100%;
    min-width: 320px;
    margin-bottom: 168px;
    overflow-y: scroll;
}
#Body {
    min-height: 550px;
    padding: 5px 0 25px 0;
    margin: 0 auto;
    max-width: 970px;
    background-color: #f5f5f5b5;
}
.tab-content, .tab-container+div>div {
    top: -1px;
    position: relative;
    background-color: #fff0;
    border-top: 1px solid #CCC;
    display: none;
}
.redesign .tab_white_31h_container .ajax__tab_active .ajax__tab_inner, .tab.active, .tab-container>.tab-active, .tab-container>.tab-active:hover {
    background-color: #f4e9f9;
    margin-top: 0;
    padding: 9px 7px;
    border-bottom: 0;
    position: relative;
    border-color: #00a2ff;
    z-index: 1;
    margin: 0 1px 0 0;
    top: 1px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
}
.text-name, .text-name:link, .text-name:hover, .text-name:visited, .text-name:active {
    color: #5192b7;
}
.text-link, .vlist .list-item .list-body .list-content a, .text-link:link, .vlist .list-item .list-body .list-content a:link, .text-link:hover, .vlist .list-item .list-body .list-content a:hover, .text-link:visited, .vlist .list-item .list-body .list-content a:visited, .text-link:active, .vlist .list-item .list-body .list-content a:active {
    color: #491cab;
    font-size: x-large;
}
.section-content {
    background-color: #b2a5ce;
    box-shadow: 0 3px 4px 4px rgba(87, 129, 206, 0.56);
    padding: 15px;
    position: relative;
}
#build-page .gear-button-wrapper {
    top: 5px;
    width: 38px;
    background-color: #c7c7c700;
    border-left: 1px solid;
    border-right: 1px solid;
    border-color: #ffffff00;
    height: 37px;
    position: relative;
    margin-right: 5px;
}
#build-page td.menu-area a.tab-item-selected {
    border-top: 1px solid #00a2ff;
    border-bottom: 1px solid #00a2ff;
    border-left: 1px solid #00a2ff;
    background-color: #f4e9f9;
    padding: 4px 9px;
    font-weight: bold;
}
.container-footer {
    background-color: #fff0;
    padding: 12px 0;
}
.text-footer-nav, .text-footer-nav:link, .text-footer-nav:visited, .text-footer-nav:active {
    color: #7870bd;
    font-size: 21px;
    font-weight: 300;
}
.text-footer {
    font-size: 12px;
    font-weight: 400;
    color: #7870bd;
}
.table td, .table th {
    padding: 5px;
    border-top: 1px solid #b89cf5;
    margin: 0;
    text-align: left;
}
.footer-link .text-footer-nav.privacy {
    color: #033f62;
}
#nav-home:hover, #nav-inventory:hover {
    color: #ffffff;
}
.rbx-left-col li a {
    color: #0f1e58;
    display: block;
}
#nav-profile:hover, #nav-character:hover {
    color: #ffffff;
}
#nav-message:hover, #nav-friends:hover, #nav-trade:hover, #nav-group:hover, #nav-forum:hover, #nav-blog:hover, #nav-shop:hover {
    color: #ffffff;
}
#nav-message:hover, #nav-friends:hover, #nav-trade:hover, #nav-group:hover, #nav-forum:hover, #nav-blog:hover, #nav-shop:hover {
    color: #ffffff;
}
.form-control {
    display: block;
    width: 100%;
    background-color: #efe9fc;
    background-image: none;
    border: 1px solid #B8B8B8;
    border-radius: 3px;
}
.input-group-btn .input-dropdown-btn:hover, .input-group-btn .input-dropdown-btn:focus {
    background-color: #af95e6;
    box-shadow: 0 1px 3px rgba(150,150,150,0.74);
    border-color: #00A2FF;
    color: #fff;
    cursor: pointer;
}
#catalog-content {
    background-color: rgba(227, 227, 227, 0.75);
}
    .notification-blue {
    background-color: #9077c6;
    border-radius: 12px;
    font-size: 12px;
    font-weight: 400;
    color: #fff;
    text-align: center;
    padding: 4px 6px;
    min-width: 24px;
    }
    #header {
        background-color: #5BA6D5;
    }
    .navigation .notification-icon,
    .rbx-highlight {
        background: #F7F7F7;
    }
    #chat-header {
        background: #5BA6D5;
        /*Party header*/
        
        ;
    }
.item-card-container {
    background-color: #f1dbff;
    position: relative;
    box-shadow: 0 1px 4px 0px rgba(19, 49, 103, 0.3);
    border-radius: 3px;
    padding: 0 0 5px;
    width: 126px;
    max-width: 150px;
}    
.slide-item-container-left,
    .profile-avatar-right {
        background: #5BA6D5;
    }
    .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
    .rbx-tabs-horizontal .rbx-tab-heading:hover,
    .rbx-tabs-horizontal .rbx-tab-heading:focus,
    .rbx-tabs-horizontal .rbx-tab-heading:active {
        box-shadow: 0 -4px 0 .6 #145A32 inset;
        /* Yea idk what this is for*/
        
        ;
    }
    .forum-table-header,
    body .forum-table-header th,
    body .forum-table-footer,
    #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
    #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: #5993B7 !important;
        /*Forum colors and messaging underline color*/
        
        ;
    }
    .rbx-upgrade-now #upgrade-now-button,
    .rbx-btn-secondary-xs.btn-more,
    #AjaxCommentsContainer > div > div.comment-form > form > button,
    .rbx-vip-server-create,
    .btn-secondary-xs {
        background-color: #9077C6 !important;
        border-color: #9077C6;
        /*Updrade button*/
        
        ;
    }
    .rbx-upgrade-now #upgrade-now-button:hover,
    .rbx-btn-secondary-xs.btn-more:hover,
    #AjaxCommentsContainer > div > div.comment-form > form > button:hover,
    .rbx-vip-server-create:hover,
    .btn-secondary-xs:hover {
        background: #8062C1 !important;
        border-color: #8062C1;
    }
    #TopAbpContainer,
    .ad-annotations,
    #LeftGutterAdContainer,
    #RightGutterAdContainer,
    #Skyscraper-Adp-Right,
    #Skyscraper-Adp-Left,
    .abp-spacer,
    #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
    #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
    #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even,
    #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd,
    .sponsored-game,
    .alert-info,
    #mCSB_1_container > ul > li.font-bold.small,
    #mCSB_1_container > ul > li.rbx-nav-sponsor {
        display: none !important;
    }
    #navbar-search-input,
    #navbar-search-btn {
        border: 1px solid #E4DCED;
        /*Search border color*/
        
        ;
    }
    body {
        background: transparent !important;
    }
    .content {
    background-color: #ffffff00 !important;
    }
    #fb-root {
        width: 100% !important;
        height: 100% !important;
        position: fixed !important;
        background-image: url("https://img00.deviantart.net/432a/i/2016/239/c/b/steven_universe_midnight_sky_by_luifex-dafh0in.png") !important;
        /* Background */
        
        background-size: cover !important;
        background-attachment: fixed !important;
    }
    #party_none_title,
    div.friend_dock_chatbox_titlebar.chat-header-blink-off {
        background: linear-gradient(to bottom, #145A32, #145A32);
    }
    #ctl00_cphRoblox_RightColumn,
    #Body > table > tbody > tr > td:nth-child(3),
    #Body > table > tbody > tr > td:nth-child(4) {
        display: none;
    }
    #GamesPageLeftColumn {
        width: auto;
        margin-right: 10px !important;
        margin-left: 10px !important;
    }
    #GamesListsContainer > div {
        background-color: transparent !important;
        width: 100% !important;
    }
    #navigation {
        background-color: rgba( 91, 166, 213, 0.8)
        /*Left side navi*/
        
        ;
    }
    .rbx-btn-primary-lg {
        background-color: rgba(253, 254, 254, 0.5);
    }
    .rbx-btn-primary-lg:hover,
    .rbx-btn-primary-lg:focus {
        background-color: rgba(253, 254, 254, 0.5);
    }
    /* above effect the background of the catalog and the forums*/
}
